// Calculate suitability score (0-100 points)
function calculateSuitabilityScore(alternative, current) {
  let score = 0;

  // Rating score (0-40 points)
  const ratingScores = { 
    'A+': 40, 
    'A': 35, 
    'A-': 30, 
    'B+': 25, 
    'B': 20, 
    'B-': 15,
    'C+': 10,
    'C': 5
  };
  score += ratingScores[alternative.rating] || 5;

  // Capacity score (0-25 points)
  const capacityRatio = alternative.capacity / current.contract_value;
  if (capacityRatio >= 3) score += 25;
  else if (capacityRatio >= 2) score += 22;
  else if (capacityRatio >= 1.5) score += 18;
  else if (capacityRatio >= 1.2) score += 15;
  else if (capacityRatio >= 1) score += 10;
  else score += 5;

  // Risk score (0-25 points) - lower risk is better
  const riskScore = Math.max(25 - alternative.risk_score, 0);
  score += riskScore;

  // Lead time score (0-10 points)
  const leadTime = alternative.lead_time.toLowerCase();
  if (leadTime.includes('day') || leadTime.includes('1 week')) score += 10;
  else if (leadTime.includes('1') || leadTime.includes('2')) score += 8;
  else if (leadTime.includes('3') || leadTime.includes('4')) score += 6;
  else if (leadTime.includes('5') || leadTime.includes('6')) score += 4;
  else score += 2;

  return Math.round(score);
}

