    factors.push('Large contract transition');
    complexityScore += 2;
  } else if (riskData.financial_health.contract_value > 500000) {
    factors.push('Medium contract transition');
    complexityScore += 1;
  }

  // Criticality assessment
  if (riskData.financial_health.criticality === 'critical') {
    factors.push('Critical supplier replacement - high stakes');
    complexityScore += 3;
  } else if (riskData.financial_health.criticality === 'high') {
    factors.push('High priority supplier replacement');
    complexityScore += 2;
  }

  // Geographic coverage assessment
  if (alternative.geographic_coverage.includes('Global')) {
    factors.push('Global coverage available');
    complexityScore -= 1;
  } else if (alternative.geographic_coverage.length === 1) {
    factors.push('Limited geographic coverage');
    complexityScore += 1;
  }

  // Determine complexity level
  if (complexityScore <= 0) complexity = 'low';
  else if (complexityScore <= 3) complexity = 'medium';
  else complexity = 'high';

  return { 
    level: complexity, 
    factors: factors,
    score: complexityScore
  };
}

