  supplier_id: riskData.supplier_id,
  supplier_name: riskData.supplier_name,
  current_risk_score: riskData.risk_scoring.adjusted_score,
  category: currentSupplier.category,
  analysis_timestamp: new Date().toISOString(),

  // Alternative supplier metrics
  alternatives_found: suitableAlternatives.length,
  total_alternatives_available: availableAlternatives.length,
  recommended_alternatives: suitableAlternatives,

  // Transition strategy
  transition_strategy: {
    urgency_level: riskData.risk_scoring.adjusted_score >= 75 ? 'immediate' : 
                   riskData.risk_scoring.adjusted_score >= 55 ? 'high' : 'standard',
    timeline: calculateTransitionTimeline(riskData.risk_scoring.adjusted_score, suitableAlternatives),
    recommendations: transitionRecommendations,
    budget_impact_estimate: estimateBudgetImpact(riskData.financial_health.contract_value, suitableAlternatives),
    complexity_assessment: suitableAlternatives.length > 0 ? suitableAlternatives[0].transition_complexity.level : 'unknown'
  },

  // Action plan
  immediate_next_steps: generateNextSteps(riskData.risk_scoring.adjusted_score, suitableAlternatives),

  // Risk mitigation analysis
  risk_mitigation: {
    current_single_point_failure: true,
    proposed_diversification: suitableAlternatives.length > 1,
    recommended_supplier_split: generateSupplierSplit(suitableAlternatives.length, riskData.risk_scoring.adjusted_score),
    contingency_readiness: assessContingencyReadiness(suitableAlternatives),
    fallback_options: suitableAlternatives.length
  },

  // Quality assessment
  alternative_quality_metrics: {
    average_rating: calculateAverageRating(suitableAlternatives),
    average_risk_score: calculateAverageRiskScore(suitableAlternatives),
    certification_coverage: assessCertificationCoverage(suitableAlternatives),
    geographic_coverage: assessGeographicCoverage(suitableAlternatives)
  }
};

// Helper functions
function estimateBudgetImpact(contractValue, alternatives) {
  if (alternatives.length === 0) return 'No alternatives available - high cost risk';

  const avgLeadTime = alternatives.reduce((sum, alt) => {
    const leadTimeWeeks = parseInt(alt.lead_time.match(/\d+/)?.[0] || '2');
    return sum + leadTimeWeeks;
  }, 0) / alternatives.length;

  const transitionCost = contractValue * 0.05; // Assume 5% transition cost
  const urgencyCost = contractValue * (riskData.risk_scoring.adjusted_score >= 75 ? 0.15 : 0.08);

  return {
    estimated_transition_cost: Math.round(transitionCost),
    urgency_premium: Math.round(urgencyCost),
    total_estimated_impact: Math.round(transitionCost + urgencyCost),
    cost_as_percentage: ((transitionCost + urgencyCost) / contractValue * 100).toFixed(1) + '%'
  };
}

function generateNextSteps(riskScore, alternatives) {
  const steps = [];

  if (riskScore >= 75) {
    steps.push('üö® Immediate: Activate crisis management team');
    steps.push('üìû Contact top 2 alternative suppliers within 4 hours');
    steps.push('‚ö° Fast-track supplier evaluation process');
    steps.push('üìã Prepare emergency contract templates');
  } else if (riskScore >= 55) {
    steps.push('üìã Schedule supplier review meeting within 24 hours');
    steps.push('üìä Request detailed proposals from top alternatives');
    steps.push('üîç Conduct accelerated due diligence');
    steps.push('üíº Begin contract term negotiations');
  } else {
    steps.push('üìù Update supplier contingency plans');
    steps.push('ü§ù Maintain regular contact with alternatives');
    steps.push('üìà Monitor current supplier performance');
    steps.push('üìÖ Schedule quarterly alternative reviews');
  }

  return steps;
}

function generateSupplierSplit(alternativeCount, riskScore) {
  if (alternativeCount === 0) return '100% current supplier (no alternatives available)';
  if (alternativeCount === 1) return riskScore >= 75 ? '100% alternative' : '80% current / 20% alternative';
  if (alternativeCount >= 2) {
    if (riskScore >= 75) return '60% primary alternative / 40% secondary alternative';
    if (riskScore >= 55) return '50% current / 30% primary alternative / 20% secondary alternative';
    return '70% current / 20% primary alternative / 10% secondary alternative';
  }
}

function assessContingencyReadiness(alternatives) {
  if (alternatives.length === 0) return 'low';
  if (alternatives.length >= 2 && alternatives[0].suitability_score >= 70) return 'high';
  if (alternatives.length >= 1 && alternatives[0].suitability_score >= 60) return 'medium';
  return 'low';
}

function calculateAverageRating(alternatives) {
  if (alternatives.length === 0) return 'N/A';
  const ratingValues = { 'A+': 4.0, 'A': 3.5, 'A-': 3.0, 'B+': 2.5, 'B': 2.0, 'B-': 1.5, 'C+': 1.0, 'C': 0.5 };
  const avgValue = alternatives.reduce((sum, alt) => sum + (ratingValues[alt.rating] || 0), 0) / alternatives.length;
  const ratingKeys = Object.keys(ratingValues);
  return ratingKeys.find(key => Math.abs(ratingValues[key] - avgValue) < 0.25) || 'Mixed';
}

function calculateAverageRiskScore(alternatives) {
  if (alternatives.length === 0) return 0;
  return Math.round(alternatives.reduce((sum, alt) => sum + alt.risk_score, 0) / alternatives.length);
}

function assessCertificationCoverage(alternatives) {
  if (alternatives.length === 0) return [];
  const allCertifications = new Set();
  alternatives.forEach(alt => alt.certifications.forEach(cert => allCertifications.add(cert)));
  return Array.from(allCertifications);
}

function assessGeographicCoverage(alternatives) {
  if (alternatives.length === 0) return [];
  const allRegions = new Set();
  alternatives.forEach(alt => alt.geographic_coverage.forEach(region => allRegions.add(region)));
  return Array.from(allRegions);
}

console.log(`Alternative Supplier Analysis Complete: Found ${suitableAlternatives.length} suitable alternatives for ${currentSupplier.category} category`);
console.log(`Risk Level: ${riskData.risk_scoring.adjusted_level} (${riskData.risk_scoring.adjusted_score}), Urgency: ${alternativeAnalysis.transition_strategy.urgency_level}`);

return [{ json: { ...riskData, alternative_analysis: alternativeAnalysis } }];

