parameters.jsCode:
const inputs = $input.all();

// Assumes: 
// inputs[0] = transcript
// inputs[1] = drive metadata

const transcriptData = inputs[0].json;
const driveData = inputs[1].json;
const tele = $('Telegram Voice Message Trigger').first().json.message;

const result = {
  Transcript: transcriptData.text || '',
  AudioURL: driveData.webContentLink || '',
  ChatID: tele.chat.id,
  FirstName: tele.from.first_name
};

return [{ json: result }];
