parameters.jsCode:
for (const item of $input.all()) {
  const subject = item.json.subject;
  const encodedSubject = encodeURIComponent(subject).replace(/%20/g, '+');
  const searchLink = `https://mail.google.com/mail/u/0/#search/${encodedSubject}`;

  item.json.searchLink = searchLink;
}

return $input.all();
