// Get the input data (your JSON)
const data = items[0].json;

// Build the email text
let emailText = `ðŸ“Š Networking Report for ${data.user_profile.name}\n`;
emailText += `\nInterests: ${data.user_profile.interests}`;
emailText += `\nTarget Industry: ${data.user_profile.target_industry}\n`;

emailText += `\nSummary:\n`;
emailText += `- Total Profiles Found: ${data.summary.total_profiles_found}\n`;
emailText += `- Analyzed Connections: ${data.summary.analyzed_connections}\n`;
emailText += `- High Priority: ${data.summary.high_priority}\n`;
emailText += `- Medium Priority: ${data.summary.medium_priority}\n`;
emailText += `- Average Score: ${data.summary.average_score}\n`;

emailText += `\nTop Connection Recommendations:\n`;
data.connection_recommendations.forEach((rec, index) => {
    emailText += `\n${index + 1}. ${rec.name} (${rec.priority} Priority, Score: ${rec.aiScore})`;
    emailText += `\n   ${rec.description}`;
    emailText += `\n   Mutual Connections: ${rec.mutual_connections}`;
    emailText += `\n   Reason: ${rec.connectionReason}`;
    emailText += `\n   Suggested Message: "${rec.suggestedMessage}"\n`;
});

emailText += `\nNext Steps:\n`;
data.next_steps.forEach((step, i) => {
    emailText += `- ${step}\n`;
});

emailText += `\nAI Insights:\n${data.ai_insights}\n`;

emailText += `\nBest Networking Times:\nDays: ${data.networking_strategy.best_days.join(", ")}\nTimes: ${data.networking_strategy.best_times.join(", ")}\n`;

return [{
    json: {
        subject: `Networking Report - ${data.user_profile.name}`,
        body: emailText
    }
}];

// Process search results and extract LinkedIn profiles
const results = $json.organic_results || [];

// Get user profile from the previous node
const userProfile = $input.first().json || {};

// Filter and process LinkedIn profiles
const linkedinProfiles = results
  .filter(result => {
    const link = result.link || '';
    return link.includes('linkedin.com/in/') && 
           !link.includes('/posts/') && 
           !link.includes('/activity/');
  })
  .map(result => {
    let name = result.title || 'Unknown';
    // Clean up the title to extract just the name
    name = name.replace(/\s*[-|]\s*LinkedIn.*$/i, '').trim();

    return {
      name: name,
      headline: result.snippet || result.title || '',
      link: result.link,
      description: result.snippet || '',
      source: 'Google Search via SerpAPI'
    };
  })
  .slice(0, 15); // Limit to top 15 results

// Safely extract skills - handle undefined/null values
const getFirstSkill = (skills) => {
  if (!skills || typeof skills !== 'string') {
    return '';
  }
  const skillsArray = skills.split(',');
  return skillsArray.length > 0 ? skillsArray[0].trim() : '';
};

// Build search query with safe property access
const buildSearchQuery = () => {
  const position = userProfile.Position || '';
  const location = userProfile.Location || '';
  const firstSkill = getFirstSkill(userProfile.Skills);

  return `site:linkedin.com/in "${position}" "${location}" ${firstSkill}`.trim();
};

// Return structured data for AI analysis
return {
  json: {
    userProfile: {
      name: $('Your Profile Information').first().json.Name || '',
      current_position: $('Your Profile Information').first().json.Position || '',
      industry: $('Your Profile Information').first().json.Industry || '',
      location:  $('Your Profile Information').first().json.Location|| '',
      skills: $('Your Profile Information').first().json.Skills|| '',
      interests: $('Your Profile Information').first().json.Interests || '',
      target_roles: $('Your Profile Information').first().json['Target Roles']|| '',
      company_types: $('Your Profile Information').first().json['Company Types'] || ''
    },
    foundProfiles: linkedinProfiles,
    totalFound: linkedinProfiles.length,
    searchQuery: buildSearchQuery()
  }
};

// Process AI recommendations and create final output
const inputData = $input.first();
const inputText = inputData.json?.text || inputData.json || inputData;

let aiRecommendations = {};
try {
  // Extract JSON from the text content
  const content = typeof inputText === 'string' ? inputText : JSON.stringify(inputText);

  // Try to parse JSON from the AI response
  const jsonMatch = content.match(/```json\n([\s\S]*?)\n```/) || content.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    const jsonString = jsonMatch[1] || jsonMatch[0];
    aiRecommendations = JSON.parse(jsonString);
  } else {
    throw new Error('No JSON found in response');
  }
} catch (error) {
  console.log('Could not parse AI response:', error.message);
  console.log('Input data:', inputData);

  // Create fallback recommendations if parsing fails
  aiRecommendations = {
    scored_profiles: [],
    top_connections: [],
    connection_strategies: [],
    networking_insights: 'Focus on building meaningful professional relationships in your industry.'
  };
}

// Helper function to safely split and get first element
function safeGetFirst(str, delimiter = ',') {
  if (!str || typeof str !== 'string') return '';
  const parts = str.split(delimiter);
  return parts.length > 0 ? parts[0].trim() : '';
}

// Helper function to safely split string
function safeSplit(str, delimiter = ',') {
  if (!str || typeof str !== 'string') return [];
  return str.split(delimiter).map(item => item.trim()).filter(item => item);
}

// Helper function to generate connection message
function generateConnectionMessage(profile, index) {
  if (!profile || !profile.name) return 'Hi! I\'d love to connect and share professional insights.';

  const firstName = safeGetFirst(profile.name, ' ');
  const description = profile.description || '';

  // Create personalized messages based on profile
  if (description.toLowerCase().includes('devops')) {
    return `Hi ${firstName}, I'm interested in DevOps and cloud technologies. I'd love to connect and learn from your experience!`;
  } else if (description.toLowerCase().includes('aws') || description.toLowerCase().includes('cloud')) {
    return `Hi ${firstName}, I noticed your expertise in cloud computing. Would love to connect and share insights about the industry!`;
  } else {
    return `Hi ${firstName}, I'd love to connect with a fellow professional and learn from your experience in the industry.`;
  }
}

// Helper function to extract tags from description
function extractTags(description) {
  if (!description) return ['Professional Contact'];

  const tags = [];
  const descText = description.toLowerCase();

  // Add technology tags
  const techs = ['aws', 'azure', 'gcp', 'devops', 'terraform', 'ansible', 'jenkins', 'kubernetes', 'docker'];
  techs.forEach(tech => {
    if (descText.includes(tech)) {
      tags.push(tech.toUpperCase());
    }
  });

  // Add role tags
  if (descText.includes('senior') || descText.includes('lead')) {
    tags.push('Senior Level');
  }

  if (descText.includes('mentor') || descText.includes('enthusiast')) {
    tags.push('Mentor');
  }

  return tags.length > 0 ? tags : ['Professional Contact'];
}

// Process the connections from AI recommendations
const connections = aiRecommendations.top_connections || [];
const scoredProfiles = aiRecommendations.scored_profiles || [];

const enhancedConnections = connections.map((profile, index) => {
  // Find matching score data
  const scoreData = scoredProfiles.find(p => 
    p.name && profile.name && 
    p.name.toLowerCase().trim() === profile.name.toLowerCase().trim()
  );

  const score = scoreData?.score || (9.5 - index * 0.3); // Fallback scoring

  return {
    name: profile.name || 'Unknown',
    description: profile.description || '',
    connections: profile.connections || 0,
    mutual_connections: profile.mutual_connections || 0,
    priority: score >= 8.5 ? 'High' : score >= 7.5 ? 'Medium' : 'Low',
    aiScore: score,
    connectionReason: scoreData?.reasons?.join(', ') || 'Professional networking opportunity',
    suggestedMessage: generateConnectionMessage(profile, index),
    tags: extractTags(profile.description),
    estimatedResponseRate: score >= 8.5 ? 'High (70-90%)' : score >= 7.5 ? 'Medium (40-70%)' : 'Low (20-40%)',
    link: profile.link || '#'
  };
}).sort((a, b) => b.aiScore - a.aiScore); // Sort by AI score

// Create individual connection strategies
const connectionStrategies = enhancedConnections.map(conn => {
  const firstName = safeGetFirst(conn.name, ' ');
  return `${firstName}: ${conn.suggestedMessage.replace(`Hi ${firstName}, `, '')}`;
});

return {
  json: {
    summary: {
      total_profiles_found: scoredProfiles.length,
      analyzed_connections: enhancedConnections.length,
      high_priority: enhancedConnections.filter(c => c.priority === 'High').length,
      medium_priority: enhancedConnections.filter(c => c.priority === 'Medium').length,
      low_priority: enhancedConnections.filter(c => c.priority === 'Low').length,
      average_score: enhancedConnections.length > 0 ? 
        (enhancedConnections.reduce((sum, c) => sum + c.aiScore, 0) / enhancedConnections.length).toFixed(1) : 0
    },
    user_profile: {
      name: "Vrushti Sukhadiya", // Extracted from the analysis text
      interests: "DevOps, Cloud Computing, Automation",
      target_industry: "IT, Tech Startups, Cloud Companies"
    },
    connection_recommendations: enhancedConnections,
    connection_strategies: connectionStrategies,
    ai_insights: aiRecommendations.networking_insights || 'Focus on building meaningful professional relationships in your industry.',
    networking_strategy: {
      weekly_goal: '5-10 new connections',
      best_days: ['Tuesday', 'Wednesday', 'Thursday'],
      best_times: ['9-11 AM', '2-4 PM'],
      follow_up_schedule: 'Within 48 hours of connection acceptance',
      focus_areas: ['DevOps professionals', 'Cloud Computing experts', 'AI/ML enthusiasts']
    },
    next_steps: [
      'Start with highest-scored profiles (Mihir Suthar - 9.5, Muneeswaran M - 9.2)',
      'Check for mutual connections before reaching out',
      'Engage with their recent posts about DevOps/Cloud topics',
      'Send personalized connection requests highlighting common interests',
      'Follow up with thoughtful messages about industry trends',
      'Consider attending DevOps/Cloud computing events where they might be present'
    ],
    top_recommendations: enhancedConnections.slice(0, 3).map(conn => ({
      name: conn.name,
      why_connect: conn.connectionReason,
      action_item: `Connect with ${safeGetFirst(conn.name, ' ')} focusing on ${conn.tags.slice(0, 2).join(' and ')} expertise`
    }))
  }
};
