// Extract content from HTML - handle all response types safely
const url = $('Loop URLs').item.json.loc;
let html = '';
let statusCode = 'Unknown';

// Handle different response formats
if (typeof $json === 'string') {
  html = $json;
  statusCode = 200; // Assume success if we got a string
} else if ($json && $json.body) {
  html = $json.body;
  statusCode = $json.statusCode || 200;
} else if ($json && typeof $json === 'object') {
  // Try to find HTML content in the object
  const possibleHtml = Object.values($json).find(val => 
    typeof val === 'string' && (val.includes('<html') || val.includes('<div') || val.length > 100)
  );
  html = possibleHtml || '';
  statusCode = $json.statusCode || 'Unknown';
}

console.log('Processing URL:', url);
console.log('Response type:', typeof $json);
console.log('HTML length:', html ? html.length : 0);
console.log('Status code:', statusCode);

function extractTitle(html) {
  if (!html) return 'No Title';

  // Try multiple methods
  const titlePatterns = [
    /<title[^>]*>([^<]+)<\/title>/i,
    /<meta[^>]*property=["']og:title["'][^>]*content=["']([^"']+)["']/i,
    /<h1[^>]*>([^<]+)<\/h1>/i
  ];

  for (const pattern of titlePatterns) {
    const match = html.match(pattern);
    if (match && match[1]) {
      return match[1].trim().replace(/\s+/g, ' ');
    }
  }

  return 'Untitled Page';
}

function extractContent(html) {
  if (!html) return 'No content available';

  // Remove scripts, styles, and comments
  let content = html
    .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
    .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
    .replace(/<!--[\s\S]*?-->/g, '')
    .replace(/<noscript[^>]*>[\s\S]*?<\/noscript>/gi, '');

  // Convert HTML to readable text
  content = content
    .replace(/<h[1-6][^>]*>([^<]+)<\/h[1-6]>/gi, '\n\n## $1\n\n')
    .replace(/<p[^>]*>([^<]+)<\/p>/gi, '\n\n$1\n\n')
    .replace(/<br\s*\/?>/gi, '\n')
    .replace(/<div[^>]*>([^<]*)<\/div>/gi, '\n$1\n')
    .replace(/<a[^>]*href=["']([^"']*)["'][^>]*>([^<]+)<\/a>/gi, '$2 [$1]')
    .replace(/<li[^>]*>([^<]+)<\/li>/gi, '\nâ€¢ $1')
    .replace(/<[^>]+>/g, ' ')
    .replace(/\s+/g, ' ')
    .replace(/\n\s+/g, '\n')
    .replace(/\s+\n/g, '\n')
    .replace(/\n{3,}/g, '\n\n')
    .trim();

  return content || 'No readable content found';
}

const title = extractTitle(html);
const content = extractContent(html);
const wordCount = content.split(/\s+/).filter(w => w.length > 0).length;

// Check for framework indicators
const isDivi = html.toLowerCase().includes('divi') || html.includes('et_pb_');
const isWordPress = html.toLowerCase().includes('wp-') || html.includes('wp_');
const hasJavaScript = html.includes('<script');

return {
  url: url,
  title: title,
  content: content,
  wordCount: wordCount,
  htmlLength: html.length,
  statusCode: statusCode,
  isDivi: isDivi,
  isWordPress: isWordPress,
  hasJavaScript: hasJavaScript,
  timestamp: new Date().toISOString(),
  success: html.length > 0
};

// Format content for Google Drive
const data = $json;

if (!data.success) {
  return {
    title: 'Scraping Failed',
    formatted: `# Scraping Failed\n\n**URL:** ${data.url}\n**Error:** No content retrieved\n**Status Code:** ${data.statusCode}\n**Timestamp:** ${data.timestamp}\n\nThe page may be JavaScript-dependent or blocking automated requests.`
  };
}

const sections = [];

// YAML frontmatter
sections.push('---');
sections.push(`title: "${data.title.replace(/"/g, '\\"')}"`); 
sections.push(`url: ${data.url}`);
sections.push(`scraped_at: ${data.timestamp}`);
sections.push(`word_count: ${data.wordCount}`);
sections.push(`html_length: ${data.htmlLength}`);
sections.push(`status_code: ${data.statusCode}`);
sections.push(`is_divi: ${data.isDivi}`);
sections.push(`is_wordpress: ${data.isWordPress}`);
sections.push(`has_javascript: ${data.hasJavaScript}`);
sections.push('scraper_type: "simple_direct_sitemap"');
sections.push('---');
sections.push('');

// Main content
sections.push(`# ${data.title}`);
sections.push('');
sections.push(`**Source URL:** ${data.url}`);
sections.push(`**Scraped:** ${new Date(data.timestamp).toLocaleString()}`);
sections.push(`**Word Count:** ${data.wordCount.toLocaleString()}`);
sections.push(`**HTML Size:** ${data.htmlLength.toLocaleString()} characters`);
sections.push(`**Status Code:** ${data.statusCode}`);
sections.push('');

if (data.isDivi) {
  sections.push('ðŸŽ¨ **Divi Theme Detected** - This WordPress site uses dynamic content loading');
  sections.push('');
}

if (data.hasJavaScript && data.wordCount < 100) {
  sections.push('âš ï¸ **Limited Content** - This page likely requires JavaScript rendering for full content');
  sections.push('');
}

sections.push('---');
sections.push('');
sections.push('## Content');
sections.push('');

if (data.content && data.content !== 'No readable content found') {
  sections.push(data.content);
} else {
  sections.push('*No readable content could be extracted from this page.*');
  sections.push('');
  sections.push('**Possible reasons:**');
  sections.push('- Page content is loaded dynamically with JavaScript');
  sections.push('- Site has anti-bot protection');
  sections.push('- Content is behind authentication');
  sections.push('- Page structure is complex or non-standard');
}

sections.push('');
sections.push('---');
sections.push('');
sections.push('**Technical Details:**');
sections.push(`- Framework detected: ${data.isDivi ? 'Divi' : (data.isWordPress ? 'WordPress' : 'Unknown')}`);
sections.push(`- JavaScript present: ${data.hasJavaScript ? 'Yes' : 'No'}`);
sections.push(`- Content extraction: ${data.wordCount > 50 ? 'Successful' : 'Limited'}`);
sections.push('');
sections.push('*Scraped with n8n Simple Working Scraper (Full Sitemap)*');

return {
  title: data.title,
  formatted: sections.join('\n')
};
