const attr = $input.first().json.data.attributes;

return [
  {
    json: {
      ip: attr.ip_address,
      hostname: attr.host_name,
      maliciousIP: attr.ip_address_last_analysis_stats.malicious,
      suspiciousIP: attr.ip_address_last_analysis_stats.suspicious,
      maliciousHostname: attr.host_name_last_analysis_stats.malicious,
      suspiciousHostname: attr.host_name_last_analysis_stats.suspicious,
      resolver: attr.resolver
    }
  }
];

