// HTML bruto da p√°gina
const html = $input.item.json.data;

// Limpa coment√°rios, scripts e estilos
let clean = html
  .replace(/<!--[\s\S]*?-->/g, '')                        // Remove coment√°rios
  .replace(/<script[\s\S]*?<\/script>/gi, '')             // Remove <script>
  .replace(/<style[\s\S]*?<\/style>/gi, '')               // Remove <style>
  .replace(/<\/?(head|meta|link|iframe|noscript)[^>]*>/gi, '') // Remove head/meta/etc
  .replace(/<[^>]+>/g, '')                                // Remove todas as tags HTML
  .replace(/\s{2,}/g, ' ')                                // Reduz m√∫ltiplos espa√ßos
  .replace(/\n{2,}/g, '\n')                               // Reduz m√∫ltiplas quebras
  .trim();

// Opcional: manter quebras de linha coerentes
clean = clean.replace(/\r\n|\r/g, '\n');

return [
  {
    json: {
      clean_text: clean,
      preview: clean.slice(0, 300) + '...',
      length: clean.length
    }
  }
];

const message = $json.body.message.text;
const parts = message.split(" ");
const url = parts[1];

return [
  {
    json: {
      url: url
    }
  }
];
