function formatDate(date) {
  return date.toISOString().split('T')[0];
}

const today = new Date();

// Prior week (15 to 9 days ago)
const priorStart = new Date(today);
priorStart.setDate(today.getDate() - 15);

const priorEnd = new Date(today);
priorEnd.setDate(today.getDate() - 9);

// Last week (8 to 2 days ago)
const lastStart = new Date(today);
lastStart.setDate(today.getDate() - 8);

const lastEnd = new Date(today);
lastEnd.setDate(today.getDate() - 2);

return [
  {
    json: {
      label: "2 Weeks Ago",
      startDate: formatDate(priorStart),
      endDate: formatDate(priorEnd)
    }
  },
  {
    json: {
      label: "Last Week",
      startDate: formatDate(lastStart),
      endDate: formatDate(lastEnd)
    }
  }
];

