const brands = $input.all();

let html = '';

function formatNumber(value) {
  return typeof value === 'number' ? value.toLocaleString() : value;
}

function formatPercentChange(current, previous, precision = 1, invert = false) {
  if (previous === 0 || previous === undefined || current === undefined) return '';
  const rawChange = ((current - previous) / previous) * 100;
  const change = invert ? -rawChange : rawChange;
  const rounded = change.toFixed(precision);
  const color = change > 0 ? 'green' : change < 0 ? 'red' : '#333';
  return `<span style="color: ${color}; font-weight: 500;">${change > 0 ? '+' : ''}${rounded}%</span>`;
}

function formatCTR(value) {
  return typeof value === 'number' ? `${(value * 100).toFixed(2)}%` : value;
}

for (const item of brands) {
  const brand = item.json.brand;
  const rows = item.json.data;

  if (!rows || rows.length < 2) continue;

  // Map rows by label
  const rowMap = {};
  rows.forEach(r => {
    if (r.label) rowMap[r.label] = r;
  });

  const current = rowMap["Last Week GSC"];
  const previous = rowMap["2 Weeks Ago GSC"];

  if (!current || !previous) continue;

  const baseFields = ["brandClicks", "nonBrandClicks"];
  const deltaFields = ["clicks", "impressions", "ctr", "position"];

  const headers = ["Label"];
  headers.push(...baseFields);
  deltaFields.forEach(f => {
    headers.push(`% Δ ${f}`);
    headers.push(f);
  });

  html += `
    <div style="margin-bottom: 40px; border-top: 3px solid #333; padding-top: 20px;">
      <table style="border-collapse: collapse; width: 100%; font-family: sans-serif; font-size: 14px; border: 1px solid #999;">
        <caption style="caption-side: top; font-weight: bold; font-size: 18px; margin-bottom: 12px; text-align: left;">${brand}</caption>
        <thead>
          <tr>
            ${headers.map(h => `<th style="border: 1px solid #999; padding: 8px 10px; background-color: #f2f2f2; text-align: left;">${h}</th>`).join('')}
          </tr>
        </thead>
        <tbody>
  `;

  ["2 Weeks Ago GSC", "Last Week GSC"].forEach(label => {
    const row = rowMap[label];
    html += `<tr><td style="border: 1px solid #999; padding: 8px 10px;">${label}</td>`;

    // Add base fields (no delta)
    baseFields.forEach(field => {
      html += `<td style="border: 1px solid #999; padding: 8px 10px;">${formatNumber(row[field]) ?? ''}</td>`;
    });

    // Add delta fields
    deltaFields.forEach(field => {
      let displayVal = row[field];
      if (field === "ctr") {
        displayVal = formatCTR(displayVal);
      } else {
        displayVal = formatNumber(displayVal);
      }

      if (label === "2 Weeks Ago GSC") {
        html += `<td style="border: 1px solid #999; padding: 8px 10px;"></td>`;
        html += `<td style="border: 1px solid #999; padding: 8px 10px;">${displayVal ?? ''}</td>`;
      } else {
        const delta = formatPercentChange(current[field], previous[field], 1, field === 'position');
        html += `<td style="border: 1px solid #999; padding: 8px 10px;">${delta}</td>`;
        html += `<td style="border: 1px solid #999; padding: 8px 10px;">${displayVal ?? ''}</td>`;
      }
    });

    html += `</tr>`;
  });

  html += `</tbody></table></div>`;
}

return [{ json: { htmlTable: html } }];
