/* =========================================================
   NOVA â€” Bitchy Luxe (Complete Theme â€¢ One-Paste)
   Uses your classes:
   - div.chat-message.chat-message-from-bot
   - div.chat-message.chat-message-from-user
   ========================================================= */

/* ---------- Root Theme Tokens ---------- */
:root{
  --chat--color-primary:#ff2e8b;
  --chat--color-primary-50:#db0f73;
  --chat--color-secondary:#00ffd1;
  --chat--color-secondary-50:#00d2b1;
  --chat--white:#fff;
  --chat--ink:#0a0b12;

  --gap:1rem;
  --radius:1.1rem;
  --blur:14px;

  --iridescent:linear-gradient(135deg,#ff2e8b 0%,#7f5cff 35%,#00ffd1 70%,#ffe85b 100%);
}

/* ---------- Global Chat Shell ---------- */
.chat-window{
  width:min(840px,96vw);
  margin:0 auto;
  background:
    radial-gradient(1200px 600px at 10% 0%, rgba(0,255,209,.08), transparent 60%),
    radial-gradient(1000px 700px at 100% 100%, rgba(127,92,255,.08), transparent 60%),
    #0b0d14;
  border-radius:1.25rem;
  box-shadow:0 24px 80px rgba(0,0,0,.55), inset 0 0 0 1px rgba(255,255,255,.06);
  overflow:hidden;
  position:relative;
  backdrop-filter:blur(var(--blur));
}
.chat-window::before{
  content:"";
  position:absolute; inset:-2px;
  border-radius:1.3rem;
  padding:2px;
  background:var(--iridescent);
  -webkit-mask:linear-gradient(#000 0 0) content-box, linear-gradient(#000 0 0);
  -webkit-mask-YOUR_OPENAI_KEY_HERE:xor; mask-YOUR_OPENAI_KEY_HERE:exclude;
  filter:blur(6px); opacity:.9; pointer-events:none;
}

/* ---------- Header ---------- */
.chat-header{
  padding:calc(var(--gap)*1.25) var(--gap);
  color:var(--chat--white);
  background:
    radial-gradient(1200px 600px at 20% -20%, rgba(255,46,139,.35), transparent 60%),
    radial-gradient(900px 500px at 120% 0%, rgba(127,92,255,.25), transparent 60%),
    linear-gradient(180deg, #121527 0%, #0a0c17 100%);
  border-bottom:1px solid rgba(255,255,255,.08);
  text-shadow:0 1px 18px rgba(255,46,139,.35);
}
.chat-header .chat-title{
  font:800 1.6rem/1.2 ui-sans-serif,system-ui,-apple-system,"Segoe UI",Inter,Roboto,Arial,sans-serif;
  letter-spacing:.3px;
}
.chat-header .chat-title::before{
  content:"â™›"; margin-right:.6rem;
  background:var(--iridescent);
  -webkit-background-clip:text; background-clip:text; color:transparent;
  filter:drop-shadow(0 0 6px rgba(255,46,139,.6));
}
.chat-header .chat-subtitle{
  margin-top:.4rem; opacity:.9; font-size:.95rem;
}

/* ---------- Messages Area ---------- */
.chat-body{ padding: var(--gap); }
.chat-messages-list{
  display:flex; flex-direction:column; gap:.9rem;
  background:linear-gradient(180deg, transparent, rgba(255,255,255,.02));
  padding-bottom: calc(var(--gap) * 1.5);
}

/* Kill upstream "white card" styles dead */
.chat-message{
  background:transparent !important;
  border:none !important;
  color:inherit;
  max-width: 72%;
  transform-origin:10% 90%;
  animation:nova-pop 220ms cubic-bezier(.2,.8,.2,1) both;
}

/* ---------- BOT: Nova (neon magenta + shimmer) ---------- */
div.chat-message.chat-message-from-bot{
  background: linear-gradient(135deg, #ff2e8b, #db0f73) !important;
  color:#fff !important;
  padding: 1rem 1.2rem !important;
  border-radius: var(--radius) var(--radius) var(--radius) .45rem !important;
  border:1px solid rgba(255,46,139,.4) !important;
  box-shadow:
    0 6px 22px rgba(255,46,139,.28),
    inset 0 1px 0 rgba(255,255,255,.32),
    inset 0 -2px 8px rgba(0,0,0,.08) !important;
  align-self:flex-start;
  position:relative;
  font-weight:500;
  background-clip: padding-box !important;
}
div.chat-message.chat-message-from-bot::after{
  content:"";
  position:absolute; inset:0; border-radius:inherit; pointer-events:none;
  background:linear-gradient(90deg,
    rgba(255,46,139,.30),
    rgba(127,92,255,.24),
    rgba(0,255,209,.24),
    rgba(255,232,91,.24));
  mix-blend-mode:screen; opacity:.55;
  background-size:200% 100%;
  animation: shimmer 6s linear infinite;
}
div.chat-message.chat-message-from-bot:hover{
  transform:translateY(-1px) rotate(-.25deg);
  box-shadow:0 16px 48px rgba(255,46,139,.32);
}

/* ---------- USER: glossy mint (locked) ---------- */
div.chat-message.chat-message-from-user{
  background: linear-gradient(135deg, #00ffd1, #00d2b1) !important;
  color: #011c18 !important;
  padding: 1rem 1.2rem !important;
  border-radius: var(--radius) var(--radius) .45rem var(--radius) !important;
  border: 1px solid rgba(0,255,209,.4) !important;
  box-shadow:
    0 6px 20px rgba(0,255,209,.25),
    inset 0 1px 0 rgba(255,255,255,.32),
    inset 0 -2px 8px rgba(0,0,0,.08) !important;
  align-self:flex-end;
  position:relative;
  font-weight:500;
  background-clip: padding-box !important;
}
div.chat-message.chat-message-from-user::after{
  content:""; position:absolute; inset:0; border-radius:inherit; pointer-events:none;
  background:linear-gradient(90deg,
    rgba(0,255,209,.35),
    rgba(255,232,91,.27),
    rgba(255,46,139,.27),
    rgba(127,92,255,.27));
  mix-blend-mode:screen; opacity:.45;
  background-size:200% 100%;
  animation: shimmer 7s linear infinite reverse;
}
div.chat-message.chat-message-from-user:hover{
  transform:translateY(-1px);
  box-shadow:0 14px 40px rgba(0,255,209,.32);
}

/* Make paragraph spacing tidy inside bubbles */
div.chat-message p{ margin:0; line-height:1.55; }
div.chat-message p + p{ margin-top:.6rem; }

/* ---------- Zebra Glitter Divider ---------- */
.chat-divider{
  height:8px;
  background:url('zebra-glitter.png') repeat-x center/8px;
  opacity:.85; border-radius:999px;
  margin:1.5rem auto; max-width:80%;
  box-shadow:0 0 12px rgba(255,46,139,.4);
  animation:divider-glow 4s ease-in-out infinite alternate;
}

/* ---------- Footer / Input ---------- */
.chat-footer{
  display:flex; gap:.6rem; align-items:center;
  padding: .75rem var(--gap);
  border-top:1px solid rgba(255,255,255,.08);
  backdrop-filter:blur(10px);
  background:rgba(11,13,20,.72);
}
.chat-input{
  flex:1; min-height:52px;
  border-radius:999px;
  background:rgba(255,255,255,.08);
  color:#f6f6fb;
  border:1px solid rgba(255,255,255,.10);
  padding:.85rem 1rem .85rem 3rem;
  outline:none;
  transition:border .2s, box-shadow .2s;
  position:relative;
}
.chat-input::placeholder{ color:rgba(255,255,255,.6); }
.chat-input:focus{
  border:1px solid var(--chat--color-primary);
  box-shadow:0 0 0 4px rgba(255,46,139,.18), 0 12px 30px rgba(255,46,139,.16);
}
.chat-input::before{
  content:"ðŸ‘€"; position:absolute; left:.9rem; top:50%; transform:translateY(-50%);
  opacity:.9; font-size:1rem;
}
.chat-send, .chat-button{
  border:0; border-radius:999px; padding:.6rem 1rem;
  font-weight:700; color:#0d0e15;
  background:var(--iridescent);
  box-shadow:0 10px 30px rgba(255,46,139,.25);
  transition:transform .12s ease, box-shadow .2s ease, filter .2s ease;
}
.chat-send:hover, .chat-button:hover{
  transform:translateY(-1px);
  box-shadow:0 16px 44px rgba(255,46,139,.35);
  filter:saturate(115%);
}

/* ---------- Typing Indicator ---------- */
.chat-typing{
  display:inline-flex; align-items:center; gap:6px;
  padding:.35rem .6rem; background:rgba(255,255,255,.06);
  border:1px solid rgba(255,255,255,.08); border-radius:999px;
  box-shadow:0 6px 18px rgba(127,92,255,.15);
  margin-left:.2rem;
}
.chat-typing::before{ content:"Nova is judging"; font-size:.75rem; opacity:.85; }
.chat-typing .dot{
  width:6px; height:6px; border-radius:50%; background:#fff; opacity:.8;
  animation:typing 1.2s infinite ease-in-out;
}
.chat-typing .dot:nth-child(2){ animation-delay:.15s; }
.chat-typing .dot:nth-child(3){ animation-delay:.3s; }

/* ---------- Animations ---------- */
@keyframes shimmer{ 0%{background-position:-200% 0;} 100%{background-position:200% 0;} }
@keyframes typing{ 0%,80%,100%{transform:translateY(0); opacity:.55;} 40%{transform:translateY(-3px); opacity:1;} }
@keyframes nova-pop{ 0%{transform:translateY(6px) scale(.98); opacity:0;} 100%{transform:translateY(0) scale(1); opacity:1;} }
@keyframes divider-glow{
  0%{box-shadow:0 0 8px rgba(255,46,139,.4);}
  50%{box-shadow:0 0 20px rgba(127,92,255,.6);}
  100%{box-shadow:0 0 8px rgba(0,255,209,.4);}
}

/* ---------- Reduced Motion ---------- */
@media (prefers-reduced-motion: reduce){
  .chat-message,
  .chat-message::after,
  .chat-typing .dot,
  .chat-divider{ animation:none !important; }
}


/* =========================
   NOVA â€” Background Fix (paste LAST)
   ========================= */

/* 1) Force the page + chat shell to the dark gradient */
html, body {
  background: #0a0b12 !important;
}

main.chat-layout,
.chat-wrapper {
  background:
    radial-gradient(1200px 600px at 10% 0%, rgba(0,255,209,.08), transparent 60%),
    radial-gradient(1000px 700px at 100% 100%, rgba(127,92,255,.08), transparent 60%),
    #0b0d14 !important;
}

/* 2) Kill any light panels behind the bubbles */
.chat-body,
.chat-messages-list {
  background: transparent !important;
}

/* 3) Belt-and-suspenders: if some list items/cards inject a fill */
.chat-messages-list > * {
  background: transparent !important;
  border: none !important;
  box-shadow: none !important;
}
