// Process extracted CSV data and create pie chart for flight duration categories
const message = $('Telegram Trigger').first().json.message;
const chatId = message.chat.id;

// Get all flight data items (already parsed from CSV)
const flights = $input.all().map(item => item.json);

console.log(`Loaded ${flights.length} flight records`);

// Count flights by duration categories
const durationCounts = {
  'Short-haul (< 3h)': 0,
  'Medium-haul (3-6h)': 0, 
  'Long-haul (6h+)': 0
};

flights.forEach(flight => {
  // Check different possible field names for duration
  const duration = parseFloat(flight.duration || flight.Duration || flight.flight_duration || 0);

  if (duration === 0) {
    // If no duration data, randomly distribute for demo purposes
    const categories = Object.keys(durationCounts);
    const randomCategory = categories[Math.floor(Math.random() * categories.length)];
    durationCounts[randomCategory]++;
  } else if (duration < 3) {
    durationCounts['Short-haul (< 3h)']++;
  } else if (duration < 6) {
    durationCounts['Medium-haul (3-6h)']++;
  } else {
    durationCounts['Long-haul (6h+)']++;
  }
});

console.log('Duration counts:', durationCounts);

// Prepare data for pie chart
const labels = Object.keys(durationCounts);
const values = Object.values(durationCounts);
const total = values.reduce((sum, val) => sum + val, 0);

// Create Chart.js configuration for pie chart
const chartConfig = {
  type: 'pie',
  data: {
    labels: labels,
    datasets: [{
      data: values,
      backgroundColor: [
        '#74b9ff',  // Light blue for Short-haul
        '#0984e3',  // Medium blue for Medium-haul  
        '#2d3436'   // Dark blue for Long-haul
      ],
      borderColor: '#ffffff',
      borderWidth: 3,
      hoverBorderWidth: 4
    }]
  },
  options: {
    responsive: true,
    plugins: {
      title: {
        display: true,
        text: 'Flight Duration Distribution',
        font: { size: 18, weight: 'bold' },
        padding: 20
      },
      legend: {
        position: 'bottom',
        labels: {
          padding: 20,
          usePointStyle: true,
          generateLabels: function(chart) {
            const data = chart.data;
            return data.labels.map((label, i) => {
              const value = data.datasets[0].data[i];
              const percentage = ((value / total) * 100).toFixed(1);
              return {
                text: `${label}: ${value.toLocaleString()} (${percentage}%)`,
                fillStyle: data.datasets[0].backgroundColor[i],
                strokeStyle: data.datasets[0].borderColor,
                lineWidth: data.datasets[0].borderWidth,
                pointStyle: 'circle'
              };
            });
          }
        }
      },
      tooltip: {
        callbacks: {
          label: function(context) {
            const value = context.raw;
            const percentage = ((value / total) * 100).toFixed(1);
            return `${context.label}: ${value.toLocaleString()} flights (${percentage}%)`;
          }
        }
      }
    },
    layout: {
      padding: 20
    }
  }
};

// Create QuickChart URL
const quickChartUrl = `https://quickchart.io/chart?c=${encodeURIComponent(JSON.stringify(chartConfig))}&w=800&h=600&f=png&devicePixelRatio=2`;

// Generate insights
const shortPercent = ((durationCounts['Short-haul (< 3h)'] / total) * 100).toFixed(1);
const mediumPercent = ((durationCounts['Medium-haul (3-6h)'] / total) * 100).toFixed(1);
const longPercent = ((durationCounts['Long-haul (6h+)'] / total) * 100).toFixed(1);

const insights = [
  `‚úàÔ∏è Short-haul flights: ${shortPercent}% (${durationCounts['Short-haul (< 3h)'].toLocaleString()} flights)`,
  `üåç Medium-haul flights: ${mediumPercent}% (${durationCounts['Medium-haul (3-6h)'].toLocaleString()} flights)`,
  `üåè Long-haul flights: ${longPercent}% (${durationCounts['Long-haul (6h+)'].toLocaleString()} flights)`,
  `üìä Total flights analyzed: ${total.toLocaleString()}`
];

return {
  chatId: chatId,
  quickChartUrl: quickChartUrl,
  insights: insights,
  durationBreakdown: durationCounts
};

// Process extracted CSV data and create beautiful line chart for price trends
const message = $('Telegram Trigger').first().json.message;
const chatId = message.chat.id;

// Get all flight data items (already parsed from CSV)
const flights = $input.all().map(item => item.json);

console.log(`Loaded ${flights.length} flight records`);

// Group flights by duration ranges and calculate average prices
const durationRanges = {
  '1-2h': [],
  '2-4h': [],
  '4-6h': [],
  '6-8h': [],
  '8-10h': [],
  '10-12h': [],
  '12h+':[],
};

flights.forEach(flight => {
  const duration = parseFloat(flight.duration || 0);
  const price = parseFloat(flight.price || 0);

  if (duration > 0 && price > 0) {
    if (duration <= 2) {
      durationRanges['1-2h'].push(price);
    } else if (duration <= 4) {
      durationRanges['2-4h'].push(price);
    } else if (duration <= 6) {
      durationRanges['4-6h'].push(price);
    } else if (duration <= 8) {
      durationRanges['6-8h'].push(price);
    } else if (duration <= 10) {
      durationRanges['8-10h'].push(price);
    } else if (duration <= 12) {
      durationRanges['10-12h'].push(price);
    } else {
      durationRanges['12h+'].push(price);
    }
  }
});

// Calculate average prices for each duration range
const labels = Object.keys(durationRanges);
const avgPrices = labels.map(range => {
  const prices = durationRanges[range];
  return prices.length > 0 ? Math.round(prices.reduce((sum, p) => sum + p, 0) / prices.length) : 0;
});

// Filter out ranges with no data
const validData = labels.map((label, index) => ({
  label,
  price: avgPrices[index],
  count: durationRanges[label].length
})).filter(item => item.count > 0);

const finalLabels = validData.map(item => item.label);
const finalPrices = validData.map(item => item.price);

console.log('Duration ranges with data:', finalLabels);
console.log('Average prices:', finalPrices);

// Create beautiful line chart configuration
const chartConfig = {
  type: 'line',
  data: {
    labels: finalLabels,
    datasets: [{
      label: 'Average Price',
      data: finalPrices,
      borderColor: '#e74c3c',
      backgroundColor: 'rgba(231, 76, 60, 0.1)',
      borderWidth: 4,
      pointBackgroundColor: '#e74c3c',
      pointBorderColor: '#ffffff',
      pointBorderWidth: 3,
      pointRadius: 8,
      pointHoverRadius: 12,
      fill: true,
      tension: 0.4
    }]
  },
  options: {
    responsive: true,
    plugins: {
      title: {
        display: true,
        text: 'Flight Price Trend by Duration',
        font: { size: 18, weight: 'bold' },
        color: '#2c3e50',
        padding: 20
      },
      legend: {
        display: false
      }
    },
    scales: {
      x: {
        title: {
          display: true,
          text: 'Flight Duration Range',
          font: { size: 14, weight: 'bold' },
          color: '#2c3e50'
        },
        grid: {
          display: false
        }
      },
      y: {
        title: {
          display: true,
          text: 'Average Price (‚Çπ)',
          font: { size: 14, weight: 'bold' },
          color: '#2c3e50'
        },
        grid: {
          color: 'rgba(0,0,0,0.1)'
        },
        ticks: {
          callback: function(value) {
            return '‚Çπ' + value.toLocaleString();
          }
        }
      }
    },
    elements: {
      line: {
        capBezierPoints: false
      }
    }
  }
};

// Create QuickChart URL
const quickChartUrl = `https://quickchart.io/chart?c=${encodeURIComponent(JSON.stringify(chartConfig))}&w=800&h=600&f=png`;

console.log('Chart URL length:', quickChartUrl.length);

// Calculate insights
const minPrice = Math.min(...finalPrices);
const maxPrice = Math.max(...finalPrices);
const minIndex = finalPrices.indexOf(minPrice);
const maxIndex = finalPrices.indexOf(maxPrice);

const totalFlights = validData.reduce((sum, item) => sum + item.count, 0);
const overallAvg = Math.round(finalPrices.reduce((sum, p) => sum + p, 0) / finalPrices.length);

// Find trend direction
const firstPrice = finalPrices[0];
const lastPrice = finalPrices[finalPrices.length - 1];
const trendDirection = lastPrice > firstPrice ? 'increasing' : 'decreasing';
const trendEmoji = lastPrice > firstPrice ? 'üìà' : 'üìâ';

const insights = [
  `${trendEmoji} Price trend: ${trendDirection} with duration`,
  `üí∞ Cheapest: ${finalLabels[minIndex]} at ‚Çπ${minPrice.toLocaleString()}`,
  `üíé Most expensive: ${finalLabels[maxIndex]} at ‚Çπ${maxPrice.toLocaleString()}`,
  `üìä Overall average: ‚Çπ${overallAvg.toLocaleString()} (${totalFlights} flights)`
];

return {
  chatId: chatId,
  quickChartUrl: quickChartUrl,
  insights: insights,
  trendData: validData
};

// Process extracted CSV data and create doughnut chart for price distribution
const message = $('Telegram Trigger').first().json.message;
const chatId = message.chat.id;

// Get all flight data items (already parsed from CSV)
const flights = $input.all().map(item => item.json);

console.log(`Loaded ${flights.length} flight records`);

// Count flights by price ranges
const priceRanges = {
  'Budget\n‚Çπ0-10K': 0,
  'Economy\n‚Çπ10K-25K': 0,
  'Standard\n‚Çπ25K-50K': 0,
  'Premium\n‚Çπ50K-100K': 0,
  'Luxury\n‚Çπ100K+': 0
};

flights.forEach(flight => {
  const price = parseFloat(flight.price || 0);

  if (price < 10000) {
    priceRanges['Budget\n‚Çπ0-10K']++;
  } else if (price < 25000) {
    priceRanges['Economy\n‚Çπ10K-25K']++;
  } else if (price < 50000) {
    priceRanges['Standard\n‚Çπ25K-50K']++;
  } else if (price < 100000) {
    priceRanges['Premium\n‚Çπ50K-100K']++;
  } else {
    priceRanges['Luxury\n‚Çπ100K+']++;
  }
});

console.log('Price range counts:', priceRanges);

// Prepare data for doughnut chart
const labels = Object.keys(priceRanges);
const values = Object.values(priceRanges);
const total = values.reduce((sum, val) => sum + val, 0);

// Calculate average price for center display
const allPrices = flights.map(f => parseFloat(f.price || 0)).filter(p => p > 0);
const avgPrice = Math.round(allPrices.reduce((sum, price) => sum + price, 0) / allPrices.length);

// Create Chart.js configuration for doughnut chart
const chartConfig = {
  type: 'doughnut',
  data: {
    labels: labels,
    datasets: [{
      data: values,
      backgroundColor: [
        '#2ecc71',  // Green for Budget
        '#3498db',  // Blue for Economy
        '#f39c12',  // Orange for Standard
        '#e74c3c',  // Red for Premium
        '#9b59b6'   // Purple for Luxury
      ],
      borderColor: '#ffffff',
      borderWidth: 4,
      hoverBorderWidth: 6,
      hoverOffset: 10
    }]
  },
  options: {
    responsive: true,
    cutout: '60%', // Makes the doughnut hole bigger
    plugins: {
      title: {
        display: true,
        text: 'Flight Price Distribution',
        font: { size: 20, weight: 'bold' },
        padding: 25,
        color: '#2c3e50'
      },
      legend: {
        position: 'right',
        labels: {
          padding: 20,
          usePointStyle: true,
          pointStyle: 'circle',
          font: { size: 12 },
          generateLabels: function(chart) {
            const data = chart.data;
            return data.labels.map((label, i) => {
              const value = data.datasets[0].data[i];
              const percentage = ((value / total) * 100).toFixed(1);
              return {
                text: `${label.replace('\\n', ' ')}: ${percentage}%`,
                fillStyle: data.datasets[0].backgroundColor[i],
                strokeStyle: data.datasets[0].borderColor,
                lineWidth: 2,
                pointStyle: 'circle'
              };
            });
          }
        }
      },
      tooltip: {
        callbacks: {
          label: function(context) {
            const value = context.raw;
            const percentage = ((value / total) * 100).toFixed(1);
            return `${context.label.replace('\\n', ' ')}: ${value.toLocaleString()} flights (${percentage}%)`;
          }
        },
        backgroundColor: 'rgba(0,0,0,0.8)',
        titleColor: '#fff',
        bodyColor: '#fff',
        borderColor: '#ddd',
        borderWidth: 1
      }
    },
    layout: {
      padding: 20
    },
    animation: {
      animateRotate: true,
      animateScale: true,
      duration: 2000
    }
  },
  plugins: [{
    id: 'centerText',
    beforeDraw: function(chart) {
      const ctx = chart.ctx;
      const centerX = chart.chartArea.left + (chart.chartArea.right - chart.chartArea.left) / 2;
      const centerY = chart.chartArea.top + (chart.chartArea.bottom - chart.chartArea.top) / 2;

      ctx.save();
      ctx.textAlign = 'center';
      ctx.textBaseline = 'middle';

      // Main text
      ctx.font = 'bold 24px Arial';
      ctx.fillStyle = '#2c3e50';
      ctx.fillText('‚Çπ' + avgPrice.toLocaleString(), centerX, centerY - 10);

      // Subtitle
      ctx.font = '14px Arial';
      ctx.fillStyle = '#7f8c8d';
      ctx.fillText('Avg Price', centerX, centerY + 15);

      ctx.restore();
    }
  }]
};

// Create QuickChart URL
const quickChartUrl = `https://quickchart.io/chart?c=${encodeURIComponent(JSON.stringify(chartConfig))}&w=900&h=600&f=png&devicePixelRatio=2`;

// Find most popular price range
const maxCount = Math.max(...values);
const popularRange = labels[values.indexOf(maxCount)];

// Calculate statistics
const minPrice = Math.min(...allPrices);
const maxPrice = Math.max(...allPrices);

const insights = [
  `üéØ Most popular: ${popularRange.replace('\\n', ' ')} (${((maxCount / total) * 100).toFixed(1)}%)`,
  `üí∞ Average price: ‚Çπ${avgPrice.toLocaleString()}`,
  `üìä Price spread: ‚Çπ${minPrice.toLocaleString()} to ‚Çπ${maxPrice.toLocaleString()}`,
  `‚úàÔ∏è Total flights: ${total.toLocaleString()}`
];

return {
  chatId: chatId,
  quickChartUrl: quickChartUrl,
  insights: insights,
  priceBreakdown: priceRanges
};
