const patients = $input.all();
const reports = [];

for (const patient of patients) {
  const currentDate = new Date().toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  const reportData = {
    patientId: patient.json['Patient ID'],
    patientName: patient.json['Patient Name'],
    age: patient.json['Age'],
    condition: patient.json['Current Condition'],
