      temperature: patient.json['Temperature'] || 'N/A',
      bloodPressure: patient.json['Blood Pressure'] || 'N/A',
      heartRate: patient.json['Heart Rate'] || 'N/A'
    },
    medication: patient.json['Current Medication'] || 'None',
    doctorEmail: patient.json['Assigned Doctor Email'],
    doctorWhatsApp: patient.json['Doctor WhatsApp'],
    lastUpdated: patient.json['Last Updated'] || currentDate,
    reportDate: currentDate,
    priority: patient.json['Priority'] || 'Normal'
  };

  // Generate condition summary
  let conditionStatus = 'Stable';
  if (patient.json['Temperature'] && parseFloat(patient.json['Temperature']) > 100.4) {
