const equipmentList = $input.all();
const alerts = [];
const today = new Date();

for (const equipment of equipmentList) {
  const equipData = equipment.json;

  // Skip header row
  if (equipData['Equipment ID'] === 'Equipment ID') continue;

  // Parse dates
  const lastMaintenance = new Date(equipData['Last Maintenance']);
  const lastCalibration = new Date(equipData['Last Calibration']);
  const warrantyExpiry = new Date(equipData['Warranty Expiry']);

  // Calculate days since last maintenance/calibration
  const daysSinceMaintenance = Math.floor((today - lastMaintenance) / (1000 * 60 * 60 * 24));
  const daysSinceCalibration = Math.floor((today - lastCalibration) / (1000 * 60 * 60 * 24));
  const daysToWarrantyExpiry = Math.floor((warrantyExpiry - today) / (1000 * 60 * 60 * 24));

  // Get maintenance and calibration intervals
  const maintenanceInterval = parseInt(equipData['Maintenance Interval (Days)']) || 90;
  const calibrationInterval = parseInt(equipData['Calibration Interval (Days)']) || 365;

  // Current usage hours
  const currentUsage = parseFloat(equipData['Current Usage Hours']) || 0;
  const maxUsage = parseFloat(equipData['Max Usage Hours']) || 8760; // Default 1 year
  const usagePercentage = (currentUsage / maxUsage) * 100;

  // Determine alert types and priority
  let alertTypes = [];
  let priority = 'Normal';
  let alertMessage = '';

  // Maintenance alerts
  if (daysSinceMaintenance >= maintenanceInterval) {
    alertTypes.push('OVERDUE_MAINTENANCE');
    priority = 'High';
    alertMessage += `âš ï¸ OVERDUE MAINTENANCE (${daysSinceMaintenance} days overdue)\n`;
  } else if (daysSinceMaintenance >= maintenanceInterval - 7) {
    alertTypes.push('UPCOMING_MAINTENANCE');
    if (priority === 'Normal') priority = 'Medium';
    const daysLeft = maintenanceInterval - daysSinceMaintenance;
    alertMessage += `ðŸ”§ Maintenance due in ${daysLeft} days\n`;
  }

  // Calibration alerts
  if (daysSinceCalibration >= calibrationInterval) {
    alertTypes.push('OVERDUE_CALIBRATION');
    priority = 'High';
    alertMessage += `âš ï¸ OVERDUE CALIBRATION (${daysSinceCalibration} days overdue)\n`;
  } else if (daysSinceCalibration >= calibrationInterval - 14) {
    alertTypes.push('UPCOMING_CALIBRATION');
    if (priority === 'Normal') priority = 'Medium';
    const daysLeft = calibrationInterval - daysSinceCalibration;
    alertMessage += `ðŸŽ¯ Calibration due in ${daysLeft} days\n`;
  }

  // Usage alerts
  if (usagePercentage >= 95) {
    alertTypes.push('CRITICAL_USAGE');
    priority = 'Critical';
    alertMessage += `ðŸš¨ CRITICAL USAGE: ${usagePercentage.toFixed(1)}% of max hours\n`;
  } else if (usagePercentage >= 80) {
    alertTypes.push('HIGH_USAGE');
    if (priority === 'Normal' || priority === 'Medium') priority = 'Medium';
    alertMessage += `ðŸ“Š High usage: ${usagePercentage.toFixed(1)}% of max hours\n`;
  }

  // Warranty alerts
  if (daysToWarrantyExpiry <= 0) {
    alertTypes.push('WARRANTY_EXPIRED');
    priority = 'High';
    alertMessage += `âŒ WARRANTY EXPIRED (${Math.abs(daysToWarrantyExpiry)} days ago)\n`;
  } else if (daysToWarrantyExpiry <= 30) {
    alertTypes.push('WARRANTY_EXPIRING');
    if (priority === 'Normal') priority = 'Medium';
    alertMessage += `â° Warranty expires in ${daysToWarrantyExpiry} days\n`;
  }

  // Status-based alerts
  if (equipData['Status'] === 'Out of Service') {
    alertTypes.push('OUT_OF_SERVICE');
    priority = 'Critical';
    alertMessage += `ðŸ”´ EQUIPMENT OUT OF SERVICE\n`;
  } else if (equipData['Status'] === 'Needs Repair') {
    alertTypes.push('NEEDS_REPAIR');
    priority = 'High';
    alertMessage += `ðŸ”§ EQUIPMENT NEEDS REPAIR\n`;
  }

  // Only create alert if there are issues
  if (alertTypes.length > 0) {
    const alert = {
      equipmentId: equipData['Equipment ID'],
      equipmentName: equipData['Equipment Name'],
      location: equipData['Location'],
      department: equipData['Department'],
      status: equipData['Status'],
      alertTypes: alertTypes,
      priority: priority,
      alertMessage: alertMessage.trim(),
      technicianEmail: equipData['Technician Email'],
      technicianWhatsApp: equipData['Technician WhatsApp'],
      supervisorEmail: equipData['Supervisor Email'],
      daysSinceMaintenance: daysSinceMaintenance,
      daysSinceCalibration: daysSinceCalibration,
      usagePercentage: usagePercentage.toFixed(1),
      daysToWarrantyExpiry: daysToWarrantyExpiry,
      manufacturer: equipData['Manufacturer'],
      model: equipData['Model'],
      serialNumber: equipData['Serial Number'],
      currentDate: today.toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      })
    };

    // Create detailed email content
    alert.emailSubject = `ðŸ¥ Equipment Alert: ${alert.equipmentName} (${alert.equipmentId}) - ${priority} Priority`;

    alert.emailBody = `
Dear Maintenance Team,

Equipment Maintenance Alert - ${alert.currentDate}

ðŸ¥ EQUIPMENT DETAILS:
