Equipment ID: ${alert.equipmentId}
Name: ${alert.equipmentName}
Location: ${alert.location}
Department: ${alert.department}
Status: ${alert.status}
Priority: ${priority}

ðŸ“‹ EQUIPMENT SPECIFICATIONS:
â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”
Manufacturer: ${alert.manufacturer}
Model: ${alert.model}
Serial Number: ${alert.serialNumber}

âš ï¸ ALERT DETAILS:
â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”
${alert.alertMessage}

ðŸ“Š MAINTENANCE STATUS:
â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”
â€¢ Days since last maintenance: ${alert.daysSinceMaintenance}
â€¢ Days since last calibration: ${alert.daysSinceCalibration}
â€¢ Current usage: ${alert.usagePercentage}% of maximum
â€¢ Warranty expires in: ${alert.daysToWarrantyExpiry} days

ðŸ”§ ACTION REQUIRED:
Please schedule appropriate maintenance/calibration as indicated above.
Contact supervisor if equipment is critical for patient care.

Best regards,
Equipment Management System
    `;

    // Create WhatsApp message
    alert.whatsappMessage = `ðŸ¥ *EQUIPMENT ALERT*\n\nðŸ”§ *${alert.equipmentName}*\nðŸ“ Location: ${alert.location}\nðŸš¨ Priority: ${priority}\n\n${alert.alertMessage.replace(/\n/g, '\n')}\n\nðŸ“Š Usage: ${alert.usagePercentage}%\nðŸ“… Alert Date: ${alert.currentDate}`;

    alerts.push(alert);
  }
}

// Sort alerts by priority (Critical > High > Medium > Normal)
const priorityOrder = { 'Critical': 1, 'High': 2, 'Medium': 3, 'Normal': 4 };
alerts.sort((a, b) => priorityOrder[a.priority] - priorityOrder[b.priority]);

return alerts.map(alert => ({ json: alert }));
