// input: daysAgo (number of days)
const daysAgo = parseInt($json["message"]["text"], 10);
const date = new Date();
date.setDate(date.getDate() - daysAgo);
const yyyy = date.getFullYear();
const mm = String(date.getMonth() + 1).padStart(2, '0');
const dd = String(date.getDate()).padStart(2, '0');
return [{ dateString: `${yyyy}/${mm}/${dd}` }];

function extractHtml(payload) {
  if (!payload) return '';
  if (payload.body && payload.body.data) {
    return Buffer.from(payload.body.data, 'base64').toString('utf-8');
  }
  function findHtmlPart(parts) {
    for (const part of parts) {
      if (part.mimeType === 'text/html' && part.body && part.body.data) {
        return Buffer.from(part.body.data, 'base64').toString('utf-8');
      }
      if (part.parts) {
        const result = findHtmlPart(part.parts);
        if (result) return result;
      }
    }
    return '';
  }
  if (payload.parts) {
    const html = findHtmlPart(payload.parts);
    if (html) return html;
  }
  return '';
}

let html = $json.html || '';
if (!html && $json.payload) {
  html = extractHtml($json.payload);
}
if (!html && $json.text) {
  html = $json.text;
}

// Clean 'from': keep only the sender's name
let from = $json.from?.text || $json.from || '';
const match = from.match(/^\"?([^\"<]+)\"?\s*<[^>]+>$/);
if (match) {
  from = match[1].trim();
}

// Convert date to DD.MM.YYYY format
let date = $json.date || '';
let formattedDate = date;
if (date) {
  const d = new Date(date);
  const day = String(d.getDate()).padStart(2, '0');
  const month = String(d.getMonth() + 1).padStart(2, '0');
  const year = d.getFullYear();
  formattedDate = `${day}.${month}.${year}`;
}

const subject = $json.subject || '';

return [{
  html,
  subject,
  from,
  date: formattedDate
}];

/**
 * This code is intended for use in the "Code" node in n8n.
 * It merges the 'topics' arrays from multiple incoming items into a single array.
 *
 * Incoming data (items) have the following structure:
 * [
 * { json: { message: { content: { topics: [...] } } } },
 * { json: { message: { content: { topics: [...] } } } },
 * ...
 * ]
 */

// We use the flatMap method, which is a combination of map and flat.
// It iterates over each item (item) in the incoming array (items),
// extracts the 'topics' array and immediately flattens all arrays into one.
const allTopics = items.flatMap(item => {
  // We use optional chaining (?.) for safe access to the nested property.
  // This prevents an error if 'message' or 'content' is missing in any of the items.
  // If the path is not found, return an empty array [], so flatMap can work correctly.
  return item.json?.message?.content?.topics || [];
});

// The "Code" node must return an array of objects.
// We return one object containing the 'json' property with our merged 'topics' array.
// This result will be available at the output of the node for further use.
return [{
  json: {
    topics: allTopics
  }
}];

const topics = $json.topics;
const list = topics.map((t, idx) =>
  `${idx + 1}. *${t.title}*

${t.descr}

${t.subject}
→ ${t.from} - ${t.date}`
).join('

');

return [{ json: { message: `${list}` } }];

/**
 * This code is intended for use in the "Code" node in n8n.
 * It prepares text for sending to Telegram in 'HTML' mode.
 * 1. Fixes "broken" formatting (*...* and _..._) by adding a closing symbol if their count is odd.
 * 2. Converts *text* to <b>text</b> and _text_ to <i>text</i>, even if the text spans multiple lines.
 * 3. Escapes basic HTML characters (<, >, &) for safety.
 *
 * IMPORTANT: In the Telegram node, Parse Mode must be set to HTML.
 *
 * The code processes EACH incoming item in the array.
 */

const correctedItems = items.map(item => {
  // FIXED: Use the 'text' key that comes from the "Split" node.
  let text = item.json.text;

  if (!text) {
    return item;
  }

  // --- STEP 1: Fix unbalanced formatting characters ---
  const fixUnbalanced = (str, char) => {
    // Use new RegExp to create a dynamic regular expression
    const count = (str.match(new RegExp(`\\${char}`, 'g')) || []).length;
    if (count % 2 !== 0) {
      return str + char;
    }
    return str;
  };

  text = fixUnbalanced(text, '*');
  text = fixUnbalanced(text, '_');

  // --- STEP 2: Convert to safe HTML ---
  // First, escape basic characters in the entire text so they don't conflict with tags.
  let safeText = text
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;');

  // Now replace Markdown with HTML tags.
  // Use the 's' (dotAll) flag so '.' also matches newline characters.
  // This is important for your multi-line italics.
  safeText = safeText
    .replace(/\*(.*?)\*/gs, '<b>$1</b>')
    .replace(/_(.*?)_/gs, '<i>$1</i>');


  // Update the 'text' field in the current item.
  // In the Telegram node, use {{ $json.text }}
  item.json.text = safeText;

  // Return the modified item.
  return item;
});

// Return the full array of processed items.
return correctedItems;

const CHUNK = 3500;
const txt = $json.message;
const parts = txt.match(/[\s\S]{1,3500}/g) || [];
return parts.map(p => ({ json: { text: p } }));

/**
 * This code is intended for use in the "Code" node in n8n.
 * Its task is to prepare data from the Gmail node for the next node (LLM).
 * It does not parse HTML, it only passes it along with other fields.
 *
 * Incoming data (items) is the result of the Gmail node.
 * [
 * { json: { html: "...", subject: "...", from: "...", date: "DD.MM.YYYY" } }
 * ]
 *
 * The code converts the date from "DD.MM.YYYY" to "MM.DD" and passes
 * all the necessary fields (html, subject, from, date) to the next step.
 */

// Use .map() to transform each incoming item.
const results = items.map(item => {
  // Extract the required fields from the incoming JSON.
  const { html, subject, from, date: originalDate } = item.json;

  // Check for required fields.
  if (!html || !originalDate) {
    // If there is no data, return null to filter this item later.
    return null;
  }

  // 1. Split the date string into components (day, month, year).
  const [day, month] = originalDate.split('.').slice(0, 2);

  // 2. Form a new string in the format "MM.DD".
  const newDate = `${month}.${day}`;

  // 3. Return a new object in the structure expected by
  // the next node (LLM).
  return {
    json: {
      html,
      subject,
      from,
      date: newDate
    }
  };
});

// Filter out empty results and return the array for the next node.
return results.filter(item => item !== null);

