function extractHtml(payload) {
  if (!payload) return '';
  if (payload.body && payload.body.data) {
    return Buffer.from(payload.body.data, 'base64').toString('utf-8');
  }
  function findHtmlPart(parts) {
    for (const part of parts) {
      if (part.mimeType === 'text/html' && part.body && part.body.data) {
        return Buffer.from(part.body.data, 'base64').toString('utf-8');
      }
      if (part.parts) {
        const result = findHtmlPart(part.parts);
        if (result) return result;
      }
    }
    return '';
  }
  if (payload.parts) {
    const html = findHtmlPart(payload.parts);
    if (html) return html;
  }
  return '';
}

let html = $json.html || '';
if (!html && $json.payload) {
  html = extractHtml($json.payload);
}
if (!html && $json.text) {
  html = $json.text;
}

// Clean 'from': keep only the sender's name
let from = $json.from?.text || $json.from || '';
const match = from.match(/^\"?([^\"<]+)\"?\s*<[^>]+>$/);
if (match) {
  from = match[1].trim();
}

// Convert date to DD.MM.YYYY format
let date = $json.date || '';
let formattedDate = date;
if (date) {
  const d = new Date(date);
  const day = String(d.getDate()).padStart(2, '0');
  const month = String(d.getMonth() + 1).padStart(2, '0');
  const year = d.getFullYear();
  formattedDate = `${day}.${month}.${year}`;
}

const subject = $json.subject || '';

return [{
  html,
  subject,
  from,
  date: formattedDate
}];

/**
 * This code is intended for use in the "Code" node in n8n.
 * It merges the 'topics' arrays from several incoming items into a single array.
 *
 * Incoming data (items) have the following structure:
 * [
 * { json: { message: { content: { topics: [...] } } } },
 * { json: { message: { content: { topics: [...] } } } },
 * ...
 * ]
 */

// We use the flatMap method, which is a combination of map and flat.
// It iterates over each element (item) in the incoming array (items),
// extracts the 'topics' array and immediately flattens all collected arrays into one.
const allTopics = items.flatMap(item => {
  // We use optional chaining (?.) for safe access to the nested property.
  // This prevents an error if 'message' or 'content' is missing in any element.
  // If the path is not found, return an empty array [] so flatMap can work correctly.
  return item.json?.message?.content?.topics || [];
});

// The "Code" node must return an array of objects.
// We return one object containing the 'json' property with our merged 'topics' array.
// This result will be available on the node's output for further use.
return [{
  json: {
    topics: allTopics
  }
}];

/**
 * This code is intended for use in the "Code" node in n8n.
 * Its task is to prepare data from the Gmail node for the next node (LLM).
 * It does not parse HTML; it only passes it along with other fields.
 *
 * Incoming data (items) — this is the result of the Gmail node.
 * [
 * { json: { html: "...", subject: "...", from: "...", date: "DD.MM.YYYY" } }
 * ]
 *
 * The code converts the date from "DD.MM.YYYY" to "MM.DD" and passes
 * all the necessary fields (html, subject, from, date) to the next step.
 */

// Use .map() to transform each incoming item.
const results = items.map(item => {
  // Extract the required fields from the incoming JSON.
  const { html, subject, from, date: originalDate } = item.json;

  // Check for required fields.
  if (!html || !originalDate) {
    // If there is no data, return null to filter this item later.
    return null;
  }

  // 1. Split the date string into components (day, month, year).
  const [day, month] = originalDate.split('.').slice(0, 2);

  // 2. Form a new string in the "MM.DD" format.
  const newDate = `${month}.${day}`;

  // 3. Return a new object in the structure expected by
  // the next node (LLM).
  return {
    json: {
      html,
      subject,
      from,
      date: newDate
    }
  };
});

// Filter out empty results and return the array for the next node.
return results.filter(item => item !== null);

/**
 * This code is intended for use in the "Code" node in n8n.
 * It takes an array of topics and creates a single,
 * well-formatted HTML email for sending.
 *
 * This node replaces the nodes "Formatting", "Split", and "Sanitizer".
 */

// Get the array of topics from the previous node.
const topics = $json.topics;

// Check if there are topics to process.
if (!Array.isArray(topics) || topics.length === 0) {
  return []; // If there are no topics, stop the workflow.
}

// --- Create HTML markup for each news item ---
const topicsHtml = topics.map((topic, index) => {
  // Escape basic HTML characters in the data to avoid breaking the markup.
  const escapeHtml = (unsafe) => {
    if (!unsafe) return '';
    return unsafe
         .replace(/&/g, "&amp;")
         .replace(/</g, "&lt;")
         .replace(/>/g, "&gt;")
         .replace(/\"/g, "&quot;")
         .replace(/'/g, "&#039;");
  };

  const title = escapeHtml(topic.title);
  const descr = escapeHtml(topic.descr).replace(/\n/g, '<br>'); // Preserve line breaks in the description
  const subject = escapeHtml(topic.subject);
  const from = escapeHtml(topic.from);
  const date = escapeHtml(topic.date);

  // Build a nice HTML block for a single news item.
  return `
    <div style="margin-bottom: 24px; padding-bottom: 16px; border-bottom: 1px solid #eeeeee;">
      <h3 style="margin: 0 0 8px 0; font-size: 18px; color: #1a1a1a;">
        ${index + 1}. ${title}
      </h3>
      <p style="margin: 0 0 12px 0; font-size: 16px; color: #333333; line-height: 1.5;">
        ${descr}
      </p>
      <p style="margin: 0; font-size: 14px; color: #777777; font-style: italic;">
        ${subject}<br>
        → ${from} - ${date}
      </p>
    </div>
  `;
}).join(''); // Join all HTML blocks into a single string.

// --- Wrap everything in a full HTML document with styles ---
const finalHtml = `
  <!DOCTYPE html>
  <html>
  <head>
    <meta charset="utf-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Ваш новостной дайджест</title>
  </head>
  <body style="margin: 0; padding: 0; background-color: #f7f7f7; font-family: Arial, sans-serif;">
    <table width="100%" border="0" cellpadding="0" cellspacing="0">
      <tr>
        <td align="center" style="padding: 20px;">
          <table width="600" border="0" cellpadding="20" cellspacing="0" style="background-color: #ffffff; border-radius: 8px;">
            <tr>
              <td>
                <h1 style="text-align: center; color: #1a1a1a;">Новостной дайджест</h1>
                ${topicsHtml}
              </td>
            </tr>
          </table>
        </td>
      </tr>
    </table>
  </body>
  </html>
`;

// Return the result. In the "Send Email" node use {{ $json.htmlBody }}
return [{ json: { htmlBody: finalHtml } }];

