const people = $json.people || [];

return [
  {
    json: {
      "include": {
        "email": true,
        "linkedInUrl": false,
        "mobile": true,
        "jobHistory": true
      },
      "notificationOptions": {
        "webhookUrl": ""
      },
      people: people.map((person) => ({
        firstName: person.firstName || "",
        lastName: person.lastName || "",
        companyName: person.companyName || "",
        companyDomain: person.companyDomain || "",
        linkedinUrl: person.linkedInUrl || "",
        externalID: `${person.firstName}_${person.lastName}_${person.companyDomain}`.toLowerCase().replace(/[^a-z0-9_]/g, '_')
      }))
    }
  }
];

const people = $json.people || [];

return people.map(person => {
  return {
    json: {
      id: person.id || '',
      firstName: person.firstName || '',
      lastName: person.lastName || '',
      email: person.emails?.[0]?.email || '',
      phone: person.mobilePhones?.[0]?.mobilePhone || '',
      jobTitle: person.jobHistory[0].jobTitle || '',
      companyName: person.jobHistory[0].companyName || '',
      companyWebsite: person.companyDomain || '',
      linkedinUrl: person.linkedInUrl || '',
      country: person.country || '',
      status: person.status || ''
    }
  };
});

// Récupère tous les items entrants
const allItems = $items();

// Sépare les items du sheet et celui avec companyDomains
const sheetItems = allItems.filter(i =>
  i.json.departments || i.json.jobTitles || i.json.seniorities || i.json.countries
);
const domainItems = allItems.filter(i => i.json.companyDomains);

// Helper : extrait, splitte, déduplique et nettoie
function extractList(items, fieldName) {
  const raw = items.flatMap(item => {
    const v = item.json[fieldName];
    if (!v) return [];
    if (typeof v === 'string' && v.includes(',')) {
      return v.split(',').map(s => s.trim()).filter(Boolean);
    }
    return [v];
  });
  // déduplique puis enlève les valeurs falsy
  return [...new Set(raw)].filter(Boolean);
}

const departments = extractList(sheetItems, 'departments');
const jobTitles = extractList(sheetItems, 'jobTitles');
const seniorities = extractList(sheetItems, 'seniorities');
const countries = extractList(sheetItems, 'countries');

// Récupère et déduplique les companyDomains
const companyDomains = [...new Set(
  domainItems.flatMap(i => {
    const cd = i.json.companyDomains;
    if (!cd) return [];
    if (Array.isArray(cd)) return cd;
    if (typeof cd === 'string') return [cd];
    return [];
  })
)].filter(Boolean);

// Payload final
return {
  companies: {
    domains: companyDomains
  },
  people: {
    departments,
    jobTitles,
    seniorities,
    countries
  },
  limit: 200
};

const company_domains = $input.all().map(item => {
  const domain = item.json.properties.domain.value || "";
  return domain;
}).filter(Boolean); // enlève les null ou undefined

return {json: { companyDomains: company_domains }}

const deal = items[0].json;
const portalId = deal.portalId;
const dealId = deal.dealId ?? deal.deal?.id ?? deal.deal?.dealId ?? null;
const dealName = deal.properties?.dealname?.value ?? '';

return [
  {
    json: {
      portalId,
      dealId,
      dealName,
    },
  },
];

// --- Assume first item is the trigger, rest are contact items ---
const trigger = items[0]?.json || {};
const contactItems = items.slice(1);

// --- Extract deal and portal info from trigger ---
const portalId = trigger.portalId ?? trigger["portal-id"] ?? (() => {
  // fallback: try to grab from a contact if trigger lacks it
  const fallback = contactItems.find(i => i.json?.properties?.hs_object_source_id?.value);
  return fallback?.json?.properties?.hs_object_source_id?.value || '';
})();
const dealName = trigger.dealName
const dealId = trigger.dealId ?? trigger.deal?.id ?? trigger.deal?.dealId ?? null;

function buildDealLink(portalId, dealId) {
  return `https://app.hubspot.com/contacts/${portalId}/record/0-3/${dealId}`;
}
const dealLink = portalId && dealId ? buildDealLink(portalId, dealId) : '';

// --- Buying group criteria and company domain (fallback) ---
const buyingGroupCriteria = trigger.buyingGroupCriteria || 'your buying group criteria';

let companyDomain = trigger.companyDomain || '';
if (!companyDomain) {
  // infer from first available contact email
  const someContact = contactItems.find(i => i.json.searchPerson?.email || i.json.properties?.email?.value);
  const emailStr = (someContact?.json.searchPerson?.email || someContact?.json.properties?.email?.value || '').trim();
  if (emailStr.includes('@')) {
    companyDomain = emailStr.split('@')[1];
  }
}
if (!companyDomain) companyDomain = '(unknown domain)';

// --- Normalize contacts (no job title) ---
const contacts = contactItems.map(item => {
  const props = item.json.properties || {};
  const search = item.json.searchPerson || {};

  const firstName = search.firstName || props.firstname?.value || '';
  const lastName = search.lastName || props.lastname?.value || '';
  const email = search.email || props.email?.value || '';
  const phone = props.phone?.value || '';
  const companyName = props.company?.value || '';
  const jobTitle = props.jobtitle?.value || '';


  const vid = item.json.vid || item.json["hs_object_id"] || '';

  return { vid, firstName, lastName, email, phone, companyName, jobTitle };
});

// --- Build HTML table rows ---
const rowsHtml = contacts.map(c => {
  const fullName = `${c.firstName} ${c.lastName}`.trim() || '(no name)';
  const emailCell  = c.email  ? `<a href="mailto:${c.email}">${c.email}</a>` : '—';
  const phoneCell  = c.phone  ? `<a href="tel:${c.phone}">${c.phone}</a>`   : '—';
  const jobTitleCell   = c.jobTitle   || '—';
  const companyCell    = c.companyName|| '—';
  const contactHubspotUrl = portalId
    ? `https://app.hubspot.com/contacts/${portalId}/record/0-1/${c.vid}`
    : '#';
  const hubspotCell = `<a href="${contactHubspotUrl}" target="_blank">View in HubSpot</a>`;

  return `
    <tr>
      <td style="padding:6px;border:1px solid #ccc;">${fullName}</td>
      <td style="padding:6px;border:1px solid #ccc;">${jobTitleCell}</td>
      <td style="padding:6px;border:1px solid #ccc;">${companyCell}</td>
      <td style="padding:6px;border:1px solid #ccc;">${emailCell}</td>
      <td style="padding:6px;border:1px solid #ccc;">${phoneCell}</td>
      <td style="padding:6px;border:1px solid #ccc;">${hubspotCell}</td>
    </tr>
  `;
}).join('');

// --- Compose HTML email with hidden preheader ---
const htmlMessage = `
  <!DOCTYPE html>
  <html>
    <body style="font-family: system-ui, -apple-system, BlinkMacSystemFont, Arial, sans-serif; line-height:1.4; color:#1f2937; padding:0; margin:0;">
      <div style="max-width:800px; margin:0 auto; padding:20px;">
        <!-- Hidden preheader for inbox preview -->
        <div style="display:none;font-size:1px;line-height:1px;max-height:0;max-width:0;opacity:0;overflow:hidden;">
          Contacts enriched based on your buying group criteria and domain—see direct links.
        </div>

        <p>Hi Team,</p>

        <p>
          Following the creation of the <a href="${dealLink}" target="_blank">HubSpot deal</a> <strong>${dealName}</strong>., here are the contacts we enriched and pushed into HubSpot based on ${buyingGroupCriteria} and the company domain <strong>${companyDomain}</strong>.
        </p>

        <p>
          <strong>Total contacts processed:</strong> ${contacts.length}
          ${dealLink ? `<br/><strong>Deal link:</strong> <a href="${dealLink}" target="_blank">View deal in HubSpot</a>` : ''}
        </p>

        <table style="width:100%; border-collapse:collapse; margin-top:10px; margin-bottom:20px;">
          <thead>
            <tr>
              <th style="padding:8px; border:1px solid #d1d5db; text-align:left; background:#f3f4f6;">Name</th>
              <th style="padding:8px; border:1px solid #d1d5db; text-align:left; background:#f3f4f6;">Job Title</th>
              <th style="padding:8px; border:1px solid #d1d5db; text-align:left; background:#f3f4f6;">Company</th>
              <th style="padding:8px; border:1px solid #d1d5db; text-align:left; background:#f3f4f6;">Email</th>
              <th style="padding:8px; border:1px solid #d1d5db; text-align:left; background:#f3f4f6;">Phone</th>
              <th style="padding:8px; border:1px solid #d1d5db; text-align:left; background:#f3f4f6;">HubSpot</th>
            </tr>
          </thead>
          <tbody>
            ${rowsHtml}
          </tbody>
        </table>

        <p>Best regards,<br/>
        Enrichment Workflow Bot</p>
      </div>
    </body>
  </html>
`;

// --- Compose plain-text fallback ---
const plainRows = contacts.map(c => {
  const fullName = `${c.firstName} ${c.lastName}`.trim() || '(no name)';
  const email = c.email || '—';
  const phone = c.phone || '—';
  const contactHubspotUrl = portalId ? `https://app.hubspot.com/contacts/${portalId}/record/0-1/${c.vid}` : '';
  return `- ${fullName} | ${email} | ${phone} | ${contactHubspotUrl}`;
}).join('\n');

const textMessage = `
Hi Team,

Following the creation of the HubSpot deal${dealId ? ` (ID: ${dealId})` : ''}, here are the contacts we enriched and pushed into HubSpot based on ${buyingGroupCriteria} and the company domain ${companyDomain}.

Total contacts processed: ${contacts.length}

Contacts:
${plainRows}

Best regards,
Surfe Enrichment Workflow Bot
`;

// --- Output for Gmail node (no 'to' field) ---
return [
  {
    json: {
      subject: 'New Enriched Contacts Added to Your HubSpot Deal',
      message: htmlMessage,
      text: textMessage,
    },
  },
];
