// Process OpenAI response and structure the report data
const aiResponse = $input.first().json;
const patientData = $('Extract Image Data').first().json;

const aiAnalysis = aiResponse.choices[0].message.content;

// Structure the analysis into sections
const sections = aiAnalysis.split('\n\n');
let findings = '';
let explanation = '';
let nextSteps = '';

// Try to parse the AI response into structured sections
sections.forEach(section => {
  if (section.toLowerCase().includes('findings') || section.toLowerCase().includes('shows')) {
    findings += section + ' ';
  } else if (section.toLowerCase().includes('means') || section.toLowerCase().includes('indicates')) {
    explanation += section + ' ';
  } else if (section.toLowerCase().includes('next') || section.toLowerCase().includes('recommend')) {
    nextSteps += section + ' ';
  }
});

// If structured parsing didn't work well, use the full response
if (!findings && !explanation) {
  findings = aiAnalysis.substring(0, aiAnalysis.length / 2);
  explanation = aiAnalysis.substring(aiAnalysis.length / 2);
}

return [{
  json: {
    ...patientData,
    ai_analysis: aiAnalysis,
    findings: findings.trim() || 'Analysis completed',
    explanation: explanation.trim() || 'Please consult with your doctor for detailed explanation',
    next_steps: nextSteps.trim() || 'Follow up with your healthcare provider as recommended',
    report_generated: new Date().toISOString(),
    confidence_note: 'This AI analysis is for informational purposes only. Always consult your doctor for medical advice.'
  }
}];

// Return success response with report details
const reportData = $('Generate PDF Report').first().json;
const pdfData = $('Convert to PDF').first().json;

return [{
  json: {
    status: 'success',
    message: 'Radiology report generated successfully',
    patient_name: reportData.patient_name,
    scan_type: reportData.scan_type,
    body_part: reportData.body_part,
    report_generated: reportData.report_generated,
    pdf_url: pdfData.download_url,
    findings_summary: reportData.findings.substring(0, 200) + '...',
    next_steps: reportData.next_steps,
    disclaimer: reportData.confidence_note
  }
}];
