// Advanced Professional LinkedIn Trending Topics Report Generator
// Enhanced with Outlook compatibility for consistent email rendering

const items = $input.all();
const currentDate = new Date().toLocaleDateString('en-US', {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

const currentTime = new Date().toLocaleTimeString('en-US', {
  hour: '2-digit',
  minute: '2-digit',
  timeZoneName: 'short'
});

// Extract trending topics from input with enhanced error handling
let trendingTopics = [];
try {
  if (items[0]?.json?.text) {
    const jsonMatch = items[0].json.text.match(/```json(.+?)```/s);
    if (jsonMatch && jsonMatch[1]) {
      trendingTopics = JSON.parse(jsonMatch[1].trim());
    }
  } else if (items[0]?.json && Array.isArray(items[0].json)) {
    trendingTopics = items[0].json;
  } else if (Array.isArray(items)) {
    trendingTopics = items.map(item => item.json);
  }
} catch (error) {
  console.error('Error parsing trending topics:', error);
  trendingTopics = [];
}

// Generate hashtags function
const generateHashtags = (title, description) => {
  const text = (title + ' ' + description).toLowerCase();
  const stopWords = ['the', 'a', 'an', 'and', 'or', 'but', 'in', 'on', 'at', 'to', 'for', 'of', 'with', 'by'];

  const industryHashtags = {
    'ai|artificial intelligence|machine learning|ml': ['#AI', '#MachineLearning', '#ArtificialIntelligence', '#TechInnovation'],
    'remote work|wfh|work from home|hybrid': ['#RemoteWork', '#WFH', '#HybridWork', '#DigitalNomad', '#FlexibleWork'],
    'leadership|management|executive': ['#Leadership', '#Management', '#ExecutiveInsights', '#LeadershipDevelopment'],
    'data|analytics|big data|data science': ['#Data', '#DataScience', '#Analytics', '#BigData', '#DataDriven'],
    'marketing|digital marketing|brand': ['#Marketing', '#DigitalMarketing', '#Branding', '#ContentMarketing'],
    'startup|entrepreneur|innovation': ['#Startup', '#Entrepreneur', '#Innovation', '#Entrepreneurship'],
    'mental health|wellness|wellbeing': ['#MentalHealth', '#Wellness', '#WorkLifeBalance', '#EmployeeWellbeing'],
    'diversity|inclusion|dei': ['#Diversity', '#Inclusion', '#DEI', '#WorkplaceCulture'],
    'finance|fintech|investment': ['#Finance', '#FinTech', '#Investment', '#FinancialTech'],
    'sales|revenue|growth': ['#Sales', '#SalesStrategy', '#RevenueGrowth', '#BusinessDevelopment'],
    'customer|client|service': ['#CustomerExperience', '#CustomerSuccess', '#ClientService'],
    'technology|tech|digital transformation': ['#Technology', '#Tech', '#DigitalTransformation', '#TechTrends'],
    'team|collaboration|workplace': ['#Teamwork', '#Collaboration', '#WorkplaceCulture', '#TeamBuilding'],
    'skill|training|learning|development': ['#SkillDevelopment', '#Learning', '#ProfessionalDevelopment', '#Training'],
    'productivity|efficiency|automation': ['#Productivity', '#Efficiency', '#Automation', '#WorkSmart']
  };

  let hashtags = new Set(['#LinkedIn', '#Professional']);

  Object.entries(industryHashtags).forEach(([pattern, tags]) => {
    const regex = new RegExp(pattern, 'i');
    if (regex.test(text)) {
      tags.forEach(tag => hashtags.add(tag));
    }
  });

  const titleWords = title.split(/[\s\-_]+/)
    .filter(word => word.length > 3 && !stopWords.includes(word.toLowerCase()))
    .slice(0, 3);

  titleWords.forEach(word => {
    const cleanWord = word.replace(/[^\w]/g, '');
    if (cleanWord.length > 2) {
      hashtags.add('#' + cleanWord.charAt(0).toUpperCase() + cleanWord.slice(1).toLowerCase());
    }
  });

  return Array.from(hashtags).slice(0, 10).join(' ');
};

// Calculate engagement potential score
const calculateEngagementScore = (topic) => {
  const text = (topic.title + ' ' + topic.description).toLowerCase();
  let score = 50; // Base score

  const highEngagementWords = ['ai', 'future', 'trend', 'innovation', 'success', 'growth', 'leadership', 'strategy'];
  highEngagementWords.forEach(word => {
    if (text.includes(word)) score += 10;
  });

  if (topic.description.length > 100 && topic.description.length < 300) score += 15;

  if (topic.title.split(' ').length >= 3 && topic.title.split(' ').length <= 8) score += 10;

  return Math.min(100, score);
};

