// Enhanced email subject with personalization
const emailSubject = `ðŸ“Š ${trendingTopics.length} LinkedIn Trends - Structured Report ${currentDate}`;

// Enhanced summary with engagement metrics
const topicsSummary = trendingTopics.map((topic, index) => 
  `${index + 1}. ${topic.title} (${calculateEngagementScore(topic)}% engagement)`
).join('\n');

// Return comprehensive data package
return [{
  json: {
    email_html: htmlReport,
    email_subject: emailSubject,
    topics_summary: topicsSummary,
    topics_count: trendingTopics.length,
    generation_date: new Date().toISOString(),
    average_engagement_score: Math.round(trendingTopics.reduce((acc, topic) => acc + calculateEngagementScore(topic), 0) / trendingTopics.length),
    total_hashtags: trendingTopics.length * 10,
    trending_topics: trendingTopics.map((topic, index) => ({
      ...topic,
      engagement_score: calculateEngagementScore(topic),
      rank: index + 1,
      hashtags: generateHashtags(topic.title, topic.description),
      content_suggestion: topic.description.length > 200 ? topic.description.substring(0, 200) + '...' : topic.description
    }))
  }
}];
