parameters.jsonBody:
={
  "type": "now",
  "shortLink": false,
  "date": "{{ $now.toISO() }}",
  "tags": [],
  "posts": [
    {
      "integration": { "id": "{{ YOUR_POSTIZ_INTEGRATION_ID }}" },
      "value": [
        {
          "content": "{{ $('GPT-5 Caption Agent').item.json.message.content }}",
          "image": [
            {
              "id": "{{ $node['Upload to Postiz'].json.id }}",
              "path": "{{ $node['Upload to Postiz'].json.path }}.mp4"
            }
          ]
        }
      ],
      "settings": {
        "post_type": "post"
      }
    }
  ]
}
