// Get structured data from output
const data = $input.first().json.output;

// Extract each field directly from JSON object
const customer = data.customer_name;
const orderType = data.order_type;
const refNumber = data.reference_number;
const paymentMethod = data.payment_method;
const address = data.address;
const latitude = data.latitude;
const longitude = data.longitude;
const shippingCost = data.shipping_cost;
const finalTotal = data.final_total;

// Process product list
const orderText = data.products.map(product => {
  return `${product.name} ‚Äì Bs ${product.price}`;
}).join('\n');

// Create final message with desired format
const order_message = `üçù *NEW ORDER -[RESTAURANT_NAME]* üáÆüáπ
