parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "customer_name": {
      "type": "string",
      "description": "Customer full name"
    },
    "order_type": {
      "type": "string",
      "description": "Delivery or Pickup",
      "enum": ["Delivery", "Pickup"]
    },
    "products": {
      "type": "array",
      "description": "Order product list",
      "items": {
        "type": "object",
        "properties": {
          "name": {
            "type": "string",
            "description": "Product name"
          },
          "price": {
            "type": "number",
            "description": "Product price in Bs"
          }
        },
        "required": ["name", "price"]
      }
    },
    "shipping_cost": {
      "type": "number",
      "description": "Shipping cost in Bs"
    },
    "address": {
      "type": "string",
      "description": "Delivery address"
    },
    "latitude": {
      "type": "number",
      "description": "Location latitude"
    },
    "longitude": {
      "type": "number",
      "description": "Location longitude"
    },
    "final_total": {
      "type": "number",
      "description": "Order final total in Bs"
    },
    "payment_method": {
      "type": "string",
      "description": "Payment method",
      "enum": ["Cash", "Transfer", "Card","QR"]
    },
    "reference_number": {
      "type": "string",
      "description": "Order reference number"
    }
  },
  "required": [
    "customer_name",
    "order_type",
    "products",
    "shipping_cost",
    "address",
    "latitude",
    "longitude",
    "final_total",
    "payment_method",
    "reference_number"
  ]
}

