// Convert file to base64 for Vision API
const base64 = item.binary.data.data; // 'data' is the binary name
return {
  json: {
    base64
  }
};

function cleanNumber(n) {
  return parseInt(n.toString().replace(/[.,]/g, '')) || 0;
}

// Get output from $json
const output = $json.output || {};

// If there are Items, process them. If not, skip.
const items = Array.isArray(output.Items)
  ? output.Items.map(item => ({
      ...item,
      Quantity: cleanNumber(item.Quantity),
      "Unit Price": cleanNumber(item["Unit Price"]),
      Total: cleanNumber(item.Total),
    }))
  : undefined;

// Optional: clean Total Amount as well
const totalAmount = output["Total Amount"]
  ? cleanNumber(output["Total Amount"])
  : 0;

return {
  json: {
    output: {
      ...output,
      ...(items && { Items: items }),
      "Total Amount": totalAmount,
    }
  }
};
