// n8n Code node (JavaScript)
// Mode: Run once for all items

const inputItems = $input.all();

const toSeconds = (v) => {
  if (v == null) return null;
  if (typeof v === 'number') return v;
  if (typeof v === 'string' && v.endsWith('s')) return parseFloat(v.slice(0, -1));
  const n = Number(v);
  return Number.isFinite(n) ? n : null;
};

const nowUtc = () => new Date().toISOString();

const rows = [];

for (const item of inputItems) {
  const j = item.json || {};

  // Metadata from the Set node ‚Äî keeping the names exactly as specified
  const meta = {
    source: j.source ?? null, // important: do not rename, as this field is needed by the next node
    campaign_id: j.campaign_id ?? null,
    ad_id: j.ad_id ?? null,
    creative_id: j.creative_id ?? null,
    video_id: j.video_id ?? null,
    file_name: j.file_name ?? null,
    image_url: j.image_url ?? null,
  };

  // If file_name was not set in the Set node ‚Äî try to extract it from the VI response
  const resolvedFileName =
    meta.file_name ??
    (j.response?.annotationResults?.[0]?.inputUri
      ? j.response.annotationResults[0].inputUri.split('/').pop()
      : null) ??
    j.name ??
    'unknown_file';

  const results = j.response?.annotationResults ?? [];
  for (const r of results) {
    // 1) Segment Labels
    for (const ann of (r.segmentLabelAnnotations ?? [])) {
      const cat = (ann.categoryEntities ?? [])
        .map(c => c?.description)
        .filter(Boolean)
        .join('|') || null;

      for (const s of (ann.segments ?? [])) {
        rows.push({
          ...meta,
          file_name: resolvedFileName,
          annotation_type: 'SegmentLabel',
          label_or_text: ann.entity?.description ?? null,
          full_transcript: null,
          category: cat,
          confidence: s.confidence ?? null,
          start_time_s: toSeconds(s.segment?.startTimeOffset),
          end_time_s: toSeconds(s.segment?.endTimeOffset),
          language_code: null,
          processed_at_utc: nowUtc(),
        });
      }
    }

    // 2) Shot Labels
    for (const ann of (r.shotLabelAnnotations ?? [])) {
      const cat = (ann.categoryEntities ?? [])
        .map(c => c?.description)
        .filter(Boolean)
        .join('|') || null;

      for (const s of (ann.segments ?? [])) {
        rows.push({
          ...meta,
          file_name: resolvedFileName,
          annotation_type: 'ShotLabel',
          label_or_text: ann.entity?.description ?? null,
          full_transcript: null,
          category: cat,
          confidence: s.confidence ?? null,
          start_time_s: toSeconds(s.segment?.startTimeOffset),
          end_time_s: toSeconds(s.segment?.endTimeOffset),
          language_code: null,
          processed_at_utc: nowUtc(),
        });
      }
    }

    // 3) Text Detection
    for (const t of (r.textAnnotations ?? [])) {
      for (const s of (t.segments ?? [])) {
        rows.push({
          ...meta,
          file_name: resolvedFileName,
          annotation_type: 'TextDetection',
          label_or_text: t.text ?? null,
          full_transcript: null,
          category: null,
          confidence: s.confidence ?? null,
          start_time_s: toSeconds(s.segment?.startTimeOffset),
          end_time_s: toSeconds(s.segment?.endTimeOffset),
          language_code: null,
          processed_at_utc: nowUtc(),
        });
      }
    }

    // 4) Speech Transcription (if enabled in VI)
    for (const st of (r.speechTranscriptions ?? [])) {
      for (const alt of (st.alternatives ?? [])) {
        const words = alt.words ?? [];
        const start = words[0]?.startTime ?? null;
        const end = words.length ? words[words.length - 1]?.endTime : null;

        rows.push({
          ...meta,
          file_name: resolvedFileName,
          annotation_type: 'SpeechTranscription',
          label_or_text: alt.transcript ?? null,
          full_transcript: alt.transcript ?? null,
          category: null,
          confidence: alt.confidence ?? null,
          start_time_s: toSeconds(start),
          end_time_s: toSeconds(end),
          language_code: st.languageCode ?? null,
          processed_at_utc: nowUtc(),
        });
      }
    }
  }
}

// Return a batch of items
return rows.map(r => ({ json: r }));

// n8n Code node (JavaScript)
// Mode: Run once for all items

const items = $input.all();
const nowUtc = () => new Date().toISOString();

const serializeBBox = (verts=[]) =>
  verts.map(v => `${(v.x ?? 0).toFixed(4)},${(v.y ?? 0).toFixed(4)}`).join('|');

const rows = [];

for (const it of items) {
  const j = it.json || {};

  // Metadata from the 'Set id data' node (trying to get image_url from several places)
  const resolvedImageUrl =
    j.image_url
    ?? j.data?.creative?.image_url
    ?? j.data?.creative?.thumbnail_url
    ?? null;

  const meta = {
    source: j.source ?? null,
    campaign_id: j.campaign_id ?? null,
    ad_id: j.ad_id ?? null,
    creative_id: j.creative_id ?? null,
    video_id: j.video_id ?? null,
    file_name: j.file_name ?? null,
    image_url: resolvedImageUrl,
  };

  // Cloud Vision response (supporting different wrappers)
  const resp = j.responses?.[0] || j.response?.responses?.[0] || j.response || {};

  // 1) LABELS
  for (const a of (resp.labelAnnotations ?? [])) {
    rows.push({
      ...meta,
      annotation_type: 'Label',
      label_or_text: a.description ?? null,
      full_transcript: null,
      category: null,
      confidence: a.score ?? null,
      start_time_s: null,
      end_time_s: null,
      language_code: null,
      processed_at_utc: nowUtc(),
    });
  }

  // 2) LOGOS
  for (const a of (resp.logoAnnotations ?? [])) {
    rows.push({
      ...meta,
      annotation_type: 'Logo',
      label_or_text: a.description ?? null,
      full_transcript: null,
      category: 'logo',
      confidence: a.score ?? null,
      start_time_s: null,
      end_time_s: null,
      language_code: null,
      processed_at_utc: nowUtc(),
    });
  }

  // 3) LANDMARKS
  for (const a of (resp.landmarkAnnotations ?? [])) {
    rows.push({
      ...meta,
      annotation_type: 'Landmark',
      label_or_text: a.description ?? null,
      full_transcript: null,
      category: null,
      confidence: a.score ?? null,
      start_time_s: null,
      end_time_s: null,
      language_code: null,
      processed_at_utc: nowUtc(),
    });
  }

  // 4) OBJECTS (bbox)
  for (const a of (resp.localizedObjectAnnotations ?? [])) {
    const bbox = serializeBBox(a.boundingPoly?.normalizedVertices ?? []);
    rows.push({
      ...meta,
      annotation_type: 'Object',
      label_or_text: a.name ?? null,
      full_transcript: null,
      category: bbox ? `bbox:${bbox}` : null,
      confidence: a.score ?? null,
      start_time_s: null,
      end_time_s: null,
      language_code: null,
      processed_at_utc: nowUtc(),
    });
  }

  // 5) WEB DETECTION
  const web = resp.webDetection || {};
  for (const e of (web.webEntities ?? [])) {
    if (!e.description) continue;
    rows.push({
      ...meta,
      annotation_type: 'WebEntity',
      label_or_text: e.description,
      full_transcript: null,
      category: null,
      confidence: e.score ?? null,
      start_time_s: null,
      end_time_s: null,
      language_code: null,
      processed_at_utc: nowUtc(),
    });
  }
  for (const b of (web.bestGuessLabels ?? [])) {
    rows.push({
      ...meta,
      annotation_type: 'BestGuess',
      label_or_text: b.label ?? null,
      full_transcript: null,
      category: null,
      confidence: null,
      start_time_s: null,
      end_time_s: null,
      language_code: null,
      processed_at_utc: nowUtc(),
    });
  }

  // 6) SAFE SEARCH
  if (resp.safeSearchAnnotation) {
    const s = resp.safeSearchAnnotation;
    for (const k of ['adult','medical','violence','spoof','racy']) {
      rows.push({
        ...meta,
        annotation_type: 'SafeSearch',
        label_or_text: `${k}:${s[k] ?? 'UNKNOWN'}`,
        full_transcript: null,
        category: null,
        confidence: null,
        start_time_s: null,
        end_time_s: null,
        language_code: null,
        processed_at_utc: nowUtc(),
      });
    }
  }

  // 7) TEXT
  const fta = resp.fullTextAnnotation;
  if (fta?.text) {
    rows.push({
      ...meta,
      annotation_type: 'Text',
      label_or_text: fta.text.slice(0, 1000),
      full_transcript: fta.text,
      category: 'fullText',
      confidence: null,
      start_time_s: null,
      end_time_s: null,
      language_code: null,
      processed_at_utc: nowUtc(),
    });
  } else {
    for (const t of (resp.textAnnotations ?? []).slice(0, 50)) {
      if (!t.description) continue;
      rows.push({
        ...meta,
        annotation_type: 'Text',
        label_or_text: t.description,
        full_transcript: null,
        category: 'snippet',
        confidence: null,
        start_time_s: null,
        end_time_s: null,
        language_code: null,
        processed_at_utc: nowUtc(),
      });
    }
  }
}

return rows.map(r => ({ json: r }));
