// Process different data sources from Philips IntelliVue
const inputData = $input.all();
let processedVitals = [];

// Process each input source
for (const input of inputData) {
  const data = input.json;
  let vitals = null;

  // Detect data source and process accordingly
  if (data.MSH && data.MSH.includes('HL7')) {
    // HL7 Message Processing
    vitals = processHL7Message(data);
  } else if (data.filename && data.filename.endsWith('.csv')) {
    // CSV Export File Processing
    vitals = processCSVExport(data);
  } else if (data.patients || data.deviceId) {
    // Gateway API Response Processing
    vitals = processGatewayResponse(data);
  }

  if (vitals) {
    processedVitals.push(vitals);
  }
}

// HL7 Message Parser
function processHL7Message(hl7Data) {
  try {
    const segments = hl7Data.message.split('\r');
    let patientInfo = {};
    let vitalSigns = {};

    segments.forEach(segment => {
      const fields = segment.split('|');

      // Patient Identification (PID segment)
      if (segment.startsWith('PID')) {
        patientInfo = {
          patient_id: fields[3] || 'UNKNOWN',
          patient_name: fields[5] || 'Unknown Patient',
          room_number: fields[11] || 'N/A'
        };
      }

      // Observation Results (OBX segment for vitals)
      if (segment.startsWith('OBX')) {
        const valueType = fields[2];
        const observationId = fields[3];
        const value = parseFloat(fields[5]) || 0;
        const unit = fields[6] || '';

        // Map Philips observation codes to vital signs
        switch(observationId) {
          case 'MDC_ECG_HEART_RATE':
            vitalSigns.heart_rate = value;
            break;
          case 'MDC_PULS_OXIM_SAT_O2':
            vitalSigns.spo2 = value;
            break;
          case 'MDC_PRESS_BLD_NONINV_SYS':
            vitalSigns.bp_systolic = value;
            break;
          case 'MDC_PRESS_BLD_NONINV_DIA':
            vitalSigns.bp_diastolic = value;
            break;
          case 'MDC_TEMP_BODY':
            vitalSigns.temperature = value;
            break;
          case 'MDC_RESP_RATE':
            vitalSigns.respiration_rate = value;
            break;
          case 'MDC_CO2_AWAY':
            vitalSigns.etco2 = value;
            break;
        }
      }
    });

    return {
      ...patientInfo,
      ...vitalSigns,
      data_source: 'HL7',
      timestamp: new Date().toISOString(),
      device_type: 'Philips_IntelliVue'
    };
  } catch (error) {
    console.error('HL7 parsing error:', error);
    return null;
  }
}

// CSV Export File Parser
function processCSVExport(csvData) {
  try {
    const lines = csvData.data.split('\n');
    const headers = lines[0].split(',');
    const latestRecord = lines[lines.length - 2].split(','); // Skip empty last line

    const vitals = {};
    headers.forEach((header, index) => {
      const cleanHeader = header.trim().replace(/"/g, '');
      const value = latestRecord[index] ? latestRecord[index].trim().replace(/"/g, '') : '';

      // Map CSV columns to standard format
      switch(cleanHeader.toLowerCase()) {
        case 'patient_id':
        case 'patientid':
          vitals.patient_id = value;
          break;
        case 'heart_rate':
        case 'hr':
        case 'ecg':
          vitals.heart_rate = parseFloat(value) || 0;
          break;
        case 'spo2':
        case 'oxygen_saturation':
          vitals.spo2 = parseFloat(value) || 0;
          break;
        case 'bp_sys':
        case 'systolic':
          vitals.bp_systolic = parseFloat(value) || 0;
          break;
        case 'bp_dia':
        case 'diastolic':
          vitals.bp_diastolic = parseFloat(value) || 0;
          break;
        case 'temp':
        case 'temperature':
          vitals.temperature = parseFloat(value) || 0;
          break;
        case 'resp_rate':
        case 'rr':
          vitals.respiration_rate = parseFloat(value) || 0;
          break;
        case 'etco2':
        case 'co2':
          vitals.etco2 = parseFloat(value) || 0;
          break;
        case 'room':
        case 'location':
          vitals.room_number = value;
          break;
      }
    });

    return {
      ...vitals,
      patient_name: vitals.patient_id || 'Unknown',
      data_source: 'CSV_Export',
      timestamp: new Date().toISOString(),
      device_type: 'Philips_IntelliVue'
    };
  } catch (error) {
    console.error('CSV parsing error:', error);
    return null;
  }
}

// Gateway API Response Parser
function processGatewayResponse(apiData) {
  try {
    // Handle different API response formats
    const patients = apiData.patients || [apiData];
    const patient = patients[0] || apiData;

    return {
      patient_id: patient.patientId || patient.id || 'UNKNOWN',
      patient_name: patient.name || patient.patientName || 'Unknown',
      room_number: patient.room || patient.location || 'N/A',
      heart_rate: patient.heartRate || patient.hr || 0,
      spo2: patient.spo2 || patient.oxygenSaturation || 0,
      bp_systolic: patient.systolicBP || patient.bpSys || 0,
      bp_diastolic: patient.diastolicBP || patient.bpDia || 0,
      temperature: patient.temperature || patient.temp || 0,
      respiration_rate: patient.respirationRate || patient.rr || 0,
      etco2: patient.etco2 || patient.co2 || 0,
      data_source: 'Gateway_API',
      timestamp: patient.timestamp || new Date().toISOString(),
      device_type: 'Philips_IntelliVue',
      device_id: patient.deviceId || apiData.deviceId || 'UNKNOWN'
    };
  } catch (error) {
    console.error('Gateway API parsing error:', error);
    return null;
  }
}

// Return all processed vitals
return processedVitals.filter(v => v !== null).map(vital => ({ json: vital }));

// Validate and enrich vital signs data
const vitals = $json;

// Data validation
if (!vitals.patient_id || vitals.patient_id === 'UNKNOWN') {
  console.warn('Missing or invalid patient ID');
  return null;
}

// Calculate derived values
if (vitals.bp_systolic && vitals.bp_diastolic) {
  vitals.pulse_pressure = vitals.bp_systolic - vitals.bp_diastolic;
  vitals.mean_arterial_pressure = Math.round(vitals.bp_diastolic + (vitals.pulse_pressure / 3));
}

// Convert temperature if needed (assume Celsius input)
if (vitals.temperature) {
  vitals.temperature_celsius = vitals.temperature;
  vitals.temperature_fahrenheit = Math.round((vitals.temperature * 9/5 + 32) * 10) / 10;
}

// Determine clinical status
vitals.clinical_status = determineClinicalStatus(vitals);
vitals.alert_level = determineAlertLevel(vitals);
vitals.clinical_alerts = generateClinicalAlerts(vitals);

// Add metadata
vitals.processed_at = new Date().toISOString();
vitals.system_status = 'PROCESSED';

function determineClinicalStatus(v) {
  const abnormalConditions = [
    v.heart_rate && (v.heart_rate < 60 || v.heart_rate > 100),
    v.spo2 && v.spo2 < 95,
    v.bp_systolic && (v.bp_systolic > 140 || v.bp_systolic < 90),
    v.temperature && (v.temperature > 38.0 || v.temperature < 36.0),
    v.respiration_rate && (v.respiration_rate < 12 || v.respiration_rate > 20)
  ];

  return abnormalConditions.some(condition => condition) ? 'ABNORMAL' : 'NORMAL';
}

function determineAlertLevel(v) {
  // Critical conditions
  const critical = [
    v.heart_rate && (v.heart_rate < 50 || v.heart_rate > 120),
    v.spo2 && v.spo2 < 90,
    v.bp_systolic && (v.bp_systolic > 180 || v.bp_systolic < 80),
    v.temperature && (v.temperature > 39.0 || v.temperature < 35.0),
    v.respiration_rate && (v.respiration_rate < 8 || v.respiration_rate > 30)
  ];

  if (critical.some(c => c)) return 'CRITICAL';
  if (v.clinical_status === 'ABNORMAL') return 'WARNING';
  return 'NORMAL';
}

function generateClinicalAlerts(v) {
  const alerts = [];

  if (v.heart_rate) {
    if (v.heart_rate < 50) alerts.push('Severe Bradycardia');
    else if (v.heart_rate > 120) alerts.push('Severe Tachycardia');
    else if (v.heart_rate < 60) alerts.push('Bradycardia');
    else if (v.heart_rate > 100) alerts.push('Tachycardia');
  }

  if (v.spo2) {
    if (v.spo2 < 90) alerts.push('Severe Hypoxemia');
    else if (v.spo2 < 95) alerts.push('Hypoxemia');
  }

  if (v.bp_systolic) {
    if (v.bp_systolic > 180) alerts.push('Hypertensive Crisis');
    else if (v.bp_systolic < 80) alerts.push('Severe Hypotension');
    else if (v.bp_systolic > 140) alerts.push('Hypertension');
    else if (v.bp_systolic < 90) alerts.push('Hypotension');
  }

  if (v.temperature) {
    if (v.temperature > 39.0) alerts.push('High Fever');
    else if (v.temperature < 35.0) alerts.push('Hypothermia');
    else if (v.temperature > 38.0) alerts.push('Fever');
  }

  return alerts;
}

return { json: vitals };

