// N8N JavaScript Code Tool for Property Search - Query Builder
// This code builds the SQL query based on form criteria

// Get form data from the previous node
const formData = $input.all()[0].json;

console.log('Received form data:', JSON.stringify(formData, null, 2));

// Initialize query components
let selectClause = `SELECT TOP (1000) [brokered_by]
    ,[status]
    ,[price]
    ,[bed]
    ,[bath]
    ,[acre_lot]
    ,[street]
    ,[city]
    ,[state]
    ,[zip_code]
    ,[house_size]
    ,[prev_sold_date]`;

let fromClause = 'FROM [REALTOR].[dbo].[realtor_usa_price]';
let whereConditions = [];

// Helper function to add numeric range conditions
function addNumericRangeCondition(fieldName, minValue, maxValue, paramPrefix) {
    const conditions = [];

    if (minValue !== undefined && minValue !== null && minValue !== '' && minValue !== 0) {
        conditions.push(`[${fieldName}] >= ${minValue}`);
    }

    if (maxValue !== undefined && maxValue !== null && maxValue !== '' && maxValue !== 0) {
        conditions.push(`[${fieldName}] <= ${maxValue}`);
    }

    return conditions;
}

// Helper function to add string conditions
function addStringCondition(fieldName, value) {
    if (value && value.trim() !== '') {
        whereConditions.push(`[${fieldName}] = '${value.trim()}'`);
    }
}

// Process form parameters
try {
    // Price range
    const priceConditions = addNumericRangeCondition(
        'price', 
        formData['Minimum Price ($)'], 
        formData['Maximum Price ($)'], 
        'price'
    );
    whereConditions.push(...priceConditions);

    // Bedroom range
    const bedConditions = addNumericRangeCondition(
        'bed', 
        formData['Minimum Bedrooms'], 
        formData['Maximum Bedrooms'], 
        'bed'
    );
    whereConditions.push(...bedConditions);

    // Bathroom range
    const bathConditions = addNumericRangeCondition(
        'bath', 
        formData['Minimum Bathrooms'], 
        formData['Maximum Bathrooms'], 
        'bath'
    );
    whereConditions.push(...bathConditions);

    // House size range (if provided)
    const houseSizeConditions = addNumericRangeCondition(
        'house_size', 
        formData['Minimum House Size (sqft)'], 
        formData['Maximum House Size (sqft)'], 
        'house_size'
    );
    whereConditions.push(...houseSizeConditions);

    // Lot size range (if provided)
    const lotSizeConditions = addNumericRangeCondition(
        'acre_lot', 
        formData['Minimum Lot Size (acres)'], 
        formData['Maximum Lot Size (acres)'], 
        'lot_size'
    );
    whereConditions.push(...lotSizeConditions);

    // String filters
    addStringCondition('state', formData['State']);
    addStringCondition('city', formData['City']);

    // ZIP Code (if provided)
    if (formData['ZIP Code'] && formData['ZIP Code'] !== '') {
        whereConditions.push(`[zip_code] = ${parseInt(formData['ZIP Code'])}`);
    }

    // Property Status (if provided and not empty)
    addStringCondition('status', formData['Property Status']);

} catch (error) {
    console.error('Error processing form data:', error);
    return [{
        json: {
            error: 'Failed to process form parameters',
            details: error.message,
            receivedData: formData
        }
    }];
}

// Build the complete query
let finalQuery = selectClause + '\n' + fromClause;

if (whereConditions.length > 0) {
    finalQuery += '\nWHERE ' + whereConditions.join('\n  AND ');
}

// Add ordering
finalQuery += '\nORDER BY [price] ASC';

console.log('Generated SQL Query:', finalQuery);

// Return the query for the SQL Server node to execute
return [{
    json: {
        query: finalQuery,
        searchCriteria: {
            priceRange: {
                min: formData['Minimum Price ($)'] || null,
                max: formData['Maximum Price ($)'] || null
            },
            bedrooms: {
                min: formData['Minimum Bedrooms'] || null,
                max: formData['Maximum Bedrooms'] || null
            },
            bathrooms: {
                min: formData['Minimum Bathrooms'] || null,
                max: formData['Maximum Bathrooms'] || null
            },
            houseSizeRange: {
                min: formData['Minimum House Size (sqft)'] || null,
                max: formData['Maximum House Size (sqft)'] || null
            },
            lotSizeRange: {
                min: formData['Minimum Lot Size (acres)'] || null,
                max: formData['Maximum Lot Size (acres)'] || null
            },
            location: {
                state: formData['State'] || null,
                city: formData['City'] || null,
                zipCode: formData['ZIP Code'] || null
            },
            status: formData['Property Status'] || null
        },
        formData: formData,
        totalConditions: whereConditions.length,
        userEmail: formData['Your Email Address'],
        searchTimestamp: new Date().toISOString()
    }
}];
