// Deduplicate papers based on DOI and title similarity
const papers = $json;
const unique = new Map();

papers.forEach(paper => {
  // First check DOI
  if (paper.doi && !unique.has(paper.doi)) {
    unique.set(paper.doi, paper);
  } else if (!paper.doi) {
    // For papers without DOI, check title similarity
    const normalizedTitle = paper.title.toLowerCase().replace(/[^a-z0-9]/g, '');
    let isDuplicate = false;

    for (const [key, existingPaper] of unique) {
      const existingTitle = existingPaper.title.toLowerCase().replace(/[^a-z0-9]/g, '');
      if (normalizedTitle === existingTitle) {
        isDuplicate = true;
        // Merge provider info
        if (!existingPaper.providers) existingPaper.providers = [existingPaper.provider];
        existingPaper.providers.push(paper.provider);
        break;
      }
    }

    if (!isDuplicate) {
      unique.set(normalizedTitle, paper);
    }
  }
});

return Array.from(unique.values());

// Calculate relevance score
const papers = $json;
const query = $node['Set Search Parameters'].json.searchQuery.toLowerCase();

const scored = papers.map(paper => {
  let score = 0;

  // Title relevance
  const titleWords = paper.title.toLowerCase().split(' ');
  const queryWords = query.split(' ');
  queryWords.forEach(word => {
    if (titleWords.includes(word)) score += 10;
  });

  // Citation impact
  score += Math.log(paper.totalCitations + 1) * 5;

  // Recency bonus
  const yearDiff = new Date().getFullYear() - paper.year;
  score += Math.max(0, 10 - yearDiff);

  // Full text availability
  if (paper.pdfUrl) score += 15;

  return { ...paper, relevanceScore: score };
});

// Sort by relevance
return scored.sort((a, b) => b.relevanceScore - a.relevanceScore);

// Generate BibTeX entries
const papers = $json;

const bibtex = papers.map((paper, index) => {
  const key = paper.doi ? paper.doi.replace(/[^a-zA-Z0-9]/g, '') : `paper${index}`;
  const authors = paper.authors.join(' and ');

  return `@article{${key},
  title={${paper.title}},
  author={${authors}},
  year={${paper.year}},
  doi={${paper.doi || ''}},
  abstract={${paper.abstract || ''}}
}`;
}).join('\n\n');

return { bibtex, papers };
