parameters.jsCode:
const prospect = $('Get prospect').item.json;

const website_rag_node = $('Get RAG domain');
const website_rag = website_rag_node?.item?.json?.error ? null : website_rag_node?.item?.json;

const search_rag_node = $('Get rag search');
const search_rag = search_rag_node?.item?.json?.error ? null : search_rag_node?.item?.json;

const basic_information = {
    name: website_rag?.home_company || prospect.name,
    city: prospect.city,
    country_code: prospect.country_code,
    domain: prospect.domain,
    sbi: prospect.sbi,
    ...(!!prospect.google_industry && { google_industry: prospect.google_industry })
};

const website_content = !website_rag ? null : {
  keywords: website_rag.texts.profiles_keywords,
  descriptions: website_rag.texts.profiles_descriptions,
  about: website_rag.texts.about_metaarticle_sum,
  website: website_rag.texts.all_metaarticle_sum
}

const search_content = !search_rag ? null : {
  ...(!!search_rag.results.length && {
    results: search_rag.results.filter(result => !!result.snippets && !!result.description).map((result) => {
      return {
        url: result.url,
        description: result.description,
        snippets: result.snippets
      }
    })
  })
}

return {
    basic_information,
    ...(!!website_content && { website_content }),
  ...(!!search_content && { search_content })
};
