// Determine customer timezone and check calling hours
const orderData = $input.first().json;

// Extract timezone from shipping address or phone number
function getTimezoneFromAddress(address) {
  const timezoneMap = {
    // US States
    'AL': 'America/Chicago', 'AK': 'America/Anchorage', 'AZ': 'America/Phoenix',
    'AR': 'America/Chicago', 'CA': 'America/Los_Angeles', 'CO': 'America/Denver',
    'CT': 'America/New_York', 'DE': 'America/New_York', 'FL': 'America/New_York',
    'GA': 'America/New_York', 'HI': 'Pacific/Honolulu', 'ID': 'America/Denver',
    'IL': 'America/Chicago', 'IN': 'America/New_York', 'IA': 'America/Chicago',
    'KS': 'America/Chicago', 'KY': 'America/New_York', 'LA': 'America/Chicago',
    'ME': 'America/New_York', 'MD': 'America/New_York', 'MA': 'America/New_York',
    'MI': 'America/New_York', 'MN': 'America/Chicago', 'MS': 'America/Chicago',
    'MO': 'America/Chicago', 'MT': 'America/Denver', 'NE': 'America/Chicago',
    'NV': 'America/Los_Angeles', 'NH': 'America/New_York', 'NJ': 'America/New_York',
    'NM': 'America/Denver', 'NY': 'America/New_York', 'NC': 'America/New_York',
    'ND': 'America/Chicago', 'OH': 'America/New_York', 'OK': 'America/Chicago',
    'OR': 'America/Los_Angeles', 'PA': 'America/New_York', 'RI': 'America/New_York',
    'SC': 'America/New_York', 'SD': 'America/Chicago', 'TN': 'America/Chicago',
    'TX': 'America/Chicago', 'UT': 'America/Denver', 'VT': 'America/New_York',
    'VA': 'America/New_York', 'WA': 'America/Los_Angeles', 'WV': 'America/New_York',
    'WI': 'America/Chicago', 'WY': 'America/Denver',
    // Countries
    'UK': 'Europe/London', 'GB': 'Europe/London', 'CANADA': 'America/Toronto',
    'AUSTRALIA': 'Australia/Sydney', 'GERMANY': 'Europe/Berlin',
    'FRANCE': 'Europe/Paris', 'INDIA': 'Asia/Kolkata', 'JAPAN': 'Asia/Tokyo'
  };

  const address_upper = address.toUpperCase();
  for (const [key, timezone] of Object.entries(timezoneMap)) {
    if (address_upper.includes(key)) {
      return timezone;
    }
  }

  // Default to customer's country code from phone number
  const phone = orderData.customer_phone;
  if (phone.startsWith('+1')) return 'America/New_York'; // US/Canada default
  if (phone.startsWith('+44')) return 'Europe/London'; // UK
  if (phone.startsWith('+91')) return 'Asia/Kolkata'; // India
  if (phone.startsWith('+81')) return 'Asia/Tokyo'; // Japan
  if (phone.startsWith('+49')) return 'Europe/Berlin'; // Germany
  if (phone.startsWith('+33')) return 'Europe/Paris'; // France
  if (phone.startsWith('+61')) return 'Australia/Sydney'; // Australia

  return 'America/New_York'; // Default fallback
}

function isWithinCallingHours(timezone) {
  const now = new Date();
  const customerTime = new Date(now.toLocaleString("en-US", {timeZone: timezone}));
  const hour = customerTime.getHours();
  const day = customerTime.getDay(); // 0 = Sunday, 6 = Saturday

  // Check if it's a weekday (Monday-Friday) and between 10 AM - 3 PM
  const isWeekday = day >= 1 && day <= 5;
  const isCallingHours = hour >= 10 && hour < 15; // 10 AM to 3 PM

  return {
    canCall: isWeekday && isCallingHours,
    currentHour: hour,
    currentDay: day,
    isWeekday,
    isCallingHours,
    customerTime: customerTime.toLocaleString(),
    timezone
  };
}

function calculateNextCallTime(timezone) {
  const now = new Date();
  const customerTime = new Date(now.toLocaleString("en-US", {timeZone: timezone}));
  const hour = customerTime.getHours();
  const day = customerTime.getDay();

  let nextCallTime = new Date(customerTime);

  // If it's weekend, schedule for next Monday at 10 AM
  if (day === 0 || day === 6) { // Sunday or Saturday
    const daysUntilMonday = day === 0 ? 1 : 2; // Sunday = 1 day, Saturday = 2 days
    nextCallTime.setDate(nextCallTime.getDate() + daysUntilMonday);
    nextCallTime.setHours(10, 0, 0, 0);
  }
  // If it's before 10 AM on weekday, schedule for 10 AM today
  else if (hour < 10) {
    nextCallTime.setHours(10, 0, 0, 0);
  }
  // If it's after 3 PM on weekday, schedule for 10 AM next day
  else if (hour >= 15) {
    nextCallTime.setDate(nextCallTime.getDate() + 1);
    // If next day is Saturday, schedule for Monday
    if (nextCallTime.getDay() === 6) {
      nextCallTime.setDate(nextCallTime.getDate() + 2);
    }
    nextCallTime.setHours(10, 0, 0, 0);
  }

  // Convert back to UTC for scheduling
  const utcOffset = nextCallTime.getTimezoneOffset() * 60000;
  const utcTime = new Date(nextCallTime.getTime() + utcOffset);
  return utcTime.toISOString();
}

// Determine customer timezone
const customerTimezone = orderData.customer_timezone || 
  getTimezoneFromAddress(orderData.shipping_address || '');

// Check calling hours
const callingStatus = isWithinCallingHours(customerTimezone);

return {
  json: {
    ...orderData,
    customer_timezone: customerTimezone,
    calling_status: callingStatus,
    can_call_now: callingStatus.canCall,
    next_call_time: callingStatus.canCall ? null : calculateNextCallTime(customerTimezone),
    scheduling_info: {
      customer_local_time: callingStatus.customerTime,
      current_hour: callingStatus.currentHour,
      is_weekday: callingStatus.isWeekday,
      is_calling_hours: callingStatus.isCallingHours
    }
  }
};

// Format order data for VAPI call with timezone awareness
const orderData = $input.first().json;

// Create order summary
const items = orderData.items.map(item => 
  `${item.quantity} ${item.name} at $${item.price} each`
).join(', ');

const orderSummary = `Order ${orderData.order_id} containing ${items}. Total amount: $${orderData.total}. Shipping to ${orderData.shipping_address}.`;

// Get customer's local time for personalized greeting
const customerLocalTime = orderData.scheduling_info.customer_local_time;
const currentHour = orderData.scheduling_info.current_hour;

let timeGreeting = "";
if (currentHour < 12) {
  timeGreeting = "Good morning";
} else if (currentHour < 17) {
  timeGreeting = "Good afternoon";
} else {
  timeGreeting = "Good evening";
}

// Create VAPI assistant configuration with timezone awareness
const vapiConfig = {
  model: {
    provider: "openai",
    model: "gpt-3.5-turbo",
    temperature: 0.3,
    systemMessage: `You are Alex, a friendly customer service representative from ${orderData.store_name || 'TechMart'}. You are calling to confirm an e-commerce order during appropriate business hours (10 AM - 3 PM customer local time).

Customer Information:
- Name: ${orderData.customer_name}
- Local time: ${customerLocalTime}
- Timezone: ${orderData.customer_timezone}

Order Details:
${orderSummary}

Your goals:
1. Use appropriate time-based greeting (it's currently ${timeGreeting.toLowerCase()} for the customer)
2. Confirm you're speaking with ${orderData.customer_name}
3. Verify the order details are correct
4. Confirm the shipping address
5. Handle any customer concerns professionally
6. Get final confirmation to proceed with shipping

Be natural, friendly, and concise. Acknowledge that you're calling during their business hours. If the customer wants to make changes, collect the details and confirm you'll process the update.

Call Script Flow:
1. "${timeGreeting}, this is Alex from ${orderData.store_name || 'TechMart'}. Am I speaking with ${orderData.customer_name}?"
2. "I hope I'm calling at a convenient time. I'm calling to confirm your recent order placed on ${orderData.order_date}."
3. "Let me go over the details: [read order items and total]"
4. "Does everything sound correct?"
5. "Great! Your order will be shipped to [read address]. Is this address still correct?"
6. "Perfect! Your order is confirmed and will be processed today. You'll receive tracking info via email within 24 hours."`
  },
  voice: {
    provider: "11labs",
    voiceId: "rachel",
    stability: 0.5,
    similarityBoost: 0.5,
    style: 0.2,
    useSpeakerBoost: true
  },
  firstMessage: `${timeGreeting}! This is Alex from ${orderData.store_name || 'TechMart'} calling to confirm your recent order. Am I speaking with ${orderData.customer_name}? I hope I'm calling at a convenient time.`,
  endCallMessage: "Thank you for confirming your order! Have a wonderful rest of your day!",
  recordingEnabled: true,
  endCallPhrases: ["goodbye", "thank you", "that's all", "end call", "have a good day"],
  maxDurationSeconds: 300,
  backgroundSound: "office",
  backchannelingEnabled: true,
  backgroundDenoisingEnabled: true,
  modelOutputInMessagesEnabled: true,
  silenceTimeoutSeconds: 10,
  responseDelaySeconds: 1
};

return {
  json: {
    order_id: orderData.order_id,
    customer_name: orderData.customer_name,
    customer_phone: orderData.customer_phone,
    customer_email: orderData.customer_email,
    customer_timezone: orderData.customer_timezone,
    order_summary: orderSummary,
    vapi_config: vapiConfig,
    call_context: {
      order_total: orderData.total,
      items_count: orderData.items.length,
      shipping_address: orderData.shipping_address,
      order_date: orderData.order_date,
      customer_local_time: customerLocalTime,
      time_greeting: timeGreeting
    }
  }
};

// Process VAPI call results with enhanced analysis
const webhookData = $input.first().json;
const callData = webhookData.message;

// Extract call information
const callId = callData.call?.id;
const callDuration = callData.call?.endedAt ? 
  new Date(callData.call.endedAt) - new Date(callData.call.startedAt) : 0;

// Get transcript and messages
const transcript = callData.transcript || '';
const messages = callData.messages || [];

// Enhanced confirmation detection
let confirmationStatus = 'unknown';
let customerResponse = 'no_response';
let confidence = 0;

const transcriptLower = transcript.toLowerCase();

// Positive confirmation keywords
const positiveKeywords = ['yes', 'correct', 'right', 'confirm', 'good', 'perfect', 'sounds good', 'that\'s right'];
const negativeKeywords = ['no', 'wrong', 'incorrect', 'change', 'different', 'not right', 'cancel'];
const issueKeywords = ['problem', 'issue', 'concern', 'question', 'but', 'however', 'wait'];

// Calculate confidence scores
let positiveScore = 0;
let negativeScore = 0;
let issueScore = 0;

positiveKeywords.forEach(keyword => {
  if (transcriptLower.includes(keyword)) {
    positiveScore += 1;
  }
});

negativeKeywords.forEach(keyword => {
  if (transcriptLower.includes(keyword)) {
    negativeScore += 2; // Weight negative more heavily
  }
});

issueKeywords.forEach(keyword => {
  if (transcriptLower.includes(keyword)) {
    issueScore += 1;
  }
});

// Determine status based on scores and call duration
if (callDuration < 10000) { // Less than 10 seconds
  confirmationStatus = 'no_answer';
  customerResponse = 'no_answer';
  confidence = 0.9;
} else if (negativeScore > 0 || issueScore > 1) {
  confirmationStatus = 'needs_review';
  customerResponse = 'requested_changes';
  confidence = 0.8;
} else if (positiveScore >= 2) {
  confirmationStatus = 'confirmed';
  customerResponse = 'confirmed';
  confidence = 0.9;
} else if (positiveScore >= 1 && negativeScore === 0) {
  confirmationStatus = 'likely_confirmed';
  customerResponse = 'likely_confirmed';
  confidence = 0.7;
} else {
  confirmationStatus = 'unclear';
  customerResponse = 'unclear';
  confidence = 0.3;
}

// Extract specific issues mentioned
const issues = [];
if (transcriptLower.includes('address') || transcriptLower.includes('shipping')) {
  issues.push('shipping_address');
}
if (transcriptLower.includes('item') || transcriptLower.includes('product') || transcriptLower.includes('order')) {
  issues.push('order_items');
}
if (transcriptLower.includes('cancel') || transcriptLower.includes('don\'t want')) {
  issues.push('cancellation_request');
}
if (transcriptLower.includes('payment') || transcriptLower.includes('card') || transcriptLower.includes('billing')) {
  issues.push('payment_issue');
}
if (transcriptLower.includes('time') || transcriptLower.includes('date') || transcriptLower.includes('when')) {
  issues.push('delivery_timing');
}

// Determine if followup is needed
const requiresFollowup = [
  'needs_review', 
  'no_answer', 
  'unclear'
].includes(confirmationStatus) || issues.length > 0;

return {
  json: {
    call_id: callId,
    order_id: callData.metadata?.order_id,
    confirmation_status: confirmationStatus,
    customer_response: customerResponse,
    confidence_score: confidence,
    call_duration: Math.round(callDuration / 1000), // in seconds
    transcript: transcript,
    issues: issues,
    call_cost: callData.cost || 0,
    processed_at: new Date().toISOString(),
    requires_followup: requiresFollowup,
    analysis_scores: {
      positive: positiveScore,
      negative: negativeScore,
      issues: issueScore
    },
    call_quality: callDuration > 30000 ? 'good' : callDuration > 10000 ? 'fair' : 'poor'
  }
};
