parameters.jsCode:
// Extract data from Marker.io webhook
const data = $input.first().json.body.data;

// Format the conversation message
const issueTitle = data.title;
const issueDescription = data.description;
const markerId = data.markerId;
const priority = data.priority;
const issueType = data.issueType.name;
const publicUrl = data.publicUrl;
const privateUrl = data.privateUrl;
const dueDate = data.dueDate;
const browser = `${data.browser.name} ${data.browser.version}`;
const os = `${data.operatingSystem.family} ${data.operatingSystem.version}`;
const website = data.website.url;
const contextString = data.contextString;

// Create formatted message body with Marker.io ticket link
const messageBody = `${issueTitle}
${issueDescription}`;

// Create formatted message body with Marker.io ticket link
const noteBody = `üêõ **New Issue Reported via Marker.io**

**Issue:** ${issueTitle}
**ID:** ${markerId}
**Type:** ${issueType}
**Priority:** ${priority}
**Due Date:** ${dueDate ? new Date(dueDate).toLocaleDateString() : 'Not set'}

**Description:**
${issueDescription}

**Technical Details:**
‚Ä¢ Browser: ${browser}
‚Ä¢ OS: ${os}
‚Ä¢ Website: ${website}
‚Ä¢ Context: ${contextString}

**Marker.io Links:**
‚Ä¢ üîó [View Issue](${publicUrl})

**Custom Data:**
${Object.entries(data.customData || {}).map(([key, value]) => `‚Ä¢ ${key}: ${value}`).join('\n')}`;

return [{
  json: {
    reporterEmail: data.reporter.email,
    reporterName: data.reporter.name,
    messageTitle: data.title,
    messageBody: messageBody,
    nodeBody: noteBody,
    issueId: data.id,
    markerId: markerId,
    priority: priority,
    issueType: issueType,
    publicUrl: publicUrl,
    privateUrl: privateUrl,
    projectId: data.project.id,
    reporterRole: data.reporter.role,
  }
}];

