// Get data from previous nodes
const webhookData = $('Webhook').item.json.body.data;
const configs = $('Configs').item.json;

// Check if the 'CreateCustomCoupon' node ran and has data
// The .exists property is a safe way to check this
// const couponNodeExists = $('CreateCustomCoupon');
let couponData = null;
if ($('CheckFirstOrder').item.json.first) {
  console.log("teste")
  couponData = $('CreateCustomCoupon').last().json.data;
}

// ---- PART 1: BUILD THE HTML FOR THE EMAIL ----

let couponHtmlSection = '';
if (couponData) {
  couponHtmlSection = `
    <div class="coupon-section">
      <h2>A Welcome Gift For You!</h2>
      <p>As a thank you for your first purchase, we've created a special <strong>10% discount coupon</strong> for your next order. Use the code below:</p>
      <div class="coupon-code">${couponData.id}</div>
    </div>
  `;
}

const emailHtml = `
<!DOCTYPE html>
<html>
<head>
<meta charset="UTF-8">
<meta name="viewport" content="width=device-width, initial-scale=1.0">
<title>Your Order Confirmation</title>
<style>
  body { font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif; margin: 0; padding: 0; background-color: #f4f4f7; }
  .container { max-width: 600px; margin: 20px auto; background-color: #ffffff; border: 1px solid #e2e8f0; border-radius: 8px; overflow: hidden; box-shadow: 0 4px 15px rgba(0,0,0,0.05); }
  .header { background-color: #4a5568; color: #ffffff; padding: 40px; text-align: center; }
  .header h1 { margin: 0; font-size: 28px; }
  .content { padding: 30px 40px; color: #4a5568; line-height: 1.6; }
  .content h2 { color: #2d3748; }
  .order-summary { width: 100%; border-collapse: collapse; margin-top: 20px; }
  .order-summary th, .order-summary td { border-bottom: 1px solid #e2e8f0; padding: 12px; text-align: left; }
  .order-summary th { background-color: #f7fafc; }
  .total { font-size: 18px; font-weight: bold; color: #2d3748; text-align: right; margin-top: 20px; }
  .coupon-section { background-color: #f7fafc; border: 2px dashed #2a9d8f; border-radius: 8px; padding: 20px; margin-top: 30px; text-align: center; }
  .coupon-code { background-color: #ffffff; border: 1px solid #e2e8f0; padding: 10px 20px; font-size: 20px; font-weight: bold; color: #2a9d8f; letter-spacing: 2px; display: inline-block; margin-top: 10px; }
  .footer { background-color: #edf2f7; color: #718096; text-align: center; padding: 20px; font-size: 12px; }
  .footer a { color: #4a5568; text-decoration: none; }
</style>
</head>
<body>
  <div class="container">
    <div class="header">
      <h1>Thank You For Your Order!</h1>
    </div>
    <div class="content">
      <p>Hello <strong>${webhookData.billing.customer.metadata.name}</strong>,</p>
      <p>Your order has been successfully processed. We appreciate your business and are already preparing your items.</p>

      <h2>Order Summary #${webhookData.billing.id}</h2>
      <table class="order-summary">
        <thead>
          <tr>
            <th>Product ID</th>
            <th>Quantity</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>${webhookData.billing.products[0].externalId}</td>
            <td>${webhookData.billing.products[0].quantity}</td>
            <td>R$ ${webhookData.billing.paidAmount / 100}</td>
          </tr>
        </tbody>
      </table>
      <p class="total">
        TOTAL: $${webhookData.billing.paidAmount / 100}
      </p>

      ${couponHtmlSection}

      <p>If you have any questions, please reply to this email or visit our support center. We're here to help!</p>
      <p>Sincerely,<br>The ${configs.companyName} Team</p>
    </div>
    <div class="footer">
      <p>&copy; ${new Date().getFullYear()} ${configs.companyName}. All rights reserved.</p>
      <p><a href="https://${configs.companySite}">Visit our website</a> | <a href="mailto:${configs.companyEmail}">Contact Us</a></p>
    </div>
  </div>
</body>
</html>
`;

let slackBlocks = [
	{
		"type": "header",
		"text": { "type": "plain_text", "text": "ðŸŽ‰ New Sale Received!", "emoji": true }
	},
	{
		"type": "section",
		"fields": [
			{ "type": "mrkdwn", "text": `*Customer:*\n${webhookData.billing.customer.metadata.name}` },
			{ "type": "mrkdwn", "text": `*Email:*\n${webhookData.billing.customer.metadata.email}` },
			{ "type": "mrkdwn", "text": `*Order ID:*\n\`${webhookData.billing.id}\`` },
			{ "type": "mrkdwn", "text": `*Amount Paid:*\n*$${webhookData.billing.paidAmount / 100}*` }
		]
	},
	{ "type": "divider" }
];

if (couponData) {
  slackBlocks.push({
		"type": "section",
		"text": {
			"type": "mrkdwn",
			"text": `ðŸŽ *First-Time Buyer Bonus!*\nA 10% discount coupon was generated for their next purchase: \`${couponData.id}\``
		}
	});
}

slackBlocks.push(
  {
		"type": "context",
		"elements": [
			{ "type": "mrkdwn", "text": `Payment via *${webhookData.payment.method}* | âœ… Confirmation email sent.` }
		]
	},
	{
		"type": "actions",
		"elements": [
			{
				"type": "button",
				"text": { "type": "plain_text", "text": "View Execution", "emoji": true },
				"url": `${$('Webhook').last().json.webhookUrl}`,
				"action_id": "view_execution_button"
			}
		]
	}
);

return {
  json: {
    emailHtml: emailHtml,
    slackBlocks:{blocks: slackBlocks}
  }
};

const orders = $('GetOrders').last().json
const currentOrder = $('Webhook').last().json.body.data
let first = false

if(!orders.error){
  let firstOrder = orders.data.filter(order => order.customer?.id === currentOrder.billing.customer.id && order.id !== currentOrder.billing.id)

  console.log('first',firstOrder)
  if (firstOrder.length === 0){
    console.log("oi")
    first = true
  }
}

return {first}
