const allItems = $input.all();


console.log("Données reçues:", JSON.stringify(allItems, null, 2));

// build the table
let tableRows = '';
allItems.forEach(item => {
  tableRows += `
  <tr>
    <td>${item.json.Topic || ''}</td>
    <td>${item.json.Status || ''}</td>
    <td>${item.json.Owner || ''}</td>
    <td>${item.json["Next Step"] || ''}</td>
  </tr>`;
});

// generate  HTML
const html = `
<!DOCTYPE html>
<html>
<head>
  <style>
    table {
      border-collapse: collapse;
      width: 100%;
      margin: 20px 0;
      font-family: Arial, sans-serif;
    }
    th, td {
      border: 1px solid #dddddd;
      text-align: left;
      padding: 8px;
    }
    th {
      background-color: #f2f2f2;
      font-weight: bold;
    }
  </style>
</head>
<body>
  <p>Hello,</p>
  <p>Here are the elements of the meeting:</p>

  <table>
    <thead>
      <tr>
        <th>Topic</th>
        <th>Status</th>
        <th>Owner</th>
        <th>Next Step</th>
      </tr>
    </thead>
    <tbody>
      ${tableRows}
    </tbody>
  </table>

  <p>Have a good day.<br/>PM Team</p>
</body>
</html>`;

return [{ json: { html } }];
