// Input: HTTP Request result (object with .items)
// Output: one item per comment, flat data ready to write
const out = [];
const root = items[0].json || {};
for (const it of (root.items || [])) {
  const s = it.snippet || {};
  const tl = (s.topLevelComment || {}).snippet || {};
  out.push({
    json: {
      comment_id: it.id,
      video_id: s.videoId,
      author: tl.authorDisplayName,
      comment_text: tl.textOriginal,
      like_count: tl.likeCount || 0,
      published_at: tl.publishedAt,
      author_channel_id: (tl.authorChannelId || {}).value,
      author_channel_url: tl.authorChannelUrl
    }
  });
}
return out;

// n8n Code node: Normalize LLM JSON -> {sentiment, score, keywords, language}
const out = [];

for (const item of items) {
  // 1) Get candidate payload from several possible formats
  let payload = item.json?.output ?? item.json?.data ?? item.json;

  // 2) If string, try to parse JSON
  if (typeof payload === 'string') {
    try { payload = JSON.parse(payload); } catch { payload = {}; }
  }

  // 3) If there's still payload.output as string JSON, parse it too
  if (payload && typeof payload.output === 'string') {
    try { payload = JSON.parse(payload.output); } catch {}
  }

  // 4) Normalize fields
  const allowed = new Set(['positive','neutral','negative']);
  let sentiment = String(payload?.sentiment ?? '').toLowerCase();
  if (!allowed.has(sentiment)) sentiment = 'neutral';

  let score = Number(payload?.score);
  if (!Number.isFinite(score)) score = 0;
  if (score > 1) score = 1;
  if (score < -1) score = -1;

  let keywords = Array.isArray(payload?.keywords) ? payload.keywords : [];
  keywords = keywords
    .map(k => String(k).toLowerCase().trim())
    .filter(k => k && k.length <= 32)
    .slice(0, 8);

  let language = String(payload?.language ?? '').slice(0, 2).toLowerCase();
  if (!language) language = 'en';

  out.push({ json: { sentiment, score, keywords, language } });
}

return out;

// n8n Code node: Summarize sentiments & top keywords
const isAggregatedArrays =
  items.length === 1 &&
  items[0].json &&
  (Array.isArray(items[0].json.sentiment) || Array.isArray(items[0].json.keywords));

function summarizeFromArrays(j) {
  const sentiments = Array.isArray(j.sentiment) ? j.sentiment : [];
  const scores = Array.isArray(j.sentiment_score) ? j.sentiment_score : [];
  let keywordsNested = Array.isArray(j.keywords) ? j.keywords : [];

  // ensure keywordsNested = array of arrays
  keywordsNested = keywordsNested.map(k =>
    Array.isArray(k) ? k : (typeof k === 'string' ? k.split(',') : [])
  );

  const total = Math.max(sentiments.length, keywordsNested.length, scores.length, 0);
  const cnt = { positive: 0, neutral: 0, negative: 0 };
  const kwFreq = {};

  for (let i = 0; i < total; i++) {
    const s = String((sentiments[i] ?? 'neutral')).toLowerCase();
    const normS = (s === 'positive' || s === 'negative' || s === 'neutral') ? s : 'neutral';
    cnt[normS]++;

    const kws = Array.isArray(keywordsNested[i]) ? keywordsNested[i] : [];
    for (let k of kws) {
      const key = String(k).toLowerCase().trim();
      if (!key) continue;
      kwFreq[key] = (kwFreq[key] || 0) + 1;
    }
  }

  const pct = v => total ? Math.round((v / total) * 100) : 0;
  const top_keywords = Object.entries(kwFreq)
    .sort((a, b) => (b[1] - a[1]) || a[0].localeCompare(b[0]))
    .slice(0, 10)
    .map(([k, v]) => `${k} (${v})`);

  return {
    total_comments: total,
    positive: cnt.positive,
    neutral: cnt.neutral,
    negative: cnt.negative,
    positive_pct: pct(cnt.positive),
    neutral_pct: pct(cnt.neutral),
    negative_pct: pct(cnt.negative),
    top_keywords
  };
}

function summarizeFromItems(items) {
  const cnt = { positive: 0, neutral: 0, negative: 0 };
  const kwFreq = {};
  let total = 0;

  for (const it of items) {
    const s = String(it.json?.sentiment ?? 'neutral').toLowerCase();
    const normS = (s === 'positive' || s === 'negative' || s === 'neutral') ? s : 'neutral';
    cnt[normS]++;
    total++;

    const kws = Array.isArray(it.json?.keywords) ? it.json.keywords : [];
    for (let k of kws) {
      const key = String(k).toLowerCase().trim();
      if (!key) continue;
      kwFreq[key] = (kwFreq[key] || 0) + 1;
    }
  }

  const pct = v => total ? Math.round((v / total) * 100) : 0;
  const top_keywords = Object.entries(kwFreq)
    .sort((a, b) => (b[1] - a[1]) || a[0].localeCompare(b[0]))
    .slice(0, 10)
    .map(([k, v]) => `${k} (${v})`);

  return {
    total_comments: total,
    positive: cnt.positive,
    neutral: cnt.neutral,
    negative: cnt.negative,
    positive_pct: pct(cnt.positive),
    neutral_pct: pct(cnt.neutral),
    negative_pct: pct(cnt.negative),
    top_keywords
  };
}

const summary = isAggregatedArrays
  ? summarizeFromArrays(items[0].json)
  : summarizeFromItems(items);

return [{ json: summary }];

