parameters.jsCode:
// Define the categories to check
const categoriesToCheck = [
  "sexual",
  "hate",
  "harassment",
  "self-harm",
  "sexual/minors",
  "hate/threatening",
  "violence/graphic",
  "self-harm/intent",
  "self-harm/instructions",
  "harassment/threatening",
  "violence"
];

// Extract the categories object from the input item
const inputCategories = $input.first().json.categories;

// Filter and collect categories that are true
const trueCategories = categoriesToCheck.filter(category => inputCategories[category] === true);

// Determine status: failure if any violation is found
const status = trueCategories.length > 0 ? "failure" : "success";

// Return the result
return {
  json: {
    status,
    violations: trueCategories
  }
};

