// n8n Code Node: Parse + Format App Intelligence Data
// Input: JSON from HTTP Request (SensorTower / similar API)
// Output: Summary data ready for LLM (formatted text)

const data = items[0].json;

// --- Get basic info ---
const appInfo = {
  name: data.name,
  app_id: data.app_id,
  publisher: data.publisher_name,
  rating: data.rating,
  rating_count: data.rating_count,
  installs: data.installs,
  category: data.categories?.map(c => c.name).join(", ") || "N/A",
  in_app_purchases: data.top_in_app_purchases?.US || "None",
  last_update: data.current_version,
};

// --- Get 3 latest featured reviews ---
const reviews = (data.featured_reviews || [])
  .slice(0, 3)
  .map(r => ({
    user: r.username,
    date: r.date,
    rating: r.rating,
    content: r.content,
    tags: r.tags || []
  }));

// Format reviews into clean string
const reviewsText = reviews.map(r => 
  `- User: ${r.user}\n  Date: ${r.date}\n  Rating: ${r.rating}\n  Content: ${r.content}\n  Tags: ${r.tags.join(", ")}`
).join("\n\n");

// --- Get top 3 competitors ---
const competitors = (data.related_apps || [])
  .slice(0, 3)
  .map(app => ({
    name: app.name,
    publisher: app.publisher_name,
    rating: app.rating,
    installs: app.rating_count,
  }));

// Format competitors into clean string
const competitorsText = competitors.map(c => 
  `- Name: ${c.name}\n  Publisher: ${c.publisher}\n  Rating: ${c.rating}\n  Installs: ${c.installs}`
).join("\n\n");

// --- Get market data (downloads & revenue last month) ---
const market = {
  downloads: data.worldwide_last_month_downloads?.value || 0,
  revenue: data.worldwide_last_month_revenue?.value || 0,
  currency: data.worldwide_last_month_revenue?.currency || "USD"
};

// --- Return final data for LLM ---
return [
  {
    json: {
      appInfo,
      reviewsText,
      competitorsText,
      market
    }
  }
];

// Input from form trigger
const url = items[0].json["Play Store URL"];

// Find the 'id=' parameter in the URL
let packageName = null;
try {
  const urlObj = new URL(url);
  packageName = urlObj.searchParams.get("id");
} catch (e) {
  // fallback manual if not a valid URL
  const match = url.match(/id=([^&]+)/);
  packageName = match ? match[1] : null;
}

return [
  {
    json: {
      packageName
    }
  }
];
