function unwrap(input) {
  let wf = input;
  if (Array.isArray(wf)) wf = wf[0];

  // Tu caso: { workflow: { data: {...} }, nodes: [] }
  if (wf?.workflow?.data?.nodes) return wf.workflow;

  // Caso com√∫n: { data: { nodes } }
  if (wf?.data?.nodes) return wf;

  // Plano: { nodes }
  if (wf?.nodes) return { data: wf };

  throw new Error('No encuentro un workflow v√°lido (workflow.data.nodes / data.nodes / nodes).');
}

const wrapped = unwrap($json);             // { data: {...} }
const nodesAll = wrapped.data?.nodes ?? [];
const realNodes = nodesAll.filter(n => n.type !== 'n8n-nodes-base.stickyNote');

const summary = realNodes.map(n => ({ name: n.name, type: n.type })).slice(0, 50);

return [{
  json: {
    wrappedWorkflow: wrapped,                 // lo llevamos hasta el final
    realNodes,                                // array para el split
    nodesSummaryText: JSON.stringify(summary, null, 2)
  }
}];

function uuidv4() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, c => {
    const r = Math.random()*16|0, v = c==='x'? r : (r&0x3|0x8);
    return v.toString(16);
  });
}

const parts = $input.all().map(i => i.json);

const wrapped = parts.find(p => p.wrappedWorkflow)?.wrappedWorkflow;
if (!wrapped) throw new Error('Assemble: missing wrappedWorkflow');
const root = wrapped.data ? wrapped.data : wrapped;

const stickies = parts.find(p => Array.isArray(p.stickies))?.stickies ?? [];
const moves    = parts.find(p => Array.isArray(p.moves))?.moves ?? [];
const overviewContent = parts.find(p => typeof p.overviewContent === 'string')?.overviewContent ?? '## Overview\n(No content)';

// ensure top-level keys
root.name = root.name || 'Workflow';
root.nodes = Array.isArray(root.nodes) ? root.nodes : [];
root.connections = root.connections || {};
root.settings = root.settings || { executionOrder: 'v1' };
root.pinData = root.pinData || {};
root.tags = root.tags || [];

// remove old stickies
root.nodes = root.nodes.filter(n => n.type !== 'n8n-nodes-base.stickyNote');

// apply moves
const moveMap = new Map(moves.map(m => [m.id, m.position]));
for (const n of root.nodes) {
  const pos = moveMap.get(n.id);
  if (pos) n.position = pos;
}

// anchors AFTER moving nodes
const xs = root.nodes.map(n => n.position?.[0] ?? 0);
const ys = root.nodes.map(n => n.position?.[1] ?? 0);
const minX = Math.min(...xs);
const topY = Math.min(...ys);

// overview (yellow) to the left of first row
const OVERVIEW_W = 600;
const OVERVIEW_H = 1020;
const OVERVIEW_MARGIN = 80;
const OVERVIEW_COLOR = 3; // yellow in most themes (try 4 if your theme differs)

const overviewSticky = {
  parameters: { content: overviewContent, width: OVERVIEW_W, height: OVERVIEW_H, color: OVERVIEW_COLOR },
  id: uuidv4(),
  name: 'Sticky Note - Overview',
  type: 'n8n-nodes-base.stickyNote',
  position: [ minX - OVERVIEW_MARGIN - OVERVIEW_W, topY ],
  typeVersion: 1,
};

// add overview + per-node stickies
root.nodes.push(overviewSticky, ...stickies);

// output root object
const out = JSON.stringify(root, null, 2);
return [{
  json: {},
  binary: {
    data: {
      data: Buffer.from(out, 'utf8').toString('base64'),
      fileName: 'workflow-with-sticky.json',
      mimeType: 'application/json'
    }
  }
}];

/******** CONFIG ‚Äî adjust as needed ********/
const NODE_W   = 300;    // visual width of an n8n node (approx.)
const NODE_H   = 100;    // visual height of an n8n node (approx.)
const STICKY_W = 380;    // sticky note width
const STICKY_H = 1020;   // sticky note height (taller for documentation)
const STICKY_COLOR = 7;  // color (7 = white in most themes, 0 = yellow, etc.)
const GAP_X    = 16;     // horizontal gap between blocks (node + sticky)
const GAP_Y    = 48;     // vertical gap between node and sticky (used in baseline)
const ROW_GAP  = 48;     // vertical gap between block rows (only used if wrapping)
const MAX_PER_ROW = 9999;// prevent wrapping (set to e.g. 6 to enable wrapping)
const START_RIGHT_MARGIN = 40; // margin from the rightmost node

// fine-tuning micro adjustments
const CENTER_TWEAK_X = 0;   // + right, - left (horizontal alignment tweak)
const CENTER_TWEAK_Y = 0;   // + down,  - up   (small tweak applied to node Y only)
const STICKY_BASELINE_TWEAK = 0; // global tweak for all stickies (+ down / - up)

/******** helpers ********/
function uuidv4() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, c => {
    const r = Math.random() * 16 | 0, v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

/******** INPUT ‚Üí normalize to { node, content } ********/
const raw = $input.all().map(i => i.json);

function defaultContent(n) {
  return `### ${n.name} (${n.type})

- Purpose: ‚Ä¶
- Inputs/Outputs: ‚Ä¶
- Key fields: ‚Ä¶
- Tip: ‚Ä¶`;
}

const items = raw.map(it => {
  // case: { node, content }
  if (it?.node?.type) {
    return { node: it.node, content: it.content ?? it.message?.content ?? defaultContent(it.node) };
  }
  // case: item is a node itself
  if (it?.type && it?.id && Array.isArray(it.position)) {
    return { node: it, content: it.content ?? it.message?.content ?? defaultContent(it) };
  }
  return null;
}).filter(Boolean);

// skip already existing sticky notes
const filtered = items.filter(i => i.node.type !== 'n8n-nodes-base.stickyNote');
if (!filtered.length) {
  return [{ json: { stickies: [], moves: [] } }];
}

/******** ORDER: right ‚Üí left ********/
const sorted = filtered.slice().sort((a, b) =>
  (b.node.position?.[0] ?? 0) - (a.node.position?.[0] ?? 0)
);

/******** GLOBAL ANCHORS / METRICS ********/
const allX = filtered.map(i => i.node.position?.[0] ?? 0);
const allY = filtered.map(i => i.node.position?.[1] ?? 0);
const maxX = Math.max(...allX);
const minY = Math.min(...allY);

// baseline for ALL stickies = aligned to the lowest node
// (in n8n, larger Y means lower on the canvas)
const lowestNodeY = Math.max(...allY);

// initial cursor to the right of the rightmost node
let cursorRight = maxX + START_RIGHT_MARGIN;
let rowTop = Number.isFinite(minY) ? minY : 0;

// block size = node above + gap + sticky below
const BLOCK_W = Math.max(NODE_W, STICKY_W);
const BLOCK_H = NODE_H + GAP_Y + STICKY_H;

const stickies = [];
const moves = [];
let countInRow = 0;

/******** PLACEMENT LOOP (node above, sticky centered below) ********/
for (const it of sorted) {
  const node = it.node;
  const content = it.content;

  const blockRight = cursorRight;
  const blockLeft  = blockRight - BLOCK_W;

  // NODE: X is centered inside block
  const nodeX = Math.round(blockLeft + (BLOCK_W - NODE_W) / 2 + CENTER_TWEAK_X);

  // NODE: Y is preserved from original position (fallback to rowTop) + micro tweak
  const origY = Array.isArray(node.position) && Number.isFinite(node.position[1])
    ? node.position[1]
    : rowTop;
  const nodeY = Math.round(origY + CENTER_TWEAK_Y);

  // STICKY: X is centered under node, Y is the same baseline for all stickies
  const nodeCenterX = nodeX + NODE_W / 2;
  const stickyX = Math.round(nodeCenterX - STICKY_W / 2);

  const baselineStickyY = Math.round(lowestNodeY + NODE_H + GAP_Y + STICKY_BASELINE_TWEAK);
  const stickyY = baselineStickyY;

  // record node move (X recalculated; Y preserved)
  moves.push({ id: node.id, position: [nodeX, nodeY] });

  // create sticky note
  stickies.push({
    parameters: { content, width: STICKY_W, height: STICKY_H, color: STICKY_COLOR },
    id: uuidv4(),
    name: `Sticky Note - ${node.name}`,
    type: 'n8n-nodes-base.stickyNote',
    position: [stickyX, stickyY],
    typeVersion: 1,
  });

  // advance to the next block to the LEFT
  cursorRight = blockLeft - GAP_X;
  countInRow++;

  // optional row wrapping if MAX_PER_ROW < 9999
  if (countInRow >= MAX_PER_ROW) {
    countInRow = 0;
    cursorRight = maxX + START_RIGHT_MARGIN;
    rowTop += BLOCK_H + ROW_GAP;
  }
}

/******** OUTPUT ********/
return [{ json: { stickies, moves } }];

