/* === THEME OVERRIDE: n8n Glass & Glow (Revised) === */

/*
  This theme is built upon the default n8n chat CSS variables.
  We'll use your desired branding and layout, and then add
  the "glass" effect using a semi-transparent background
  and a backdrop-filter on the main window class.
*/

:root {
  /* --- 1. CORE BRANDING & FONTS --- */
  /* Your choices for font and primary color are applied here. */
  --chat--font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
  --chat--color-primary: #EA4B71; /* n8n Red */
  --chat--color-primary-shade-50: #D93A60;
  --chat--color-primary-shade-100: #C82A50;
  --chat--color-secondary: #20b69e; /* Kept default secondary for things like file uploads */
  --chat--color-secondary-shade-50: #1ca08a;
  --chat--color-white: #ffffff;
  --chat--color-light: #f2f4f8;
  --chat--color-dark: #1F2937; /* Darker, more modern text color */
  --chat--color-disabled: #9CA3AF;
  --chat--color-typing: #404040;

  /* --- 2. WINDOW & LAYOUT --- */
  /* Your dimensions and rounded corners. The actual glass effect is handled in the class override below. */
  --chat--window--width: 430px;
  --chat--window--height: 80vh;
  --chat--window--border-radius: 16px;
  --chat--window--border: 1px solid rgba(255, 255, 255, 0.3);
  --chat--spacing: 1rem; /* 16px base spacing */
  --chat--window--z-index: 9999;
  --chat--window--bottom: var(--chat--spacing);
  --chat--window--right: var(--chat--spacing);
  --chat--window--margin-bottom: var(--chat--spacing);

  /* --- 3. GLASSMORPHISM BACKGROUNDS --- */
  /* We make the main sections transparent to let the glass window show through. */
  --chat--header--background: transparent;
  --chat--body--background: transparent;
  --chat--footer--background: transparent;

  /* --- 4. HEADER STYLING (REMOVED) --- */
  /* The header is removed by setting its height, padding, and border to 0/none. */
  --chat--header-height: 0;
  --chat--header--padding: 0;
  --chat--header--border-bottom: none;
  /* The rest of the header variables are now irrelevant. */
  --chat--header--color: var(--chat--color-dark);
  --chat--heading--font-size: 1.5rem;
  --chat--subtitle--font-size: 0.875rem;
  --chat--subtitle--line-height: 1.4;
  --chat--close--button--color-hover: var(--chat--color-primary);

  /* --- 5. MESSAGE BUBBLES --- */
  --chat--message--font-size: 0.875rem;
  --chat--message--padding: 12px 16px;
  --chat--message--border-radius: 12px;
  --chat--message-line-height: 1.6;
  --chat--message--margin-bottom: calc(var(--chat--spacing) * 0.75);
  /* Adjusted padding: Top is 0, sides and bottom use the base spacing. */
  --chat--messages-list--padding: 0 var(--chat--spacing) var(--chat--spacing);
  --chat--message--bot--background: var(--chat--color-white);
  --chat--message--bot--color: #111827;
  --chat--message--bot--border: none;
  --chat--message--user--background: var(--chat--color-primary);
  --chat--message--user--color: var(--chat--color-white);
  --chat--message--user--border: none;

  /* --- 6. INPUT AREA --- */
  --chat--textarea--height: 50px;
  --chat--textarea--max-height: 10rem;
  --chat--input--font-size: 0.9rem;
  --chat--input--border: 0;
  --chat--input--border-radius: 12px; /* Rounded input field */
  --chat--input--padding: 12px 16px;
  --chat--input--background: rgba(255, 255, 255, 0.5); /* Semi-transparent input */
  --chat--input--text-color: #111827;
  --chat--input--line-height: 1.5;
  --chat--input--border-active: 0;

  /* --- 7. SEND & FILE BUTTONS --- */
  --chat--input--send--button--background: transparent;
  --chat--input--send--button--color: var(--chat--color-primary);
  --chat--input--send--button--background-hover: transparent;
  --chat--input--send--button--color-hover: var(--chat--color-primary-shade-50);
  --chat--input--file--button--background: transparent;
  --chat--input--file--button--color: var(--chat--color-secondary);
  --chat--input--file--button--background-hover: transparent;
  --chat--input--file--button--color-hover: var(--chat--color-secondary-shade-50);

  /* --- 8. TOGGLE BUTTON & OTHERS --- */
  /* Using your primary brand color for the main toggle button */
  --chat--toggle--size: 64px;
  --chat--toggle--width: var(--chat--toggle--size);
  --chat--toggle--height: var(--chat--toggle--size);
  --chat--toggle--border-radius: 50%;
  --chat--toggle--background: var(--chat--color-primary);
  --chat--toggle--hover--background: var(--chat--color-primary-shade-50);
  --chat--toggle--active--background: var(--chat--color-primary-shade-100);
  --chat--toggle--color: var(--chat--color-white);
}

/* === CLASS OVERRIDES === */
/*
  These are essential for effects that CSS variables can't control,
  like the glass blur, shadows, and max-height.
*/

.chat-window {
  /* This is the magic for the glass effect! */
  background-color: rgba(249, 243, 245, 0.6); /* A semi-transparent background color */
  backdrop-filter: blur(20px);
  -webkit-backdrop-filter: blur(20px); /* For Safari compatibility */

  /* A subtle shadow helps lift the window off the page */
  box-shadow: 0 8px 32px 0 rgba(31, 38, 135, 0.1);

  /* Here we apply the max-height you wanted */
  max-height: 750px;
}

/* Add top margin to the first message only for initial spacing */
.chat-messages-list .chat-message:first-child {
  margin-top: var(--chat--spacing);
}

/* Make user messages slightly wider for better balance */
.chat-message.is-user {
	max-width: 70%;
}

.chat-message.is-bot {
	max-width: 80%;
}

/* Add a subtle glow to messages on hover */
.chat-message {
    transition: transform 0.2s ease, box-shadow 0.2s ease;
}
.chat-message:hover {
    transform: translateY(-2px);
    box-shadow: 0 0 25px 0 rgba(234, 75, 113, 0.2);
}

/* === PAGE BACKGROUND === */
/* Your dotted background to simulate the n8n canvas. Perfect as is! */
body {
    background-color: #FDFBFF;
    background-image:
        radial-gradient(circle at 1px 1px, rgba(0,0,0,0.07) 1px, transparent 0),
        linear-gradient(135deg, #F9F3F5 0%, #EFF3FB 100%);
    background-size: 25px 25px, 100% 100%;
}

