const MAX_LEN = 1000;
const OVERLAP = 100;

function splitTextIntoChunks(text, maxLen = 1000, overlap = 100) {
  if (text == null) return [];
  text = 
      String($input.first().json.data);
  //String($input.first().json.candidates[0].content.parts[0].text);
  maxLen = Math.max(1, maxLen | 0);
  overlap = Math.max(0, overlap | 0);

  const chunks = [];
  let start = 0;
  const n = text.length;

  while (start < n) {
    const hardEnd = Math.min(n, start + maxLen);
    let split = -1;

    for (let i = hardEnd - 1; i >= start; i--) {
      const c = text[i];
      if (c === '.' || c === '!' || c === '?' || c === '\n') {
        split = i + 1;
        break;
      }
    }

    if (split === -1 || split <= start) {
      split = hardEnd;
    }

    chunks.push(text.slice(start, split));

    if (split >= n) break;

    start = Math.max(split - overlap, start + 1);
  }

  return chunks;
}

const out = [];
// Iterate through all items (each item must have a .json.text field, or similar content)
for (let i = 0; i < items.length; i++) {
  // If .json.text exists, use it; otherwise use hardcoded text for demonstration
  const text = items[i].json.text || "This is a demo text. Add your own data to the 'text' property of the input JSON. This script will split long text into chunks, each up to MAX_LEN, preserving overlap as set by OVERLAP.";

  const chunks = splitTextIntoChunks(text, MAX_LEN, OVERLAP);
  for (let j = 0; j < chunks.length; j++) {
    out.push({
      json: {
        chunkIndex: j,
        chunk: chunks[j],
      },
    });
  }
}

return out;
