// Comprehensive data aggregation
const workflow_run_id = `startup_analysis_${Date.now()}`;
const timestamp = new Date().toISOString();

// Collect data from all agents
let idea = {};
let critique = {};
let sentiment = {};

try {
  // Get idea from Agent 1
  idea = $('Idea Generator Agent').first().json.output || {};
  console.log('Idea data collected:', !!idea.idea_title);

  // Get critique from Agent 2  
  critique = $('Idea Critic Agent').first().json.output || {};
  console.log('Critique data collected:', !!critique.overall_viability_score);

  // Get sentiment from current node (Agent 3)
  sentiment = $('Sentiment Analysis').first().json.output || {};
  console.log('Sentiment data collected:', !!sentiment.final_synthesis);

} catch (error) {
  console.error('Data collection error:', error);
}

// Calculate composite scores
const calculateCompositeScore = () => {
  const ideaScore = parseFloat(idea.innovation_score) || 0;
  const viabilityScore = parseFloat(critique.overall_viability_score) || 0;
  const successProbability = parseFloat(sentiment.final_synthesis?.success_probability) || 0;

  return {
    innovation: ideaScore,
    viability: viabilityScore, 
    success_probability: successProbability,
    composite: ((ideaScore + viabilityScore + (successProbability/10)) / 3).toFixed(1)
  };
};

const scores = calculateCompositeScore();

// Determine recommendation priority
const getRecommendationPriority = () => {
  const verdict = critique.verdict || 'UNKNOWN';
  const sentimentRec = sentiment.final_synthesis?.overall_recommendation || 'UNKNOWN';

  if (verdict.includes('ABANDON') || sentimentRec === 'PASS') return 'LOW';
  if (verdict.includes('CAUTION') || sentimentRec.includes('CAUTIOUS')) return 'MEDIUM';
  if (verdict.includes('PROCEED') && sentimentRec.includes('STRONG')) return 'HIGH';
  return 'MEDIUM';
};

// Create comprehensive output structure
const aggregatedData = {
  // Metadata
  meta: {
    workflow_run_id,
    timestamp,
    analysis_version: '1.0',
    processing_time_estimate: '~3-5 minutes'
  },

  // Raw agent outputs
  raw_outputs: {
    idea_generation: idea,
    market_critique: critique, 
    sentiment_analysis: sentiment
  },

  // Processed insights
  insights: {
    scores,
    recommendation_priority: getRecommendationPriority(),
    key_opportunities: critique.recommendations?.slice(0, 3) || [],
    key_risks: critique.risk_factors?.slice(0, 3) || [],
    next_steps: sentiment.final_synthesis?.key_next_steps || []
  },

  // Summary for quick review
  executive_summary: {
    idea_title: idea.idea_title || 'Untitled Startup Idea',
    one_liner: idea.core_concept || 'No description available',
    verdict: critique.verdict || 'NO_VERDICT',
    confidence: sentiment.final_synthesis?.decision_confidence || 'Unknown',
    bottom_line: sentiment.executive_summary || 'Analysis incomplete'
  },

  // Flags for routing decisions
  routing_flags: {
    should_email: scores.composite >= 5.0,
    needs_human_review: getRecommendationPriority() === 'HIGH',
    save_to_database: true,
    generate_report: scores.composite >= 6.0
  }
};

return aggregatedData;

const data = $input.first().json;
const { executive_summary, insights, meta } = data;

// Create human-readable email content
const emailContent = {
  subject: `üöÄ Startup Analysis: ${executive_summary.idea_title} [${executive_summary.verdict}]`,

  html_body: `
    <h2>üöÄ Startup Idea Analysis Report</h2>
    <p><strong>Generated:</strong> ${new Date(meta.timestamp).toLocaleString()}</p>

    <div style="background: #f5f5f5; padding: 15px; border-radius: 8px; margin: 15px 0;">
      <h3>${executive_summary.idea_title}</h3>
      <p><em>${executive_summary.one_liner}</em></p>
    </div>

    <h3>üìä Quick Scores</h3>
    <ul>
      <li><strong>Innovation:</strong> ${insights.scores.innovation}/10</li>
      <li><strong>Market Viability:</strong> ${insights.scores.viability}/10</li> 
      <li><strong>Success Probability:</strong> ${insights.scores.success_probability}%</li>
      <li><strong>Composite Score:</strong> ${insights.scores.composite}/10</li>
    </ul>

    <h3>üéØ Verdict: ${executive_summary.verdict}</h3>
    <p><strong>Bottom Line:</strong> ${executive_summary.bottom_line}</p>

    <h3>üö® Key Risks</h3>
    <ul>
      ${insights.key_risks.map(risk => `<li>${risk}</li>`).join('')}
    </ul>

    <h3>üí° Key Opportunities</h3>
    <ul>
      ${insights.key_opportunities.map(opp => `<li>${opp}</li>`).join('')}
    </ul>

    <h3>üìã Recommended Next Steps</h3>
    <ol>
      ${insights.next_steps.map(step => `<li>${step}</li>`).join('')}
    </ol>

    <hr>
    <p><small>Analysis ID: ${meta.workflow_run_id}</small></p>
  `,

  text_body: `
STARTUP ANALYSIS REPORT
Generated: ${new Date(meta.timestamp).toLocaleString()}

IDEA: ${executive_summary.idea_title}
${executive_summary.one_liner}

SCORES:
- Innovation: ${insights.scores.innovation}/10
- Market Viability: ${insights.scores.viability}/10  
- Success Probability: ${insights.scores.success_probability}%
- Composite: ${insights.scores.composite}/10

VERDICT: ${executive_summary.verdict}
${executive_summary.bottom_line}

KEY RISKS:
${insights.key_risks.map((risk, i) => `${i+1}. ${risk}`).join('\n')}

KEY OPPORTUNITIES:  
${insights.key_opportunities.map((opp, i) => `${i+1}. ${opp}`).join('\n')}

NEXT STEPS:
${insights.next_steps.map((step, i) => `${i+1}. ${step}`).join('\n')}

Analysis ID: ${meta.workflow_run_id}
  `
};

return [{ json: emailContent }];

