const data = $json;

// Limit to first 5 search results
const limitedSearchResults = (data.search_results || []).slice(0, 5).map(item => {
  return {
    title: item.title || null,
    url: item.url || null,
    date: item.date || null,
    last_updated: item.last_updated || null
  };
});

// Limit to first 5 citations
const limitedCitations = (data.citations || []).slice(0, 5);

// Return single item to OpenAI with trimmed content
return [
  {
    json: {
      search_results: limitedSearchResults,
      citations: limitedCitations,
      message: data.choices?.[0]?.message?.content || null
    }
  }
];

