// Get markdown content
let md = $input.first().json.message.content || "";

// Markdown to HTML conversion (no external libs)
function mdToHtml(markdown) {
  return markdown
    // H6 to H1 (strip # and wrap in correct tag)
    .replace(/^###### (.*$)/gim, '<h6>$1</h6>')
    .replace(/^##### (.*$)/gim, '<h5>$1</h5>')
    .replace(/^#### (.*$)/gim, '<h4>$1</h4>')
    .replace(/^### (.*$)/gim, '<h3>$1</h3>')
    .replace(/^## (.*$)/gim, '<h2>$1</h2>')
    .replace(/^# (.*$)/gim, '<h1>$1</h1>')
    // Bold
    .replace(/\*\*(.*?)\*\*/gim, '<strong>$1</strong>')
    // Italic
    .replace(/\*(.*?)\*/gim, '<em>$1</em>')
    // Links
    .replace(/\[(.*?)\]\((.*?)\)/gim, '<a href="$2" target="_blank">$1</a>')
    // Lists
    .replace(/^\s*[-*] (.*$)/gim, '<li>$1</li>')
    // Paragraphs (only if not already in block tags)
    .replace(/^(?!<h\d>|<li>|<strong>|<em>|<a)(.+)$/gim, '<p>$1</p>')
    // Wrap list items in <ul>
    .replace(/(<li>.*<\/li>)/gims, '<ul>$1</ul>');
}

let html = mdToHtml(md);

// Styling wrapper
let finalHtml = `<div style="font-family: Nunito, sans-serif; line-height: 1.6;">${html}</div>`;

return [{ html: finalHtml }];
