    .replace(/<\/p><p>/gi, ' ')    // Convert </p><p> to space
    .replace(/<p[^>]*>/gi, '')     // Remove all opening p tags
    .replace(/<\/p>/gi, '')        // Remove all closing p tags
    .trim();

  // If content is now empty, provide fallback
  if (!html || html.trim() === '') {
    html = 'Content not available';
  }

  // Wrap plain text in a paragraph for Shopify
  if (!html.startsWith('<') && html.trim()) {
    html = html.trim();
  }

  // Parse inline formatting within text
  function parseInlineElements(htmlString) {
    const result = [];

    // Handle nested formatting tags
    const tagRegex = /<(\/?)(strong|b|em|i|a)([^>]*)>/gi;
    let lastIndex = 0;
    let match;

    const stack = {
      bold: false,
      italic: false,
      link: null
    };

    while ((match = tagRegex.exec(htmlString)) !== null) {
      // Add text before this tag
      const textBefore = htmlString.slice(lastIndex, match.index);
      if (textBefore) {
        addTextNode(result, textBefore, stack);
      }

      const isClosing = match[1] === '/';
      const tagName = match[2].toLowerCase();
      const attributes = match[3];

      // Update formatting stack
      if (tagName === 'strong' || tagName === 'b') {
        stack.bold = !isClosing;
      } else if (tagName === 'em' || tagName === 'i') {
        stack.italic = !isClosing;
      } else if (tagName === 'a') {
        if (isClosing) {
          stack.link = null;
        } else {
          const hrefMatch = attributes.match(/href=["']([^"']*)["']/i);
          stack.link = hrefMatch ? hrefMatch[1] : '';
        }
      }

      lastIndex = tagRegex.lastIndex;
    }

    // Add remaining text
    const remainingText = htmlString.slice(lastIndex);
    if (remainingText) {
      addTextNode(result, remainingText, stack);
    }

    return result.length ? result : [{ type: 'text', value: htmlString || '' }];
  }

  function addTextNode(result, text, stack) {
    if (!text.trim()) return;

    const textNode = {
      type: 'text',
      value: text
    };

    // Add formatting properties only if true
    if (stack.bold) textNode.bold = true;
    if (stack.italic) textNode.italic = true;

    // Handle links
    if (stack.link) {
      result.push({
        type: 'link',
        url: stack.link,
        children: [textNode]
      });
    } else {
      result.push(textNode);
    }
  }

  // Parse block-level elements - SIMPLIFIED to avoid paragraph issues
  function parseBlocks(html) {
    const blocks = [];

    // Extract lists first
    const listRegex = /<(ul|ol)[^>]*>([^]*?)<\/\1>/gi;
    let match;
    let lastIndex = 0;

    while ((match = listRegex.exec(html)) !== null) {
      // Add any text before this list as a paragraph
      const beforeList = html.slice(lastIndex, match.index).trim();
      if (beforeList) {
        blocks.push({
          type: 'paragraph',
          children: parseInlineElements(beforeList)
        });
      }

      // Process the list
      const listItems = extractListItems(match[2]);
      if (listItems.length > 0) {
        blocks.push({
          type: 'list',
          listType: match[1].toLowerCase() === 'ul' ? 'unordered' : 'ordered',
          children: listItems
        });
      }

      lastIndex = match.index + match[0].length;
    }

    // Add remaining content
    const remaining = html.slice(lastIndex).trim();
    if (remaining) {
      // Check for headings
      const headingMatch = remaining.match(/^<h([1-6])[^>]*>([^]*?)<\/h[1-6]>(.*)$/i);
      if (headingMatch) {
        blocks.push({
          type: 'heading',
          level: parseInt(headingMatch[1]),
          children: parseInlineElements(headingMatch[2])
        });

        // Add content after heading
        const afterHeading = headingMatch[3].trim();
        if (afterHeading) {
          blocks.push({
            type: 'paragraph',
            children: parseInlineElements(afterHeading)
          });
        }
      } else {
        // Regular content - add as paragraph
        blocks.push({
          type: 'paragraph',
          children: parseInlineElements(remaining)
        });
      }
    }

    return blocks;
  }

  // Extract list items
  function extractListItems(listContent) {
    const items = [];
    const liRegex = /<li[^>]*>([^]*?)<\/li>/gi;
    let match;

    while ((match = liRegex.exec(listContent)) !== null) {
      let itemContent = match[1].trim();

      if (itemContent) {
        const children = parseInlineElements(itemContent);
        items.push({
          type: 'list-item',
          children: children
