// Parse OpenAI response and process urgency data
const response = $input.first();
let aiAnalysis;

try {
  // Extract JSON from AI response
  const content = response.json.choices[0].message.content;
  aiAnalysis = JSON.parse(content);
} catch (error) {
  // Fallback if JSON parsing fails
  aiAnalysis = {
    urgency_score: 3,
    reasoning: "Failed to parse AI response, using default priority",
    suggested_priority: "normal",
    key_indicators: [],
    estimated_impact: "medium",
    recommended_response_time: "4"
  };
}

// Determine if Slack notification needed (urgency 4 or 5)
const needsSlackNotification = aiAnalysis.urgency_score >= 4;

// Map urgency score to Zendesk priority
let zendeskPriority;
switch(aiAnalysis.urgency_score) {
  case 5:
    zendeskPriority = "urgent";
    break;
  case 4:
    zendeskPriority = "high";
    break;
  case 3:
    zendeskPriority = "normal";
    break;
  case 2:
    zendeskPriority = "low";
    break;
  default:
    zendeskPriority = "normal";
}

// Prepare output data
return {
  json: {
    ticket_id: $('Extract Ticket Data').first().json.ticket_id,
    ticket_subject: $('Extract Ticket Data').first().json.ticket_subject,
    customer_name: $('Extract Ticket Data').first().json.customer_name,
    customer_email: $('Extract Ticket Data').first().json.customer_email,
    urgency_score: aiAnalysis.urgency_score,
    ai_reasoning: aiAnalysis.reasoning,
    suggested_priority: aiAnalysis.suggested_priority,
    zendesk_priority: zendeskPriority,
    key_indicators: aiAnalysis.key_indicators,
    estimated_impact: aiAnalysis.estimated_impact,
    recommended_response_time: aiAnalysis.recommended_response_time,
    needs_slack_notification: needsSlackNotification,
    analysis_timestamp: new Date().toISOString()
  }
};
