// Filter content calendar for today's posts
const today = new Date().toISOString().split('T')[0];
const calendarData = $input.all();

// Skip header row and filter for today's content
const todaysContent = calendarData.slice(1).filter(row => {
  const rowData = row.json;

  if (!rowData.Date || !rowData.Topic) return false;

  let postDate;
  if (rowData.Date.includes('/')) {
    const [month, day, year] = rowData.Date.split('/');
    postDate = `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;
  } else {
    postDate = rowData.Date;
  }

  return postDate === today && 
         rowData.Status !== 'Completed' && 
         rowData.Status !== 'Posted' &&
         rowData.Topic.trim() !== '';
});

if (todaysContent.length === 0) {
  return [{
    json: {
      message: "No content scheduled for today",
      date: today,
      hasContent: false
    }
  }];
}

// Process each content item
return todaysContent.map((item, index) => {
  const rowData = item.json;

  return {
    json: {
      rowIndex: index + 2,
      topic: rowData.Topic || '',
      platforms: rowData.Platforms || 'Twitter,LinkedIn',
      contentType: rowData['Content Type'] || 'Educational',
      keywords: rowData.Keywords || '',
      date: today,
      hasContent: true,
      brandVoice: $('Configuration Variables').first().json.BRAND_VOICE,
      companyName: $('Configuration Variables').first().json.COMPANY_NAME,
      targetAudience: $('Configuration Variables').first().json.TARGET_AUDIENCE
    }
  };
});

