// Process AI content and prepare for scheduling
const contentInput = $('Generate Social Content with AI').first();
const originalData = $('Filter Today\'s Content').first();

let socialContent;
try {
  const aiResponse = contentInput.json.choices[0].message.content;
  socialContent = JSON.parse(aiResponse);
} catch (error) {
  // Fallback content
  socialContent = {
    twitter: {
      text: `Sharing insights about ${originalData.json.topic}! üöÄ`,
      hashtags: ["business", "insights"],
      character_count: 80
    },
    linkedin: {
      text: `Today let's explore ${originalData.json.topic}. What are your thoughts?`,
      hashtags: ["business", "leadership"],
      character_count: 120
    },
    instagram: {
      text: `‚ú® ${originalData.json.topic} ‚ú®\n\nWhat's your experience? Share below! üëá`,
      hashtags: ["business", "motivation"],
      character_count: 100
    }
  };
}

// Process platforms
const requestedPlatforms = originalData.json.platforms.toLowerCase().split(',').map(p => p.trim());
const results = [];

requerestedPlatforms.forEach(platform => {
  const platformKey = platform === 'twitter' ? 'twitter' : 
                     platform === 'linkedin' ? 'linkedin' : 
                     platform === 'instagram' ? 'instagram' : null;

  if (platformKey && socialContent[platformKey]) {
    results.push({
      json: {
        platform: platform,
        content: socialContent[platformKey].text,
        hashtags: socialContent[platformKey].hashtags || [],
        topic: originalData.json.topic,
        row_index: originalData.json.rowIndex,
        date: originalData.json.date,
        scheduled_time: new Date().toISOString()
      }
    });
  }
});

return results.length > 0 ? results : [{
  json: {
    error: "No valid platforms found",
    topic: originalData.json.topic
  }
}];
