parameters.jsCode:
// === cc_route_on_no_controls ===
// Se output === "NO_CONTROLS_TO_BE_CONSOLIDATED", substitui o payload pelo json de cc_extract_file_info.
// Senão, apenas repassa o item original.
//
// Compatível com:
// 1) { json: { output: "NO_CONTROLS_TO_BE_CONSOLIDATED", threadId: "..." } }
// 2) { json: [ { output: "NO_CONTROLS_TO_BE_CONSOLIDATED", threadId: "..." } ] }

function getOutputValue(payload) {
  if (payload == null) return '';
  if (Array.isArray(payload)) {
    const first = payload[0];
    return typeof first?.output === 'string' ? first.output.trim() : '';
  }
  if (typeof payload === 'object') {
    return typeof payload.output === 'string' ? payload.output.trim() : '';
  }
  if (typeof payload === 'string') return payload.trim();
  return '';
}

function getThreadId(payload) {
  if (payload == null) return undefined;
  if (Array.isArray(payload)) return payload[0]?.threadId;
  if (typeof payload === 'object') return payload.threadId;
  return undefined;
}

function getCcInfo() {
  try {
    const n = $items('cc_extract_file_info')[0];
    const j = n?.json ?? {};

    // settings e uuid (uuid pode estar dentro de settings ou na raiz, por segurança)
    const settings = j.settings ?? {};
    const uuid = String((settings.uuid ?? j.uuid ?? '')).trim();

    // opcionalmente preserva alguns metadados úteis se existirem
    const meta = {};
    for (const k of ['fileId', 'fileName', 'mimeType', 'path', 'size']) {
      if (j[k] !== undefined) meta[k] = j[k];
    }

    return { settings, uuid, ...meta };
  } catch {
    return null;
  }
}

let itemsIn;
try {
  itemsIn = $input.all(); // n8n Code node novo
} catch {
  // fallback (algumas versões)
  itemsIn = [{ json: $json, binary: $binary }];
}

const itemsOut = itemsIn.map((item) => {
  const outVal = getOutputValue(item.json);
  const outValUC = outVal.toUpperCase();

  if (outValUC === 'NO_CONTROLS_TO_BE_CONSOLIDATED') {
    const info = getCcInfo();
    // Se não conseguir ler cc_extract_file_info, mantém o item original para não quebrar o fluxo
    if (!info) return item;

    // opcional: mantém o threadId original (se existir) para rastreabilidade
    const threadId = getThreadId(item.json);
    if (threadId) info.threadId = threadId;

    return { json: info };
  }

  // Qualquer outra resposta: apenas passa adiante sem alterações
  return item;
});

return itemsOut;

// === make_file_from_output ===
// Fonte única: $('controls_transfer_area').first().json.data
// Salva .json se o conteúdo for JSON válido; caso contrário, .txt.

const SOURCE_NODE = 'controls_transfer_area';

// ---------- helpers ----------
function stripCodeFences(s) {
  return String(s)
    .replace(/^\s*```[a-z]*\s*/i, '')
    .replace(/\s*```[\s\r\n]*$/i, '')
    .replace(/^\uFEFF/, '')
    .replace(/\r\n/g, '\n')
    .trim();
}

function isValidJsonObject(s) {
  try {
    const x = JSON.parse(s);
    return x && typeof x === 'object';
  } catch {
    return false;
  }
}

// Aceita objeto ou string; tenta extrair 'technology'
function detectTechnologySmartAny(input) {
  try {
    if (input && typeof input === 'object') {
      if (input.technology) {
        return String(input.technology).trim().replace(/[^\w.-]+/g, '_');
      }
      if (input.output && typeof input.output === 'object' && input.output.technology) {
        return String(input.output.technology).trim().replace(/[^\w.-]+/g, '_');
      }
    }
  } catch {}
  const s = typeof input === 'string' ? input : JSON.stringify(input ?? '');
  try {
    const o = JSON.parse(s);
    if (o?.technology) return String(o.technology).trim().replace(/[^\w.-]+/g, '_');
  } catch {}
  const m = s.match(/"technology"\s*:\s*"([^"]+)"/i);
  if (m) return m[1].trim().replace(/[^\w.-]+/g, '_');
  const m2 = s.match(/Technology:\s*([^\n]+)/i);
  return m2 ? m2[1].trim().replace(/[^\w.-]+/g, '_') : null;
}

// ---------- coleta somente do nó-fonte ----------
let srcItems = [];
try {
  srcItems = $items(SOURCE_NODE, 0) || [];
} catch (e) {
  srcItems = [];
}

// Se não houver nada no nó-fonte, retorna arquivo "vazio" para não quebrar o fluxo
if (!Array.isArray(srcItems) || srcItems.length === 0) {
  const finalText = 'NO_CONTENT_FROM_SOURCE_NODE';
  const fileName = 'controls_output.txt';
  const mimeType = 'text/plain';
  const b64 = Buffer.from(finalText, 'utf8').toString('base64');
  return [{
    json: {
      fileName,
      mimeType,
      bytes: Buffer.byteLength(finalText, 'utf8'),
      sourceNode: SOURCE_NODE,
      itemsFromSource: 0
    },
    binary: { data: { data: b64, mimeType, fileName } }
  }];
}

// Primeiro item do controls_transfer_area
const first = srcItems[0] || {};

// === alvo principal: .json.data ===
let rawCandidate;
if (first.json && Object.prototype.hasOwnProperty.call(first.json, 'data')) {
  rawCandidate = first.json.data;
} else if (first.json && Object.prototype.hasOwnProperty.call(first.json, 'output')) {
  // fallback amigável se alguém ainda estiver usando "output"
  rawCandidate = first.json.output;
} else {
  // fallback final: o próprio json
  rawCandidate = first.json;
}

// Normaliza para string final e decide se é JSON
let finalText = 'NO_CONTENT_EXTRACTED';
let mimeType = 'text/plain';
let ext = 'txt';

if (typeof rawCandidate === 'string') {
  const cleaned = stripCodeFences(rawCandidate);
  finalText = cleaned || 'NO_CONTENT_EXTRACTED';
  if (isValidJsonObject(finalText)) {
    mimeType = 'application/json';
    ext = 'json';
  }
} else if (rawCandidate && typeof rawCandidate === 'object') {
  // Caso o .data seja um objeto — inclusive se tiver { output: ... }
  if (rawCandidate && typeof rawCandidate.output === 'string') {
    const cleaned = stripCodeFences(rawCandidate.output);
    finalText = cleaned || 'NO_CONTENT_EXTRACTED';
    if (isValidJsonObject(finalText)) {
      mimeType = 'application/json';
      ext = 'json';
    }
  } else if (rawCandidate && typeof rawCandidate.output === 'object') {
    finalText = JSON.stringify(rawCandidate.output, null, 2);
    mimeType = 'application/json';
    ext = 'json';
  } else {
    finalText = JSON.stringify(rawCandidate, null, 2);
    mimeType = 'application/json';
    ext = 'json';
  }
}

// Nome do arquivo
let tech;
try {
  tech = detectTechnologySmartAny(
    mimeType === 'application/json' ? JSON.parse(finalText) : finalText
  ) || 'output';
} catch {
  tech = 'output';
}

const ts = new Date().toISOString().replace(/[:.]/g, '-');
const fileName = `controls_${tech}_${ts}.${ext}`;

// Binário
const bytes = Buffer.byteLength(finalText, 'utf8');
const b64 = Buffer.from(finalText, 'utf8').toString('base64');

// Retorno para n8n
return [{
  json: {
    fileName,
    mimeType,
    bytes,
    sourceNode: SOURCE_NODE,
    itemsFromSource: srcItems.length
  },
  binary: { data: { data: b64, mimeType, fileName } },
}];

// Lê todos os itens de entrada do nó anterior
const all = $input.all(); // [{json: {...}}, ...]

// Normaliza: (a) vários itens simples, (b) 1 item com `data[]`, (c) 1 item com array plano
let list;
if (all.length === 1 && Array.isArray(all[0].json?.data)) {
  list = all[0].json.data;                // caso: { data: [...] }
} else if (all.length === 1 && Array.isArray(all[0].json)) {
  list = all[0].json;                     // caso: [{id,name,model}, ...] dentro do json
} else {
  list = all.map(i => i.json);            // caso: cada item já é {id,name,model}
}

// Garante array plano de objetos válidos
list = list
  .flatMap(x => Array.isArray(x) ? x : [x])
  .filter(x => x && typeof x === 'object');

// Helper: escolher por nome usando uma lista de regex (primeiro que casar)
const pick = (...regexes) => {
  for (const re of regexes) {
    const hit = list.find(a => re.test(String(a.name || '')));
    if (hit) return hit;
  }
  return undefined;
};

// 1) Extractor (1_)
const extractor = pick(
  /(^|[\s_-])1[\s_-]*DefySec[\s_-]*Extractor\b/i,
  /\bDefySec[\s_-]*Extractor\b/i,
  /\bExtractor\b/i
);

// 2) Control Composer (2_)
const composer  = pick(
  /(^|[\s_-])2[\s_-]*DefySec[\s_-]*Control[\s_-]*Composer\b/i,
  /\bDefySec[\s_-]*Control[\s_-]*Composer\b/i,
  /\bComposer\b/i
);

// 3) Baseline Builder (3_)
const baseline  = pick(
  /(^|[\s_-])3[\s_-]*DefySec[\s_-]*(Baseline[\s_-]*Builder|Baseline)\b/i,
  /\bDefySec[\s_-]*Baseline[\s_-]*Builder\b/i,
  /\bBaseline[\s_-]*Builder\b/i
);

// 4) Auditor (4_)
const auditor   = pick(
  /(^|[\s_-])4[\s_-]*DefySec[\s_-]*Baseline[\s_-]*Auditor\b/i,
  /\bDefySec[\s_-]*Baseline[\s_-]*Auditor\b/i,
  /\bBaseline[\s_-]*Auditor\b/i,
  /\bAuditor\b/i
);

// 5) Reviewer (5_) — nome oficial atual
const reviewer  = pick(
  /(^|[\s_-])5[\s_-]*DefySec[\s_-]*Baseline[\s_-]*Reviewer\b/i,
  /\bDefySec[\s_-]*Baseline[\s_-]*Reviewer\b/i,
  /\bBaseline[\s_-]*Reviewer\b/i,
  /\bReviewer\b/i
);

// Saída única apenas com os IDs esperados no fluxo atual
return [
  {
    json: {
      assistant_extractor_id: extractor?.id ?? '',
      assistant_composer_id:  composer?.id  ?? '',
      assistant_baseline_id:  baseline?.id  ?? '',
      assistant_auditor_id:   auditor?.id   ?? '',
      assistant_reviewer_id:  reviewer?.id  ?? ''
    }
  }
];

// n8n Code Node (JavaScript) — Validação de payload (com fallback de URL)

// Helpers
function isNonEmptyString(v) {
  return typeof v === 'string' && v.trim().length > 0;
}

// Regex simples e eficaz para http/https (sem espaços)
const HTTP_URL_REGEX = /^https?:\/\/\S+$/i;

function validateHttpUrl(value) {
  const s = String(value ?? '').trim();
  if (!s) return { ok: false, value: s, reason: 'empty' };

  // 1) Tenta usar global URL se existir
  const hasURLCtor = (typeof URL !== 'undefined');
  if (hasURLCtor) {
    try {
      const u = new URL(s);
      if (u.protocol === 'http:' || u.protocol === 'https:') {
        return { ok: true, value: s, via: 'URL' };
      }
      return { ok: false, value: s, reason: 'unsupported_protocol' };
    } catch {
      // cai para regex
    }
  }

  // 2) Fallback: regex
  if (HTTP_URL_REGEX.test(s)) {
    return { ok: true, value: s, via: 'regex_fallback' };
  }
  return { ok: false, value: s, reason: hasURLCtor ? 'parse_error' : 'no_URL_ctor_and_regex_failed' };
}

return items.map(item => {
  const req = item.json ?? {};

  // Body pode vir como string (content-type errado) ou objeto
  let b = null;
  if (req && typeof req.body === 'string') {
    try { b = JSON.parse(req.body); } catch { b = null; }
  } else if (req && typeof req.body === 'object' && req.body !== null) {
    b = req.body;
  }

  const required = ['cloudProvider', 'technology', 'urls'];
  const missing = [];
  const invalid = {};
  const debug = {};

  if (!b) {
    missing.push(...required);
  } else {
    // cloudProvider
    if (!isNonEmptyString(b.cloudProvider)) missing.push('cloudProvider');

    // technology
    if (!isNonEmptyString(b.technology)) missing.push('technology');

    // urls
    if (!Array.isArray(b.urls) || b.urls.length === 0) {
      missing.push('urls');
    } else {
      const checks = b.urls.map(u => validateHttpUrl(u));
      debug.urlChecks = checks;
      const bad = checks.filter(c => !c.ok).map(c => ({ value: c.value, reason: c.reason }));
      if (bad.length) invalid.urls = bad;
    }
  }

  const hasIssues = missing.length > 0 || (invalid.urls?.length > 0);

  const normalized = (!hasIssues && b) ? {
    cloudProvider: String(b.cloudProvider).trim().toLowerCase(),
    technology: String(b.technology).trim(),
    urls: b.urls.map(u => String(u).trim()),
  } : undefined;

  return {
    json: {
      ok: !hasIssues,
      error: hasIssues ? 'ValidationError' : null,
      message: hasIssues
        ? 'Payload inválido. Corrija os campos obrigatórios antes de reenviar.'
        : 'Payload válido.',
      required,
      missing,
      invalid: Object.keys(invalid).length ? invalid : undefined,
      receivedKeys: b ? Object.keys(b) : [],
      _requestMeta: {
        headers: req.headers ?? null,
        params: req.params ?? null,
        query: req.query ?? null,
        webhookUrl: req.webhookUrl ?? null,
        executionMode: req.executionMode ?? null,
      },
      normalized,
      // campo de diagnóstico para entender POR QUE deu falso (remova em produção)
      _debug: debug,
    }
  };
});

// n8n Code Node (JavaScript)
// Saída: 1 item com { Original_Data, Last_Version, Data_feedback }

function normalize(val) {
  if (val == null) return '';
  if (typeof val === 'string') return val.trim();
  if (Array.isArray(val)) return val.map(v => (typeof v === 'string' ? v : JSON.stringify(v))).join('\n');
  if (typeof val === 'object') return JSON.stringify(val, null, 2);
  return String(val);
}

// Original_Data: $('3_DefySec Baseline Builder').first()?.json?.output
let originalRaw;
try {
  originalRaw = $('3_DefySec Baseline Builder').first()?.json?.output;
} catch {
  originalRaw = undefined;
}

// Last_Version: $('controls_transfer_area').first().json.data
let lastVersionRaw;
try {
  lastVersionRaw = $('controls_transfer_area').first()?.json?.data;
} catch {
  lastVersionRaw = undefined;
}

// Data_feedback: $input.first()?.json?.output
const feedbackRaw = $input.first()?.json?.output;

return [
  {
    json: {
      Original_Data: normalize(originalRaw),
      Last_Version: normalize(lastVersionRaw),
      Data_feedback: normalize(feedbackRaw),
    },
  },
];
